//
//  DefaultConfiguration.swift
//  SwiftMedia
//
//  Created on 6/25/25.
//

import Foundation

/// Central configuration for all default settings in SwiftMedia.
/// This provides a single source of truth for default values used throughout the application.
struct DefaultConfiguration {

    // MARK: - Directory Paths

    /// Default directory for video files.
    /// Uses the user's Movies folder as a sensible default.
    static var defaultVideoDirectory: String {
        return "~/Downloads/vids"
    }

    /// Default directory for metadata files (markers, projects).
    /// Uses the Application Support directory for proper data storage.
    static var defaultMetadataDirectory: String {
        if let appSupport = FileManager.default.urls(for: .applicationSupportDirectory,
                                                     in: .userDomainMask).first {
            return appSupport.appendingPathComponent("SwiftMedia/Metadata").path
        }
        return "~/Downloads/timestamps"
    }


    // MARK: - Performance Settings

    /// Default buffer duration for video playback (in seconds)
    /// Note: This is the fallback value when adaptive calculation cannot be performed
    static let defaultBufferDuration: Double = 3.0

    /// Calculate adaptive buffer duration based on video resolution and bitrate
    /// - Parameters:
    ///   - width: Video width in pixels
    ///   - height: Video height in pixels
    ///   - bitrate: Estimated bitrate in bits per second
    /// - Returns: Recommended buffer duration in seconds
    static func adaptiveBufferDuration(width: CGFloat, height: CGFloat, bitrate: Float) -> TimeInterval {
        // Determine if video is 4K, 8K, or higher resolution
        let is8K = width >= 7680 || height >= 4320
        let is4K = width >= 3840 || height >= 2160
        let isHighBitrate = bitrate > 20_000_000 // 20 Mbps

        // Adaptive buffer sizing based on resolution and bitrate
        if is8K {
            return 15.0 // 15 seconds for 8K
        } else if is4K || isHighBitrate {
            return 10.0 // 10 seconds for 4K or high bitrate
        } else if width >= 1920 || height >= 1080 {
            return 5.0  // 5 seconds for 1080p
        } else {
            return 3.0  // 3 seconds for lower resolutions
        }
    }

    /// Default frame observation interval for UI updates (in seconds)
    static let defaultFrameObservationInterval: Double = 1.0 / 30.0

    // MARK: - Auto-save Settings

    /// Default auto-save interval (in seconds)
    static let defaultAutoSaveInterval: TimeInterval = 300 // 5 minutes

    /// Default auto-save enabled state
    static let defaultAutoSaveEnabled: Bool = true

    // MARK: - UI Settings

    /// Default error banner display duration (in seconds)
    static let defaultErrorBannerDuration: TimeInterval = 5.0

    /// Default animation duration for UI transitions (in seconds)
    static let defaultAnimationDuration: Double = 0.2

    // MARK: - Marker Settings

    /// Default marker rating (1-5)
    static let defaultMarkerRating: Int = 3

    // MARK: - Helper Methods

    /// Expands tilde (~) in paths to the full home directory path
    static func expandPath(_ path: String) -> String {
        return NSString(string: path).expandingTildeInPath
    }

    /// Creates a URL from a path string, expanding tilde if present
    static func url(fromPath path: String) -> URL? {
        let expandedPath = expandPath(path)
        return URL(fileURLWithPath: expandedPath)
    }

    /// Ensures a directory exists at the specified path
    static func ensureDirectoryExists(at path: String) throws {
        let expandedPath = expandPath(path)
        let url = URL(fileURLWithPath: expandedPath)

        try FileManager.default.createDirectory(at: url,
                                               withIntermediateDirectories: true,
                                               attributes: nil)
    }
}

//
//  PermissionVerificationManager.swift
//  SwiftMedia
//
//  Created on 6/25/25.
//

import Foundation
import AppKit

@MainActor
class PermissionVerificationManager {
    static let shared = PermissionVerificationManager()

    private init() {}

    /// Verifies permissions for default directories at app launch
    func verifyPermissionsAtLaunch() async {
        print("[PermissionVerification] Starting permission verification at launch")

        // Request broad access to home directory instead of individual directories
        let homeDirectory = FileManager.default.homeDirectoryForCurrentUser
        await verifyAndRequestPermission(for: homeDirectory, directoryType: "home")
    }

    /// Verifies permission for a directory and requests if needed
    private func verifyAndRequestPermission(for directoryURL: URL, directoryType: String) async {
        print("[PermissionVerification] Checking \(directoryType) directory: \(directoryURL.path)")

        // For sandboxed apps, we should check specific directories, not home directory
        // Let's check the actual directories we need
        let videoDirectory = DefaultConfiguration.url(fromPath: DefaultConfiguration.defaultVideoDirectory)
        let metadataDirectory = DefaultConfiguration.url(fromPath: DefaultConfiguration.defaultMetadataDirectory)

        var needsPermission = false

        // Check if we can access the video directory
        if let videoDir = videoDirectory {
            let videoAccess = await checkDirectoryAccess(videoDir)
            if !videoAccess {
                print("[PermissionVerification] No access to video directory")
                needsPermission = true
            }
        }

        // Check if we can access the metadata directory  
        if let metadataDir = metadataDirectory {
            let metadataAccess = await checkDirectoryAccess(metadataDir)
            if !metadataAccess {
                print("[PermissionVerification] No access to metadata directory")
                needsPermission = true
            }
        }

        if needsPermission {
            print("[PermissionVerification] Need permission for directories, requesting...")

            // For home directory, ensure subdirectories exist
            if directoryType == "home" {
                // Create default directories if they don't exist
                if let videoDir = DefaultConfiguration.url(fromPath: DefaultConfiguration.defaultVideoDirectory) {
                    try? FileManager.default.createDirectory(at: videoDir, withIntermediateDirectories: true)
                }
                if let metadataDir = DefaultConfiguration.url(fromPath: DefaultConfiguration.defaultMetadataDirectory) {
                    try? FileManager.default.createDirectory(at: metadataDir, withIntermediateDirectories: true)
                }
            }

            // Request permission
            let granted = await requestDirectoryAccess(for: directoryURL, directoryType: directoryType)
            if granted {
                print("[PermissionVerification] Permission granted for \(directoryType) directory")
            } else {
                print("[PermissionVerification] Permission denied for \(directoryType) directory")
            }
        } else {
            print("[PermissionVerification] Already have access to required directories")
        }
    }

    /// Checks if we can access a directory
    private func checkDirectoryAccess(_ directoryURL: URL) async -> Bool {
        let fileManager = FileManager.default

        print("[PermissionVerification] Checking access to: \(directoryURL.path)")

        // First check if directory exists
        guard fileManager.fileExists(atPath: directoryURL.path) else {
            print("[PermissionVerification] Directory does not exist: \(directoryURL.path)")
            return false
        }

        // Try to read directory contents
        do {
            let contents = try fileManager.contentsOfDirectory(at: directoryURL, 
                                                   includingPropertiesForKeys: nil, 
                                                   options: .skipsHiddenFiles)
            print("[PermissionVerification] Successfully read directory, found \(contents.count) items")
            return true
        } catch {
            print("[PermissionVerification] Cannot access directory: \(error)")
            return false
        }
    }

    /// Requests directory access using a simple yes/no dialog
    private func requestDirectoryAccess(for directoryURL: URL, directoryType: String) async -> Bool {
        return await MainActor.run {
            let alert = NSAlert()
            alert.messageText = "Grant Directory Access"

            if directoryType == "home" {
                alert.informativeText = "SwiftMedia needs access to your home directory to:\n• Load videos from Downloads/vids\n• Save metadata to Downloads/timestamps\n• Navigate between videos in directories\n\nWould you like to grant access?"
            } else {
                alert.informativeText = "SwiftMedia needs access to the \(directoryType) directory:\n\n\(directoryURL.path)\n\nWould you like to grant access?"
            }

            alert.alertStyle = .informational
            alert.addButton(withTitle: "Grant Access")
            alert.addButton(withTitle: "Deny")

            let response = alert.runModal()

            if response == .alertFirstButtonReturn {
                // User clicked "Grant Access"
                // For sandboxed apps, we need to use NSOpenPanel to get actual permission
                // But we'll make it automatic by setting the directory URL
                let panel = NSOpenPanel()
                panel.title = "Select Directory"

                if directoryType == "home" {
                    panel.message = "Please select your home directory to grant SwiftMedia access"
                } else {
                    panel.message = "Please confirm access to: \(directoryURL.lastPathComponent)"
                }

                panel.prompt = "Grant Access"
                panel.canChooseFiles = false
                panel.canChooseDirectories = true
                panel.allowsMultipleSelection = false
                panel.directoryURL = directoryURL
                panel.canCreateDirectories = false
                panel.showsHiddenFiles = false

                // This will open the panel with the directory pre-selected
                if panel.runModal() == .OK,
                   let selectedURL = panel.url {
                    // Try to create a security-scoped bookmark
                    do {
                        let bookmarkData = try selectedURL.bookmarkData(
                            options: [.withSecurityScope, .securityScopeAllowOnlyReadAccess],
                            includingResourceValuesForKeys: nil,
                            relativeTo: nil
                        )
                        // Store bookmark for future use
                        UserDefaults.standard.set(bookmarkData, forKey: "DirectoryBookmark-\(selectedURL.path)")
                        print("[PermissionVerification] Created and stored bookmark for \(directoryType) directory")
                        return true
                    } catch {
                        print("[PermissionVerification] Failed to create bookmark: \(error)")
                        return false
                    }
                }
            }

            return false
        }
    }
}

