import SwiftUI

struct DirectoriesTabView: View {
    @ObservedObject var viewModel: SettingsViewModel

    var body: some View {
        VStack(alignment: .leading, spacing: 20) {
            Text("Default Directories")
                .font(.headline)
                .padding(.top)

            // Video Directory
            VStack(alignment: .leading, spacing: 8) {
                Text("Default Video Directory")
                    .font(.subheadline)
                    .foregroundColor(.secondary)

                HStack {
                    Text(viewModel.tempVideoDirectory.isEmpty ? "Not Set" : DefaultConfiguration.expandPath(viewModel.tempVideoDirectory))
                        .frame(maxWidth: .infinity, alignment: .leading)
                        .padding(8)
                        .background(Color(NSColor.textBackgroundColor))
                        .cornerRadius(6)
                        .foregroundColor(viewModel.tempVideoDirectory.isEmpty ? .secondary : .primary)

                    Button("Choose...") {
                        selectDirectory(for: .video)
                    }
                }
            }

            // Metadata Directory
            VStack(alignment: .leading, spacing: 8) {
                Text("Default Metadata Directory")
                    .font(.subheadline)
                    .foregroundColor(.secondary)

                HStack {
                    Text(viewModel.tempMetadataDirectory.isEmpty ? "Not Set" : DefaultConfiguration.expandPath(viewModel.tempMetadataDirectory))
                        .frame(maxWidth: .infinity, alignment: .leading)
                        .padding(8)
                        .background(Color(NSColor.textBackgroundColor))
                        .cornerRadius(6)
                        .foregroundColor(viewModel.tempMetadataDirectory.isEmpty ? .secondary : .primary)

                    Button("Choose...") {
                        selectDirectory(for: .metadata)
                    }
                }
            }

            Text("These directories will be used as default locations for videos and all SwiftMedia data (projects, markers, CSV exports).")
                .font(.caption)
                .foregroundColor(.secondary)
                .padding(.top)

            Spacer()
        }
        .padding()
    }

    private func selectDirectory(for type: DirectoryType) {
        let panel = NSOpenPanel()
        panel.canChooseFiles = false
        panel.canChooseDirectories = true
        panel.allowsMultipleSelection = false
        panel.title = type == .video ? "Select Default Video Directory" : "Select Default Metadata Directory"

        if panel.runModal() == .OK {
            if let url = panel.url {
                switch type {
                case .video:
                    viewModel.tempVideoDirectory = url.path
                    viewModel.markAsChanged()
                case .metadata:
                    viewModel.tempMetadataDirectory = url.path
                    viewModel.markAsChanged()
                }
            }
        }
    }

    enum DirectoryType {
        case video
        case metadata
    }
}

import Foundation
import AVFoundation

/// Actor-based marker store with pagination support for handling large datasets efficiently
actor MarkerStore {
    static let shared = MarkerStore()

    // Configuration
    private let pageSize = 100
    private let maxCachePages = 20 // Increased cache size for better performance
    private let maxCacheMemoryBytes = 50 * 1024 * 1024 // 50MB max cache

    // Storage
    private var pageCache: [String: [Int: MarkerPage]] = [:] // videoHash -> pageIndex -> page
    private var markerIndices: [String: MarkerIndex] = [:] // videoHash -> index

    // Improved LRU cache implementation
    private var lruCache = LRUCache<CacheKey, MarkerPage>()

    // Temporary in-memory storage for demo/testing
    private var inMemoryMarkers: [String: [Marker]] = [:] // videoHash -> markers

    // Structures
    struct MarkerPage {
        let markers: [Marker]
        let pageIndex: Int
        let totalCount: Int

        var estimatedMemorySize: Int {
            // Rough estimate: 200 bytes per marker
            markers.count * 200
        }
    }

    struct MarkerIndex {
        let totalCount: Int
        let sortedTimestamps: [CMTime]
        let markerIDs: [UUID]
        let lastModified: Date
    }

    struct CacheKey: Hashable {
        let videoHash: String
        let pageIndex: Int
    }

    // MARK: - Public Interface

    /// Load a specific page of markers
    func loadPage(for videoHash: String, pageIndex: Int) async throws -> MarkerPage {
        let cacheKey = CacheKey(videoHash: videoHash, pageIndex: pageIndex)

        // Check LRU cache first
        if let cachedPage = lruCache.get(cacheKey) {
            return cachedPage
        }

        // Load index if needed
        let index = try await loadOrBuildIndex(for: videoHash)

        // Calculate page bounds
        let startIdx = pageIndex * pageSize
        let endIdx = min(startIdx + pageSize, index.totalCount)

        guard startIdx < index.totalCount else {
            return MarkerPage(markers: [], pageIndex: pageIndex, totalCount: index.totalCount)
        }

        // Load markers for this page
        let pageMarkers = try await loadMarkersInRange(
            for: videoHash,
            startIndex: startIdx,
            endIndex: endIdx,
            index: index
        )

        let page = MarkerPage(
            markers: pageMarkers,
            pageIndex: pageIndex,
            totalCount: index.totalCount
        )

        // Cache the page with memory limit checking
        lruCache.set(cacheKey, value: page, cost: page.estimatedMemorySize)

        // Also update the legacy cache for compatibility
        if pageCache[videoHash] == nil {
            pageCache[videoHash] = [:]
        }
        pageCache[videoHash]?[pageIndex] = page

        return page
    }

    /// Get total marker count without loading all markers
    func getMarkerCount(for videoHash: String) async throws -> Int {
        let index = try await loadOrBuildIndex(for: videoHash)
        return index.totalCount
    }

    /// Find markers in a time range (optimized with binary search)
    func findMarkers(for videoHash: String, in timeRange: CMTimeRange) async throws -> [Marker] {
        let index = try await loadOrBuildIndex(for: videoHash)

        // Binary search for start and end positions
        let startPos = index.sortedTimestamps.firstIndex { $0 >= timeRange.start } ?? 0
        let endPos = index.sortedTimestamps.lastIndex { $0 <= timeRange.end } ?? (index.totalCount - 1)

        // Load only the pages containing these markers
        let startPage = startPos / pageSize
        let endPage = endPos / pageSize

        var markers: [Marker] = []
        for pageIdx in startPage...endPage {
            let page = try await loadPage(for: videoHash, pageIndex: pageIdx)
            let filtered = page.markers.filter { timeRange.containsTime($0.timestamp) }
            markers.append(contentsOf: filtered)
        }

        return markers
    }

    /// Add a new marker (updates index and invalidates relevant cache)
    func addMarker(_ marker: Marker, for videoHash: String) async throws {
        // Load current markers to find insertion point
        let index = try await loadOrBuildIndex(for: videoHash)

        // Find insertion position
        let insertPos = index.sortedTimestamps.firstIndex { $0 > marker.timestamp } ?? index.totalCount
        let pageIndex = insertPos / pageSize

        // Invalidate cached pages from this point forward
        invalidatePages(for: videoHash, fromPage: pageIndex)

        // Update in-memory storage
        var markers = inMemoryMarkers[videoHash] ?? []
        let insertIdx = markers.firstIndex { $0.timestamp > marker.timestamp } ?? markers.count
        markers.insert(marker, at: insertIdx)
        inMemoryMarkers[videoHash] = markers

        // Rebuild index
        _ = try await buildIndex(for: videoHash)
    }

    /// Update an existing marker
    func updateMarker(_ marker: Marker, for videoHash: String) async throws {
        // Find which page contains this marker
        let index = try await loadOrBuildIndex(for: videoHash)

        if let markerIdx = index.markerIDs.firstIndex(of: marker.id) {
            let pageIndex = markerIdx / pageSize
            invalidatePages(for: videoHash, fromPage: pageIndex, toPage: pageIndex)
        }

        // Update in-memory storage
        if var markers = inMemoryMarkers[videoHash],
           let idx = markers.firstIndex(where: { $0.id == marker.id }) {
            markers[idx] = marker
            markers.sort { $0.timestamp < $1.timestamp }
            inMemoryMarkers[videoHash] = markers
        }
    }

    /// Delete a marker
    func deleteMarker(id: UUID, for videoHash: String) async throws {
        let index = try await loadOrBuildIndex(for: videoHash)

        if let markerIdx = index.markerIDs.firstIndex(of: id) {
            let pageIndex = markerIdx / pageSize

            // Invalidate all pages from this point (due to index shift)
            invalidatePages(for: videoHash, fromPage: pageIndex)

            // Delete from in-memory storage
            if var markers = inMemoryMarkers[videoHash] {
                markers.removeAll { $0.id == id }
                inMemoryMarkers[videoHash] = markers
            }

            // Rebuild index
            _ = try await buildIndex(for: videoHash)
        }
    }

    /// Save all markers (batch operation)
    func saveAllMarkers(_ markers: [Marker], for videoHash: String) async throws {
        // Clear all caches for this video
        pageCache[videoHash] = nil
        markerIndices[videoHash] = nil

        // Store in memory
        inMemoryMarkers[videoHash] = markers

        // Build new index
        _ = try await buildIndex(for: videoHash)
    }

    // MARK: - Private Methods

    private func loadOrBuildIndex(for videoHash: String) async throws -> MarkerIndex {
        // Check if we have a current index
        if let index = markerIndices[videoHash] {
            // For now, always use cached index
            return index
        }

        // Build new index
        return try await buildIndex(for: videoHash)
    }

    private func buildIndex(for videoHash: String) async throws -> MarkerIndex {
        // Load from in-memory storage
        let allMarkers = inMemoryMarkers[videoHash] ?? []

        // Sort by timestamp
        let sorted = allMarkers.sorted { $0.timestamp < $1.timestamp }

        let index = MarkerIndex(
            totalCount: sorted.count,
            sortedTimestamps: sorted.map { $0.timestamp },
            markerIDs: sorted.map { $0.id },
            lastModified: Date()
        )

        markerIndices[videoHash] = index
        return index
    }

    private func loadMarkersInRange(
        for videoHash: String,
        startIndex: Int,
        endIndex: Int,
        index: MarkerIndex
    ) async throws -> [Marker] {
        // Get marker IDs for this range
        let markerIDs = Array(index.markerIDs[startIndex..<endIndex])

        // Load from in-memory storage
        let allMarkers = inMemoryMarkers[videoHash] ?? []
        return markerIDs.compactMap { id in
            allMarkers.first { $0.id == id }
        }
    }

    /// Clear cache for a specific video
    func clearCache(for videoHash: String) {
        pageCache[videoHash] = nil
        markerIndices[videoHash] = nil

        // Clear from LRU cache
        for pageIndex in 0..<100 { // Reasonable upper limit
            let key = CacheKey(videoHash: videoHash, pageIndex: pageIndex)
            lruCache.remove(key)
        }
    }

    /// Get cache statistics
    func getCacheStats() -> (pageCount: Int, memoryUsage: Int) {
        return lruCache.stats()
    }

    private func invalidatePages(for videoHash: String, fromPage: Int, toPage: Int? = nil) {
        guard var cache = pageCache[videoHash] else { return }

        if let toPage = toPage {
            for page in fromPage...toPage {
                cache.removeValue(forKey: page)
                let key = CacheKey(videoHash: videoHash, pageIndex: page)
                lruCache.remove(key)
            }
        } else {
            // Invalidate all pages from this point forward
            for (pageIdx, _) in cache where pageIdx >= fromPage {
                let key = CacheKey(videoHash: videoHash, pageIndex: pageIdx)
                lruCache.remove(key)
            }
            cache = cache.filter { $0.key < fromPage }
        }

        pageCache[videoHash] = cache.isEmpty ? nil : cache
    }
}

// MARK: - LRU Cache Implementation

/// Thread-safe LRU cache with memory limit support
class LRUCache<Key: Hashable, Value> {
    private class Node {
        var key: Key
        var value: Value
        var cost: Int
        var prev: Node?
        var next: Node?

        init(key: Key, value: Value, cost: Int) {
            self.key = key
            self.value = value
            self.cost = cost
        }
    }

    private let lock = NSLock()
    private var cache: [Key: Node] = [:]
    private var head: Node?
    private var tail: Node?
    private var totalCost = 0
    private let maxCost = 50 * 1024 * 1024 // 50MB default
    private let maxCount = 100 // Maximum number of entries

    func get(_ key: Key) -> Value? {
        lock.lock()
        defer { lock.unlock() }

        guard let node = cache[key] else { return nil }

        // Move to front (most recently used)
        moveToFront(node)
        return node.value
    }

    func set(_ key: Key, value: Value, cost: Int = 0) {
        lock.lock()
        defer { lock.unlock() }

        // Update existing node
        if let node = cache[key] {
            node.value = value
            let oldCost = node.cost
            node.cost = cost
            totalCost += (cost - oldCost)
            moveToFront(node)
        } else {
            // Create new node
            let node = Node(key: key, value: value, cost: cost)
            cache[key] = node
            totalCost += cost

            // Add to front
            if head == nil {
                head = node
                tail = node
            } else {
                node.next = head
                head?.prev = node
                head = node
            }
        }

        // Evict if necessary
        evictIfNeeded()
    }

    func remove(_ key: Key) {
        lock.lock()
        defer { lock.unlock() }

        guard let node = cache.removeValue(forKey: key) else { return }
        totalCost -= node.cost
        removeNode(node)
    }

    func stats() -> (pageCount: Int, memoryUsage: Int) {
        lock.lock()
        defer { lock.unlock() }

        return (cache.count, totalCost)
    }

    private func moveToFront(_ node: Node) {
        guard node !== head else { return }

        // Remove from current position
        removeNode(node)

        // Add to front
        node.next = head
        node.prev = nil
        head?.prev = node
        head = node
    }

    private func removeNode(_ node: Node) {
        if node === head {
            head = node.next
        }
        if node === tail {
            tail = node.prev
        }

        node.prev?.next = node.next
        node.next?.prev = node.prev
        node.prev = nil
        node.next = nil
    }

    private func evictIfNeeded() {
        // Evict based on memory limit
        while totalCost > maxCost && tail != nil {
            if let node = tail {
                cache.removeValue(forKey: node.key)
                totalCost -= node.cost
                removeNode(node)
            }
        }

        // Evict based on count limit
        while cache.count > maxCount && tail != nil {
            if let node = tail {
                cache.removeValue(forKey: node.key)
                totalCost -= node.cost
                removeNode(node)
            }
        }
    }
}

