import Foundation

/// Central configuration for all default settings in SwiftMedia.
/// This provides a single source of truth for default values used throughout the application.
struct DefaultConfiguration {

    // MARK: - Directory Paths

    /// Default directory for video files.
    /// Uses the user's Movies folder as a sensible default.
    static var defaultVideoDirectory: String {
        return "~/Downloads/vids"
    }

    /// Default directory for metadata files (markers, projects).
    /// Uses the Application Support directory for proper data storage.
    static var defaultMetadataDirectory: String {
        if let appSupport = FileManager.default.urls(for: .applicationSupportDirectory,
                                                     in: .userDomainMask).first {
            return appSupport.appendingPathComponent("SwiftMedia/Metadata").path
        }
        return "~/Downloads/timestamps"
    }


    // MARK: - Performance Settings

    /// Default buffer duration for video playback (in seconds)
    /// Note: This is the fallback value when adaptive calculation cannot be performed
    static let defaultBufferDuration: Double = 3.0

    /// Calculate adaptive buffer duration based on video resolution and bitrate
    /// - Parameters:
    ///   - width: Video width in pixels
    ///   - height: Video height in pixels
    ///   - bitrate: Estimated bitrate in bits per second
    /// - Returns: Recommended buffer duration in seconds
    static func adaptiveBufferDuration(width: CGFloat, height: CGFloat, bitrate: Float) -> TimeInterval {
        // Determine if video is 4K, 8K, or higher resolution
        let is8K = width >= 7680 || height >= 4320
        let is4K = width >= 3840 || height >= 2160
        let isHighBitrate = bitrate > 20_000_000 // 20 Mbps

        // Adaptive buffer sizing based on resolution and bitrate
        if is8K {
            return 15.0 // 15 seconds for 8K
        } else if is4K || isHighBitrate {
            return 10.0 // 10 seconds for 4K or high bitrate
        } else if width >= 1920 || height >= 1080 {
            return 5.0  // 5 seconds for 1080p
        } else {
            return 3.0  // 3 seconds for lower resolutions
        }
    }

    /// Default frame observation interval for UI updates (in seconds)
    static let defaultFrameObservationInterval: Double = 1.0 / 30.0

    // MARK: - Auto-save Settings

    /// Default auto-save interval (in seconds)
    static let defaultAutoSaveInterval: TimeInterval = 300 // 5 minutes

    /// Default auto-save enabled state
    static let defaultAutoSaveEnabled: Bool = true

    // MARK: - UI Settings

    /// Default error banner display duration (in seconds)
    static let defaultErrorBannerDuration: TimeInterval = 5.0

    /// Default animation duration for UI transitions (in seconds)
    static let defaultAnimationDuration: Double = 0.2

    // MARK: - Marker Settings

    /// Default marker rating (1-5)
    static let defaultMarkerRating: Int = 3

    // MARK: - Helper Methods

    /// Expands tilde (~) in paths to the full home directory path
    static func expandPath(_ path: String) -> String {
        return NSString(string: path).expandingTildeInPath
    }

    /// Creates a URL from a path string, expanding tilde if present
    static func url(fromPath path: String) -> URL? {
        let expandedPath = expandPath(path)
        return URL(fileURLWithPath: expandedPath)
    }

    /// Ensures a directory exists at the specified path
    static func ensureDirectoryExists(at path: String) throws {
        let expandedPath = expandPath(path)
        let url = URL(fileURLWithPath: expandedPath)

        try FileManager.default.createDirectory(at: url,
                                               withIntermediateDirectories: true,
                                               attributes: nil)
    }
}

