import Foundation
import CryptoKit
import SwiftUI

enum MarkerPersistenceError: LocalizedError {
    case failedToGenerateFileURL
    case failedToCreateDirectory(Error)
    case failedToSaveMarkers(Error)
    case failedToLoadMarkers(Error)
    case failedToDeleteMarkers(Error)

    var errorDescription: String? {
        switch self {
        case .failedToGenerateFileURL:
            return "Failed to generate marker file URL"
        case .failedToCreateDirectory(let error):
            return "Failed to create metadata directory: \(error.localizedDescription)"
        case .failedToSaveMarkers(let error):
            return "Failed to save markers: \(error.localizedDescription)"
        case .failedToLoadMarkers(let error):
            return "Failed to load markers: \(error.localizedDescription)"
        case .failedToDeleteMarkers(let error):
            return "Failed to delete markers: \(error.localizedDescription)"
        }
    }

    var recoverySuggestion: String? {
        switch self {
        case .failedToGenerateFileURL:
            return "Check app permissions and try restarting."
        case .failedToCreateDirectory:
            return "Check disk space and folder permissions."
        case .failedToSaveMarkers:
            return "Ensure you have write permissions and sufficient disk space."
        case .failedToLoadMarkers:
            return "Check if the marker file exists and isn't corrupted."
        case .failedToDeleteMarkers:
            return "Check file permissions and try closing other applications."
        }
    }
}

actor MarkerPersistence {
    static let shared = MarkerPersistence()

    private let userDefaults = UserDefaults.standard
    private var cache: [String: [Marker]] = [:]

    private var markersDirectory: URL {
        get async {
            // Get from UserDefaults on actor
            let metadataDirectory = userDefaults.string(forKey: "defaultMetadataDirectory") ?? DefaultConfiguration.defaultMetadataDirectory

            // Use configured metadata directory
            if !metadataDirectory.isEmpty,
               let url = DefaultConfiguration.url(fromPath: metadataDirectory) {
                return url
            }

            // Fallback to default
            let appSupport = FileManager.default.urls(for: .applicationSupportDirectory, in: .userDomainMask).first!
            return appSupport.appendingPathComponent("SwiftMedia/Metadata", isDirectory: true)
        }
    }

    private init() {
        // Directory will be created on demand when saving
    }

    // Generate SHA256 hash of first 1MB of video file
    func generateVideoHash(for url: URL) -> String? {
        guard let fileHandle = try? FileHandle(forReadingFrom: url) else { return nil }
        defer { fileHandle.closeFile() }

        // Read first 1MB or entire file if smaller
        let data = fileHandle.readData(ofLength: 1024 * 1024)

        let hash = SHA256.hash(data: data)
        return hash.compactMap { String(format: "%02x", $0) }.joined()
    }

    func markerFileURL(for videoURL: URL) async -> URL? {
        guard let hash = generateVideoHash(for: videoURL) else { return nil }
        let directory = await markersDirectory
        return directory.appendingPathComponent("\(hash).json")
    }

    func saveMarkers(_ markers: [Marker], for videoURL: URL) async throws {
        guard let fileURL = await markerFileURL(for: videoURL) else {
            throw MarkerPersistenceError.failedToGenerateFileURL
        }

        // Cache the video hash for quick access
        if let hash = generateVideoHash(for: videoURL) {
            cache[hash] = markers
        }

        // Ensure directory exists
        let directory = await markersDirectory
        try FileManager.default.createDirectory(at: directory, withIntermediateDirectories: true)
        print("[MarkerPersistence] Using metadata directory: \(directory.path)")

        let encoder = JSONEncoder()
        encoder.outputFormatting = [.prettyPrinted, .sortedKeys]
        encoder.dateEncodingStrategy = .iso8601
        let data = try encoder.encode(markers)
        try data.write(to: fileURL, options: .atomic)
        print("[MarkerPersistence] Saved \(markers.count) markers to: \(fileURL.path)")
    }

    func loadMarkers(for videoURL: URL) async throws -> [Marker] {
        // Check cache first
        if let hash = generateVideoHash(for: videoURL),
           let cached = cache[hash] {
            print("[MarkerPersistence] Loaded \(cached.count) markers from cache")
            return cached
        }

        guard let fileURL = await markerFileURL(for: videoURL),
              FileManager.default.fileExists(atPath: fileURL.path) else {
            print("[MarkerPersistence] No saved markers found for video")
            return []
        }

        let data = try Data(contentsOf: fileURL)
        let decoder = JSONDecoder()
        decoder.dateDecodingStrategy = .iso8601
        let markers = try decoder.decode([Marker].self, from: data)

        // Update cache
        if let hash = generateVideoHash(for: videoURL) {
            cache[hash] = markers
        }

        print("[MarkerPersistence] Loaded \(markers.count) markers from: \(fileURL.path)")
        return markers
    }

    func deleteMarkers(for videoURL: URL) async throws {
        guard let fileURL = await markerFileURL(for: videoURL) else {
            throw MarkerPersistenceError.failedToGenerateFileURL
        }

        // Remove from cache
        if let hash = generateVideoHash(for: videoURL) {
            cache.removeValue(forKey: hash)
        }

        try FileManager.default.removeItem(at: fileURL)
        print("[MarkerPersistence] Deleted markers for video")
    }

    func shouldDeleteMetadataFile(markers: [Marker]) -> Bool {
        // Check if we only have the start marker
        return markers.count == 1 && 
               markers.first?.timestamp == .zero && 
               markers.first?.labels.contains("Start") == true
    }

    func clearCache() {
        cache.removeAll()
    }

    func clearCacheForVideo(url: URL) {
        if let hash = generateVideoHash(for: url) {
            cache.removeValue(forKey: hash)
        }
    }
}

