//

import Foundation
import AppKit

@MainActor
class PermissionVerificationManager {
    static let shared = PermissionVerificationManager()

    // Bookmark keys for UserDefaults
    private let videoDirectoryBookmarkKey = "VideoDirectoryBookmark"
    private let metadataDirectoryBookmarkKey = "MetadataDirectoryBookmark"

    private init() {}

    /// Verifies permissions for default directories at app launch
    func verifyPermissionsAtLaunch() async {
        print("[PermissionVerification] Starting permission verification at launch")

        // First try to restore existing bookmarks
        let hasVideoAccess = await restoreBookmarkAccess(for: .videoDirectory)
        let hasMetadataAccess = await restoreBookmarkAccess(for: .metadataDirectory)

        // Request permissions for directories we don't have access to
        if !hasVideoAccess {
            print("[PermissionVerification] Need to request video directory access")
            _ = await requestDirectoryAccess(for: .videoDirectory)
        }

        if !hasMetadataAccess {
            print("[PermissionVerification] Need to request metadata directory access")
            _ = await requestDirectoryAccess(for: .metadataDirectory)
        }
    }

    enum DirectoryType {
        case videoDirectory
        case metadataDirectory

        var url: URL? {
            switch self {
            case .videoDirectory:
                return DefaultConfiguration.url(fromPath: DefaultConfiguration.defaultVideoDirectory)
            case .metadataDirectory:
                return DefaultConfiguration.url(fromPath: DefaultConfiguration.defaultMetadataDirectory)
            }
        }

        var bookmarkKey: String {
            switch self {
            case .videoDirectory:
                return "VideoDirectoryBookmark"
            case .metadataDirectory:
                return "MetadataDirectoryBookmark"
            }
        }

        var displayName: String {
            switch self {
            case .videoDirectory:
                return "Video Directory"
            case .metadataDirectory:
                return "Metadata Directory"
            }
        }

        var description: String {
            switch self {
            case .videoDirectory:
                return "SwiftMedia needs access to your video directory to load and play video files."
            case .metadataDirectory:
                return "SwiftMedia needs access to save markers and project data."
            }
        }
    }

    /// Restores access from a saved bookmark
    private func restoreBookmarkAccess(for directoryType: DirectoryType) async -> Bool {
        guard let bookmarkData = UserDefaults.standard.data(forKey: directoryType.bookmarkKey) else {
            print("[PermissionVerification] No saved bookmark for \(directoryType.displayName)")
            return false
        }

        do {
            var isStale = false
            let url = try URL(resolvingBookmarkData: bookmarkData,
                            options: .withSecurityScope,
                            relativeTo: nil,
                            bookmarkDataIsStale: &isStale)

            if isStale {
                print("[PermissionVerification] Bookmark is stale for \(directoryType.displayName)")
                // Remove stale bookmark
                UserDefaults.standard.removeObject(forKey: directoryType.bookmarkKey)
                return false
            }

            // Start accessing the security-scoped resource
            if url.startAccessingSecurityScopedResource() {
                print("[PermissionVerification] Successfully restored access to \(directoryType.displayName)")

                // Ensure the directory exists
                try? FileManager.default.createDirectory(at: url, withIntermediateDirectories: true)

                // We should stop accessing when done, but for directories we need constant access
                // The system will manage this for us
                return true
            } else {
                print("[PermissionVerification] Failed to start accessing security-scoped resource")
                return false
            }
        } catch {
            print("[PermissionVerification] Failed to resolve bookmark: \(error)")
            // Remove invalid bookmark
            UserDefaults.standard.removeObject(forKey: directoryType.bookmarkKey)
            return false
        }
    }

    /// Requests directory access using NSOpenPanel
    func requestDirectoryAccess(for directoryType: DirectoryType) async -> Bool {
        return await MainActor.run {
            guard let targetURL = directoryType.url else {
                print("[PermissionVerification] Invalid URL for \(directoryType.displayName)")
                return false
            }

            // First show an informative dialog
            let alert = NSAlert()
            alert.messageText = "Grant Directory Access"
            alert.informativeText = "\(directoryType.description)\n\nDirectory: \(targetURL.path)\n\nClick 'Grant Access' to select the directory."
            alert.alertStyle = .informational
            alert.addButton(withTitle: "Grant Access")
            alert.addButton(withTitle: "Cancel")

            let response = alert.runModal()

            if response == .alertFirstButtonReturn {
                // User wants to grant access, show NSOpenPanel
                let panel = NSOpenPanel()
                panel.title = "Select \(directoryType.displayName)"
                panel.message = "Please select the \(directoryType.displayName) folder to grant access"
                panel.prompt = "Grant Access"
                panel.canChooseFiles = false
                panel.canChooseDirectories = true
                panel.allowsMultipleSelection = false
                panel.canCreateDirectories = true
                panel.showsHiddenFiles = true

                // Pre-select the target directory
                panel.directoryURL = targetURL

                // Ensure parent directory exists
                if let parent = targetURL.parent {
                    try? FileManager.default.createDirectory(at: parent, withIntermediateDirectories: true)
                }

                if panel.runModal() == .OK,
                   let selectedURL = panel.url {
                    // Create security-scoped bookmark
                    do {
                        let bookmarkData = try selectedURL.bookmarkData(
                            options: [.withSecurityScope],
                            includingResourceValuesForKeys: nil,
                            relativeTo: nil
                        )

                        // Save bookmark
                        UserDefaults.standard.set(bookmarkData, forKey: directoryType.bookmarkKey)

                        // Start accessing immediately
                        if selectedURL.startAccessingSecurityScopedResource() {
                            // Ensure directory exists
                            try? FileManager.default.createDirectory(at: selectedURL, withIntermediateDirectories: true)

                            print("[PermissionVerification] Successfully saved bookmark and gained access to \(directoryType.displayName)")
                            return true
                        }
                    } catch {
                        print("[PermissionVerification] Failed to create bookmark: \(error)")

                        // Show error to user
                        let errorAlert = NSAlert()
                        errorAlert.messageText = "Permission Error"
                        errorAlert.informativeText = "Failed to save permission for \(directoryType.displayName): \(error.localizedDescription)"
                        errorAlert.alertStyle = .critical
                        errorAlert.runModal()
                    }
                }
            }

            return false
        }
    }

    /// Check if we have access to a specific directory
    func hasAccess(to directoryType: DirectoryType) async -> Bool {
        // First check if we can restore from bookmark
        if await restoreBookmarkAccess(for: directoryType) {
            return true
        }

        // Otherwise check direct file system access
        guard let url = directoryType.url else { return false }

        return FileManager.default.isReadableFile(atPath: url.path) &&
               FileManager.default.isWritableFile(atPath: url.path)
    }

    /// Get the accessible directory URL (with fallback to Application Support)
    func getAccessibleDirectoryURL(for directoryType: DirectoryType) async -> URL {
        // Try to get the configured directory with proper access
        if await hasAccess(to: directoryType), let url = directoryType.url {
            return url
        }

        // Fallback to Application Support (always accessible in sandbox)
        let appSupport = FileManager.default.urls(for: .applicationSupportDirectory, in: .userDomainMask).first!
        let fallbackURL: URL

        switch directoryType {
        case .videoDirectory:
            fallbackURL = appSupport.appendingPathComponent("SwiftMedia/Videos", isDirectory: true)
        case .metadataDirectory:
            fallbackURL = appSupport.appendingPathComponent("SwiftMedia/Metadata", isDirectory: true)
        }

        // Ensure fallback directory exists
        try? FileManager.default.createDirectory(at: fallbackURL, withIntermediateDirectories: true)

        print("[PermissionVerification] Using fallback directory for \(directoryType.displayName): \(fallbackURL.path)")
        return fallbackURL
    }
}

// Helper extension for URL
extension URL {
    var parent: URL? {
        return self.deletingLastPathComponent()
    }
}

