import Testing
@testable import SwiftMedia

@Suite("Shortcut Conflict Detection Tests")
struct ShortcutConflictDetectionTests {

    @Test("Detects conflicts with rating shortcuts")
    func testRatingShortcutConflicts() {
        let viewModel = SettingsViewModel()

        // Set a main shortcut to conflict with a rating key
        viewModel.tempPlayPauseKey = "1"  // Conflicts with rating 1

        let conflicts = viewModel.hasShortcutConflicts()
        #expect(conflicts.count > 0)
        #expect(conflicts.contains { $0.contains("'1'") && $0.contains("Play/Pause") && $0.contains("Set Rating 1") })
    }

    @Test("Detects conflicts with volume shortcuts")
    func testVolumeShortcutConflicts() {
        let viewModel = SettingsViewModel()

        // Set a main shortcut to conflict with volume up
        viewModel.tempAddMarkerKey = "g"  // Conflicts with volume up

        let conflicts = viewModel.hasShortcutConflicts()
        #expect(conflicts.count > 0)
        #expect(conflicts.contains { $0.contains("'g'") && $0.contains("Add Marker") && $0.contains("Volume Up") })
    }

    @Test("Detects conflicts with performance overlay")
    func testPerformanceOverlayConflicts() {
        let viewModel = SettingsViewModel()

        // Set a main shortcut to conflict with performance overlay
        viewModel.tempDeleteMarkerKey = "p"  // Conflicts with performance overlay

        let conflicts = viewModel.hasShortcutConflicts()
        #expect(conflicts.count > 0)
        #expect(conflicts.contains { $0.contains("'p'") && $0.contains("Delete Marker") && $0.contains("Toggle Performance Overlay") })
    }

    @Test("Detects conflicts with toggle marker list")
    func testToggleMarkerListConflicts() {
        let viewModel = SettingsViewModel()

        // Set a main shortcut to conflict with toggle marker list
        viewModel.tempEditMarkerKey = "d"  // Conflicts with toggle marker list

        let conflicts = viewModel.hasShortcutConflicts()
        #expect(conflicts.count > 0)
        #expect(conflicts.contains { $0.contains("'d'") && $0.contains("Edit Marker") && $0.contains("Toggle Marker List") })
    }

    @Test("Detects conflicts with open file")
    func testOpenFileConflicts() {
        let viewModel = SettingsViewModel()

        // Set a main shortcut to conflict with open file
        viewModel.tempNextMarkerKey = "o"  // Conflicts with open file

        let conflicts = viewModel.hasShortcutConflicts()
        #expect(conflicts.count > 0)
        #expect(conflicts.contains { $0.contains("'o'") && $0.contains("Next Marker") && $0.contains("Open File") })
    }

    @Test("Detects multiple rating key conflicts")
    func testMultipleRatingConflicts() {
        let viewModel = SettingsViewModel()

        // Set multiple shortcuts to number keys
        viewModel.tempPlayPauseKey = "1"
        viewModel.tempAddMarkerKey = "2"
        viewModel.tempPreviousMarkerKey = "3"

        let conflicts = viewModel.hasShortcutConflicts()
        #expect(conflicts.count >= 3)
        #expect(conflicts.contains { $0.contains("'1'") })
        #expect(conflicts.contains { $0.contains("'2'") })
        #expect(conflicts.contains { $0.contains("'3'") })
    }

    @Test("No conflicts with default configuration")
    func testNoConflictsWithDefaults() {
        let viewModel = SettingsViewModel()
        viewModel.resetShortcutsToDefaults()

        let conflicts = viewModel.hasShortcutConflicts()
        #expect(conflicts.isEmpty, "Default shortcuts should have no conflicts")
    }

    @Test("Detects scrubbing vs main shortcut conflicts")
    func testScrubbingVsMainShortcutConflicts() {
        let viewModel = SettingsViewModel()

        // Create a scrubbing config that conflicts with a main shortcut
        viewModel.tempScrubbingConfigs = [
            ScrubbingConfig(
                name: "Test",
                backwardKey: "m",  // Conflicts with add marker
                forwardKey: "e",   // Conflicts with next marker
                playingSeekSeconds: 1.0,
                pausedSeekFrames: 1
            )
        ]

        let conflicts = viewModel.hasShortcutConflicts()
        #expect(conflicts.count >= 2)
        #expect(conflicts.contains { $0.contains("'m'") && $0.contains("Add Marker") && $0.contains("scrubbing") })
        #expect(conflicts.contains { $0.contains("'e'") && $0.contains("Next Marker") && $0.contains("scrubbing") })
    }

    @Test("Comprehensive conflict check with all shortcuts")
    func testComprehensiveConflictCheck() {
        let viewModel = SettingsViewModel()

        // Get all shortcuts and verify they're all included
        let allShortcuts = viewModel.allCurrentShortcuts

        // Should include all categories
        let categories = Set(allShortcuts.map { $0.category })
        #expect(categories.contains("Playback"))
        #expect(categories.contains("Markers"))
        #expect(categories.contains("Application"))
        #expect(categories.contains("Navigation"))
        #expect(categories.contains("Volume"))
        #expect(categories.contains("Rating"))

        // Should include rating shortcuts 1-5
        let ratingKeys = allShortcuts.filter { $0.category == "Rating" }.map { $0.key }
        #expect(ratingKeys.contains("1"))
        #expect(ratingKeys.contains("2"))
        #expect(ratingKeys.contains("3"))
        #expect(ratingKeys.contains("4"))
        #expect(ratingKeys.contains("5"))

        // Should include volume shortcuts
        let volumeKeys = allShortcuts.filter { $0.category == "Volume" }.map { $0.key }
        #expect(volumeKeys.contains("g"))
        #expect(volumeKeys.contains("v"))

        // Should include application shortcuts
        let appShortcuts = allShortcuts.filter { $0.category == "Application" }
        #expect(appShortcuts.contains { $0.key == "p" && $0.description.contains("Performance") })
        #expect(appShortcuts.contains { $0.key == "o" && $0.description.contains("Open") })

        // Should include marker shortcuts
        let markerShortcuts = allShortcuts.filter { $0.category == "Markers" }
        #expect(markerShortcuts.contains { $0.key == "d" && $0.description.contains("Toggle Marker List") })
    }
}

