    print("Test 2: Conflict with rating key")
    print("Changed Play/Pause to use key '1'")
    print("Conflicts found: \(testDuplicates.count)")
    if !testDuplicates.isEmpty {
        let conflictingShortcuts = testShortcuts.filter { testDuplicates.contains($0.key) }
        for key in testDuplicates {
            let shortcuts = conflictingShortcuts.filter { $0.key == key }
