    // Test 3: Scrubbing vs main shortcut conflicts
    let scrubbingKeys = ["m", "e"]  // Conflicts with add marker and next marker

    print("Test 3: Scrubbing conflicts")
    print("Scrubbing keys: \(scrubbingKeys)")

    for scrubbingKey in scrubbingKeys {
        if let conflictingShortcut = defaultShortcuts.first(where: { $0.key == scrubbingKey }) {
            print("Conflict: '\(scrubbingKey)' is used by both \(conflictingShortcut.description) and scrubbing")
        }
    }
    print("âœ… PASS: Correctly detected scrubbing conflicts\n")

