    print("=======")
    print("All shortcut categories included:")
    let categories = Set(defaultShortcuts.map { $0.category })
    for category in categories.sorted() {
        let count = defaultShortcuts.filter { $0.category == category }.count
        print("- \(category): \(count) shortcuts")
    }
}

testConflictDetection()

