//
//  KeyboardShortcutRecorder.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import SwiftUI

struct KeyboardShortcutRecorder: View {
    @Binding var shortcut: String
    @State private var isRecording = false
    @FocusState private var isFocused: Bool

    // Format the shortcut for display
    private var displayText: String {
        if isRecording {
            return "Press a key..."
        } else if shortcut.isEmpty {
            return "Click to set"
        } else if shortcut == " " {
            return "Space"
        } else if shortcut == "\r" {
            return "Return"
        } else if shortcut == "\t" {
            return "Tab"
        } else if shortcut == "\u{001B}" {
            return "Escape"
        } else {
            return shortcut
        }
    }

    var body: some View {
        Button(action: {
            isRecording.toggle()
            isFocused = isRecording
        }) {
            Text(displayText)
                .font(.system(.body, design: .monospaced))
                .frame(minWidth: 80)
                .padding(.horizontal, 12)
                .padding(.vertical, 6)
                .background(
                    RoundedRectangle(cornerRadius: 6)
                        .fill(isRecording ? Color.accentColor.opacity(0.2) : Color(NSColor.textBackgroundColor))
                )
                .overlay(
                    RoundedRectangle(cornerRadius: 6)
                        .stroke(isRecording ? Color.accentColor : Color.clear, lineWidth: 2)
                )
        }
        .buttonStyle(.plain)
        .focusable()
        .focused($isFocused)
        .onKeyPress { keyPress in
            if isRecording {
                // Capture the key
                shortcut = keyPress.characters
                isRecording = false
                isFocused = false
                return .handled
            }
            return .ignored
        }
        .onAppear {
            if shortcut.isEmpty {
                shortcut = ""
            }
        }
    }
}

// Validation helper
struct ScrubbingConfigValidator {
    static func validate(_ config: ScrubbingConfig) -> [String] {
        var errors: [String] = []

        if config.name.trimmingCharacters(in: .whitespaces).isEmpty {
            errors.append("Name cannot be empty")
        }

        if config.backwardKey.isEmpty {
            errors.append("Backward key must be set")
        }

        if config.forwardKey.isEmpty {
            errors.append("Forward key must be set")
        }

        if config.backwardKey == config.forwardKey {
            errors.append("Backward and forward keys must be different")
        }

        if config.playingSeekSeconds <= 0 {
            errors.append("Playing seek time must be greater than 0")
        }

        if config.pausedSeekFrames < 0 {
            errors.append("Paused seek frames cannot be negative")
        }

        return errors
    }

    static func hasConflicts(_ configs: [ScrubbingConfig]) -> [(String, String)] {
        var conflicts: [(String, String)] = []

        for firstConfigIndex in 0..<configs.count {
            for secondConfigIndex in (firstConfigIndex + 1)..<configs.count {
                let config1 = configs[firstConfigIndex]
                let config2 = configs[secondConfigIndex]

                if config1.forwardKey == config2.forwardKey ||
                   config1.forwardKey == config2.backwardKey ||
                   config1.backwardKey == config2.forwardKey ||
                   config1.backwardKey == config2.backwardKey {
                    conflicts.append((config1.name, config2.name))
                }
            }
        }

        return conflicts
    }
}

