//
//  ErrorBanner.swift
//  SwiftMedia
//
//  Created by Claude on 6/25/25.
//

import SwiftUI

struct ErrorBanner: View {
    let error: PlayerError
    let onDismiss: () -> Void

    @State private var isShowing = true

    var body: some View {
        if isShowing {
            HStack(spacing: DesignSystem.Spacing.sm) {
                Image(systemName: iconName)
                    .font(.system(size: 16))
                    .foregroundColor(iconColor)

                VStack(alignment: .leading, spacing: DesignSystem.Spacing.xxs) {
                    Text(error.errorDescription ?? "An error occurred")
                        .font(DesignSystem.Typography.headline)
                        .foregroundColor(DesignSystem.Colors.textPrimary)

                    if let recovery = error.recoverySuggestion {
                        Text(recovery)
                            .font(DesignSystem.Typography.caption)
                            .foregroundColor(DesignSystem.Colors.textSecondary)
                    }
                }

                Spacer()

                Button(action: dismiss) {
                    Image(systemName: "xmark.circle.fill")
                        .font(.system(size: 20))
                        .foregroundColor(DesignSystem.Colors.textTertiary)
                }
                .buttonStyle(PlainButtonStyle())
                .onHover { hovering in
                    NSCursor.pointingHand.set()
                }
            }
            .padding(DesignSystem.Spacing.md)
            .background(backgroundColor)
            .cornerRadius(DesignSystem.CornerRadius.medium)
            .shadow(radius: 4)
            .transition(.asymmetric(
                insertion: .move(edge: .top).combined(with: .opacity),
                removal: .move(edge: .top).combined(with: .opacity)
            ))
            .animation(DesignSystem.Animation.spring, value: isShowing)
            .onAppear {
                // Auto-dismiss after 5 seconds for non-critical errors
                if !error.isCritical {
                    DispatchQueue.main.asyncAfter(deadline: .now() + 5) {
                        dismiss()
                    }
                }
            }
        }
    }

    private var iconName: String {
        switch error {
        case .fileNotFound:
            return "doc.badge.xmark"
        case .unsupportedFormat:
            return "exclamationmark.triangle"
        case .decodingFailed:
            return "exclamationmark.octagon"
        case .markerSaveFailed:
            return "bookmark.slash"
        }
    }

    private var iconColor: Color {
        error.isCritical ? DesignSystem.Colors.error : DesignSystem.Colors.warning
    }

    private var backgroundColor: Color {
        if error.isCritical {
            return DesignSystem.Colors.error.opacity(0.15)
        } else {
            return DesignSystem.Colors.warning.opacity(0.15)
        }
    }

    private func dismiss() {
        withAnimation(DesignSystem.Animation.fast) {
            isShowing = false
        }

        DispatchQueue.main.asyncAfter(deadline: .now() + 0.3) {
            onDismiss()
        }
    }
}

// MARK: - Error Extensions
extension PlayerError {
    var isCritical: Bool {
        switch self {
        case .fileNotFound, .decodingFailed:
            return true
        case .unsupportedFormat, .markerSaveFailed:
            return false
        }
    }

    var recoverySuggestion: String? {
        switch self {
        case .fileNotFound:
            return "Check if the file has been moved or deleted"
        case .unsupportedFormat:
            return "Try converting the video to H.264 or H.265 format"
        case .decodingFailed:
            return "The video file may be corrupted"
        case .markerSaveFailed:
            return "Check permissions and try again"
        }
    }
}

// MARK: - Generic Error Toast
struct ErrorToast: View {
    let message: String
    let type: ErrorType
    let onDismiss: () -> Void

    enum ErrorType {
        case error
        case warning
        case info
        case success

        var color: Color {
            switch self {
            case .error: return DesignSystem.Colors.error
            case .warning: return DesignSystem.Colors.warning
            case .info: return DesignSystem.Colors.info
            case .success: return DesignSystem.Colors.success
            }
        }

        var icon: String {
            switch self {
            case .error: return "xmark.circle.fill"
            case .warning: return "exclamationmark.triangle.fill"
            case .info: return "info.circle.fill"
            case .success: return "checkmark.circle.fill"
            }
        }
    }

    @State private var isShowing = true

    var body: some View {
        if isShowing {
            HStack(spacing: DesignSystem.Spacing.sm) {
                Image(systemName: type.icon)
                    .foregroundColor(type.color)

                Text(message)
                    .font(DesignSystem.Typography.body)
                    .foregroundColor(DesignSystem.Colors.textPrimary)

                Spacer()

                Button(action: {
                    withAnimation(DesignSystem.Animation.fast) {
                        isShowing = false
                    }
                    onDismiss()
                }) {
                    Image(systemName: "xmark")
                        .font(.caption)
                        .foregroundColor(DesignSystem.Colors.textTertiary)
                }
                .buttonStyle(PlainButtonStyle())
            }
            .padding(.horizontal, DesignSystem.Spacing.md)
            .padding(.vertical, DesignSystem.Spacing.sm)
            .background(type.color.opacity(0.15))
            .overlay(
                RoundedRectangle(cornerRadius: DesignSystem.CornerRadius.small)
                    .stroke(type.color.opacity(0.3), lineWidth: 1)
            )
            .cornerRadius(DesignSystem.CornerRadius.small)
            .transition(.asymmetric(
                insertion: .move(edge: .top).combined(with: .opacity),
                removal: .move(edge: .top).combined(with: .opacity)
            ))
            .animation(DesignSystem.Animation.spring, value: isShowing)
        }
    }
}

//
//  DesignSystem.swift
//  SwiftMedia
//
//  Created by Claude on 6/25/25.
//

import SwiftUI

// MARK: - Design System Constants
struct DesignSystem {

    // MARK: - Spacing (8pt grid)
    struct Spacing {
        static let xxs: CGFloat = 4
        static let xs: CGFloat = 8
        static let sm: CGFloat = 12
        static let md: CGFloat = 16
        static let lg: CGFloat = 24
        static let xl: CGFloat = 32
        static let xxl: CGFloat = 48
    }

    // MARK: - Colors
    struct Colors {
        // Primary colors
        static let primary = Color.accentColor
        static let primaryVariant = Color.blue.opacity(0.8)

        // Background colors
        static let background = Color.black
        static let surface = Color(white: 0.1)
        static let surfaceSecondary = Color(white: 0.15)

        // UI Element colors
        static let controlBackground = Color.white.opacity(0.1)
        static let controlBackgroundHover = Color.white.opacity(0.15)
        static let controlBackgroundPressed = Color.white.opacity(0.2)

        // Text colors
        static let textPrimary = Color.white
        static let textSecondary = Color.white.opacity(0.7)
        static let textTertiary = Color.white.opacity(0.5)

        // Marker colors
        static let markerDefault = Color.yellow
        static let markerSelected = Color.orange

        // Status colors
        static let success = Color.green
        static let warning = Color.orange
        static let error = Color.red
        static let info = Color.blue
    }

    // MARK: - Typography
    struct Typography {
        static let largeTitle = Font.system(size: 28, weight: .bold, design: .default)
        static let title = Font.system(size: 20, weight: .semibold, design: .default)
        static let headline = Font.system(size: 16, weight: .medium, design: .default)
        static let body = Font.system(size: 14, weight: .regular, design: .default)
        static let caption = Font.system(size: 12, weight: .regular, design: .default)
        static let footnote = Font.system(size: 10, weight: .regular, design: .default)

        // Monospaced variants
        static let monoBody = Font.system(size: 14, weight: .regular, design: .monospaced)
        static let monoCaption = Font.system(size: 12, weight: .regular, design: .monospaced)
    }

    // MARK: - Corner Radius
    struct CornerRadius {
        static let small: CGFloat = 4
        static let medium: CGFloat = 8
        static let large: CGFloat = 12
    }

    // MARK: - Shadows
    struct Shadows {
        static let small = (color: Color.black.opacity(0.3), radius: CGFloat(2), x: CGFloat(0), y: CGFloat(1))
        static let medium = (color: Color.black.opacity(0.4), radius: CGFloat(4), x: CGFloat(0), y: CGFloat(2))
        static let large = (color: Color.black.opacity(0.5), radius: CGFloat(8), x: CGFloat(0), y: CGFloat(4))
    }

    // MARK: - Animation
    struct Animation {
        static let fast = SwiftUI.Animation.easeInOut(duration: 0.15)
        static let standard = SwiftUI.Animation.easeInOut(duration: 0.25)
        static let slow = SwiftUI.Animation.easeInOut(duration: 0.35)

        static let spring = SwiftUI.Animation.spring(response: 0.3, dampingFraction: 0.8)
    }
}

// MARK: - View Modifiers
extension View {
    func cardStyle() -> some View {
        self
            .background(DesignSystem.Colors.surface)
            .cornerRadius(DesignSystem.CornerRadius.medium)
            .shadow(color: .black.opacity(0.3), radius: 4, x: 0, y: 2)

