private func startAdaptiveMonitoring() {
    // Skip monitoring if in background
    guard !isInBackground else {
        currentSamplingInterval = maxSamplingInterval
        return
    }

    timer = Timer.scheduledTimer(withTimeInterval: currentSamplingInterval, repeats: true) { [weak self] _ in
        self?.updateSystemMetrics()
        self?.updateFrameRateStability()
        self?.updateCurrentMetrics()
        self?.adjustSamplingRateIfNeeded()
    }
}

private func restartTimer() {
    timer?.invalidate()
    startAdaptiveMonitoring()
}

private func adjustSamplingRateIfNeeded() {
    let score = calculatePerformanceScore()
    performanceScoreHistory.append(score)

    // Adjust sampling rate based on average performance score
    let scores = performanceScoreHistory.allElements
    guard !scores.isEmpty else { return }

    let averageScore = scores.reduce(0, +) / Double(scores.count)
    adjustSamplingRate(forPerformanceScore: averageScore)
}

private func adjustSamplingRate(forPerformanceScore score: Double) {
    let oldInterval = currentSamplingInterval

    // Adaptive sampling: increase frequency when performance is poor
    if score < 70 {
        currentSamplingInterval = minSamplingInterval
    } else if score < 85 {
        currentSamplingInterval = 1.0
    } else if score < 95 {
        currentSamplingInterval = 2.0
    } else {
        currentSamplingInterval = isInBackground ? maxSamplingInterval : 3.0
    }

    // Restart timer if interval changed significantly
    if abs(oldInterval - currentSamplingInterval) > 0.1 {
        logger.info("Adjusting sampling rate from \(oldInterval)s to \(currentSamplingInterval)s (score: \(score))")
        restartTimer()
    }
}

private func calculatePerformanceScore() -> Double {
    // Calculate a performance score from 0-100
    var score = 100.0

    // CPU impact (weight: 40%)
    if cpuUsage > 10 {
        score -= min(40, (cpuUsage - 10) * 0.8)
    }

    // Memory impact (weight: 20%)
    if memoryUsage > 50 {
        score -= min(20, (memoryUsage - 50) * 0.4)
    }

    // Frame drops impact (weight: 30%)
    if droppedFrames > 0 {
        let dropPenalty = min(30, Double(droppedFrames) * 2)
        score -= dropPenalty
    }

    // Frame rate stability impact (weight: 10%)
    score -= (100 - frameRateStability) * 0.1

    return max(0, score)
}

private func updateSystemMetrics() {
    // Skip expensive operations if in background
    if isInBackground {
        return
    }

    // Send updates through debounced publishers
    let cpu = getCurrentCPUUsage()
    cpuPublisher.send(cpu)

    let memory = getCurrentMemoryUsage()
    memoryPublisher.send(memory)

    // Frame rate is updated via handleAccessLogUpdate
    frameRatePublisher.send(frameRate)

    // Log warnings for high usage
    if cpu > 40 {
        logger.warning("High CPU usage: \(String(format: "%.1f", cpu))%")
    }

    if memory > 80 {
        logger.warning("High memory usage: \(String(format: "%.1f", memory))%")
    }
}

