//
//  PerformanceMonitor.swift
//  SwiftMedia
//
//  Created by Claude on 6/25/25.
//

import Foundation
import AVFoundation
import os.log
import Combine
import AppKit

// Circular buffer for performance data
struct CircularBuffer<T> {
    private var storage: [T?]
    private var writeIndex = 0
    private var count = 0
    private let capacity: Int

    init(capacity: Int) {
        self.capacity = capacity
        self.storage = Array(repeating: nil, count: capacity)
    }

    mutating func append(_ element: T) {
        storage[writeIndex] = element
        writeIndex = (writeIndex + 1) % capacity
        count = min(count + 1, capacity)
    }

    mutating func clear() {
        storage = Array(repeating: nil, count: capacity)
        writeIndex = 0
        count = 0
    }

    var allElements: [T] {
        guard count > 0 else { return [] }

        if count < capacity {
            return storage.compactMap { $0 }
        } else {
            var result: [T] = []
            var readIndex = writeIndex
            for _ in 0..<capacity {
                if let element = storage[readIndex] {
                    result.append(element)
                }
                readIndex = (readIndex + 1) % capacity
            }
            return result
        }
    }
}

class PerformanceMonitor: ObservableObject {
    static let shared = PerformanceMonitor()

    @Published var cpuUsage: Double = 0.0
    @Published var memoryUsage: Double = 0.0
    @Published var droppedFrames: Int = 0
    @Published var frameRate: Double = 0.0
    @Published var frameRateStability: Double = 100.0
    @Published var currentMetrics: PerformanceMetrics?
    @Published var isMonitoring = false

    // History for graphs - using circular buffers for efficiency
    @Published var cpuHistory: [Double] = []
    @Published var memoryHistory: [Double] = []
    @Published var frameRateHistory: [Double] = []

    private let logger = Logger(subsystem: "com.swiftmedia", category: "Performance")
    private var timer: Timer?
    private var player: AVPlayer?
    private var lastAccessLogEntry: AVPlayerItemAccessLogEvent?
    private var frameRateSamples = CircularBuffer<Double>(capacity: 30)
    private let maxHistorySize = 60

    // Circular buffers for internal storage
    private var cpuBuffer = CircularBuffer<Double>(capacity: 60)
    private var memoryBuffer = CircularBuffer<Double>(capacity: 60)
    private var frameRateBuffer = CircularBuffer<Double>(capacity: 60)

    private var cancellables = Set<AnyCancellable>()

    // For accurate CPU usage calculation
    private var lastCPUInfo: (user: time_value_t, system: time_value_t)?
    private var lastCPUCheckTime: TimeInterval = 0

    // Adaptive sampling rate
    private var currentSamplingInterval: TimeInterval = 1.0
    private let minSamplingInterval: TimeInterval = 0.5
    private let maxSamplingInterval: TimeInterval = 5.0
    private var performanceScoreHistory = CircularBuffer<Double>(capacity: 10)

    // Debounced publishers
    private let cpuPublisher = PassthroughSubject<Double, Never>()
    private let memoryPublisher = PassthroughSubject<Double, Never>()
    private let frameRatePublisher = PassthroughSubject<Double, Never>()

    // App lifecycle monitoring
    private var isInBackground = false

    private init() {
        setupAppLifecycleMonitoring()
        setupDebouncedPublishers()
    }

    private func setupAppLifecycleMonitoring() {
        // Monitor app lifecycle to pause monitoring in background
        NSWorkspace.shared.notificationCenter.publisher(for: NSWorkspace.didActivateApplicationNotification)
            .sink { [weak self] notification in
                if let app = notification.userInfo?[NSWorkspace.applicationUserInfoKey] as? NSRunningApplication,
                   app.bundleIdentifier == Bundle.main.bundleIdentifier {
                    self?.handleAppActivation()
                }
            }
            .store(in: &cancellables)

        NSWorkspace.shared.notificationCenter.publisher(for: NSWorkspace.didDeactivateApplicationNotification)
            .sink { [weak self] notification in
                if let app = notification.userInfo?[NSWorkspace.applicationUserInfoKey] as? NSRunningApplication,
                   app.bundleIdentifier == Bundle.main.bundleIdentifier {
                    self?.handleAppDeactivation()
                }
            }
            .store(in: &cancellables)
    }

    private func setupDebouncedPublishers() {
        // Debounce CPU updates to reduce UI overhead
        cpuPublisher
            .debounce(for: .milliseconds(250), scheduler: DispatchQueue.main)
            .sink { [weak self] value in
                self?.cpuUsage = value
                self?.cpuBuffer.append(value)
                self?.cpuHistory = self?.cpuBuffer.allElements ?? []
            }
            .store(in: &cancellables)

        // Debounce memory updates
        memoryPublisher
            .debounce(for: .milliseconds(250), scheduler: DispatchQueue.main)
            .sink { [weak self] value in
                self?.memoryUsage = value
                self?.memoryBuffer.append(value)
                self?.memoryHistory = self?.memoryBuffer.allElements ?? []
            }
            .store(in: &cancellables)

        // Debounce frame rate updates
        frameRatePublisher
            .debounce(for: .milliseconds(100), scheduler: DispatchQueue.main)
            .sink { [weak self] value in
                self?.frameRate = value
                self?.frameRateBuffer.append(value)
                self?.frameRateHistory = self?.frameRateBuffer.allElements ?? []
            }
            .store(in: &cancellables)
    }

    private func handleAppActivation() {
        isInBackground = false
        if isMonitoring {
            logger.info("App activated, resuming performance monitoring")
            adjustSamplingRate(forPerformanceScore: calculatePerformanceScore())
        }
    }

    private func handleAppDeactivation() {
        isInBackground = true
        if isMonitoring {
            logger.info("App deactivated, reducing monitoring frequency")
            // Increase sampling interval when in background
            currentSamplingInterval = maxSamplingInterval
            restartTimer()
        }
    }

    func startMonitoring(player: AVPlayer) {
        guard !isMonitoring else { return }

        self.player = player
        isMonitoring = true

        // Clear previous data
        clearHistory()

        // Monitor player access logs
        NotificationCenter.default.publisher(for: .AVPlayerItemNewAccessLogEntry, object: player.currentItem)
            .sink { [weak self] _ in
                self?.handleAccessLogUpdate()
            }
            .store(in: &cancellables)

        // Monitor error logs
        NotificationCenter.default.publisher(for: .AVPlayerItemNewErrorLogEntry, object: player.currentItem)
            .sink { [weak self] _ in
                self?.handleErrorLogUpdate()
            }
            .store(in: &cancellables)

        // Start system metrics monitoring with adaptive interval
        startAdaptiveMonitoring()

        logger.info("Performance monitoring started")
    }

    func stopMonitoring() {
        isMonitoring = false
        timer?.invalidate()
        timer = nil
        cancellables.removeAll()

        // Reset CPU tracking
        lastCPUInfo = nil
        lastCPUCheckTime = 0

        logger.info("Performance monitoring stopped")
    }

    private func clearHistory() {
        cpuBuffer.clear()
        memoryBuffer.clear()
        frameRateBuffer.clear()
        frameRateSamples.clear()
        performanceScoreHistory.clear()
        cpuHistory = []
        memoryHistory = []
        frameRateHistory = []
        droppedFrames = 0
    }

    private func handleAccessLogUpdate() {
        guard let item = player?.currentItem,
              let events = item.accessLog()?.events,
              let latestEvent = events.last else { return }

        // Track dropped frames
        if let lastEvent = lastAccessLogEntry {
            let newDrops = Int(latestEvent.numberOfDroppedVideoFrames - lastEvent.numberOfDroppedVideoFrames)
            if newDrops > 0 {
                droppedFrames += newDrops
                logger.warning("Dropped \(newDrops) frames (total: \(self.droppedFrames))")
            }
        }

        lastAccessLogEntry = latestEvent

        // Calculate frame rate from playback info
        if latestEvent.indicatedBitrate > 0 && latestEvent.observedBitrate > 0 {
            // Estimate frame rate based on segment download rate
            let segmentDuration = latestEvent.segmentsDownloadedDuration
            if segmentDuration > 0 {
                frameRate = 30.0 // Default to expected frame rate

                // Adjust based on playback performance
                let efficiency = latestEvent.observedBitrate / latestEvent.indicatedBitrate
                if efficiency < 0.95 {
                    frameRate *= efficiency
                }
            }
        }

        // Check for performance issues
        if latestEvent.numberOfStalls > 0 {
            logger.error("Playback stalled \(latestEvent.numberOfStalls) times")
        }
    }

    private func handleErrorLogUpdate() {
        guard let item = player?.currentItem,
              let events = item.errorLog()?.events else { return }

        for event in events {
            logger.error("Player error: \(event.errorComment ?? "Unknown error")")
        }
    }

    private func updateSystemMetrics() {
        // Update CPU usage
        cpuUsage = getCurrentCPUUsage()
        cpuHistory.append(cpuUsage)
        if cpuHistory.count > maxHistorySize {
            cpuHistory.removeFirst()
        }

        // Update memory usage
        memoryUsage = getCurrentMemoryUsage()
        memoryHistory.append(memoryUsage)
        if memoryHistory.count > maxHistorySize {
            memoryHistory.removeFirst()
        }

        // Update frame rate history
        frameRateHistory.append(frameRate)
        if frameRateHistory.count > maxHistorySize {
            frameRateHistory.removeFirst()
        }

        // Log warnings for high usage
        if cpuUsage > 40 {
            logger.warning("High CPU usage: \(String(format: "%.1f", self.cpuUsage))%")
        }

        if memoryUsage > 80 {
            logger.warning("High memory usage: \(String(format: "%.1f", self.memoryUsage))%")
        }
    }

    private func getCurrentCPUUsage() -> Double {
        var info = mach_task_basic_info()
        var count = mach_msg_type_number_t(MemoryLayout<mach_task_basic_info>.size) / 4

        let result = withUnsafeMutablePointer(to: &info) {
            $0.withMemoryRebound(to: integer_t.self, capacity: 1) {
                task_info(mach_task_self_,
                         task_flavor_t(MACH_TASK_BASIC_INFO),
                         $0,
                         &count)
            }
        }

        if result == KERN_SUCCESS {
            let currentTime = ProcessInfo.processInfo.systemUptime

            // If this is the first measurement, just store the values
            guard let lastInfo = lastCPUInfo, lastCPUCheckTime > 0 else {
                lastCPUInfo = (user: info.user_time, system: info.system_time)
                lastCPUCheckTime = currentTime
                return 0.0
            }

            // Calculate time elapsed since last check
            let timeElapsed = currentTime - lastCPUCheckTime
            guard timeElapsed > 0 else { return 0.0 }

            // Calculate CPU time used since last check
            let userTimeDelta = info.user_time.totalSeconds - lastInfo.user.totalSeconds
            let systemTimeDelta = info.system_time.totalSeconds - lastInfo.system.totalSeconds
            let totalCPUTimeDelta = userTimeDelta + systemTimeDelta

            // Store current values for next calculation
            lastCPUInfo = (user: info.user_time, system: info.system_time)
            lastCPUCheckTime = currentTime

            // Calculate percentage (multiply by 100 for percentage, multiply by number of cores for multi-core systems)
            let cpuCount = Double(ProcessInfo.processInfo.processorCount)
            let cpuPercent = (totalCPUTimeDelta / timeElapsed) * 100.0

            // Cap at 100% per core (so max is cpuCount * 100)
            return min(cpuPercent, cpuCount * 100.0)
        }

        return 0.0
    }

    private func getCurrentMemoryUsage() -> Double {
        var info = mach_task_basic_info()
        var count = mach_msg_type_number_t(MemoryLayout<mach_task_basic_info>.size) / 4

        let result = withUnsafeMutablePointer(to: &info) {
            $0.withMemoryRebound(to: integer_t.self, capacity: 1) {
                task_info(mach_task_self_,
                         task_flavor_t(MACH_TASK_BASIC_INFO),
                         $0,
                         &count)
            }
        }

        if result == KERN_SUCCESS {
            let usedMemory = Double(info.resident_size)
            let totalMemory = Double(ProcessInfo.processInfo.physicalMemory)
            return (usedMemory / totalMemory) * 100.0
        }

        return 0.0
    }

    private func updateFrameRateStability() {
        frameRateSamples.append(frameRate)
        if frameRateSamples.count > maxFrameRateSamples {
            frameRateSamples.removeFirst()
        }

        guard frameRateSamples.count > 5 else {
            frameRateStability = 100.0
            return
        }

        // Calculate stability based on variance
        let average = frameRateSamples.reduce(0, +) / Double(frameRateSamples.count)
        let variance = frameRateSamples.reduce(0) { result, rate in
            result + pow(rate - average, 2)
        } / Double(frameRateSamples.count)

        // Convert variance to stability percentage
        let stability = max(0, 100 - (variance * 10))
        frameRateStability = stability
    }

    private func updateCurrentMetrics() {
        currentMetrics = PerformanceMetrics(
            timestamp: Date(),
            cpuUsage: cpuUsage,
            memoryUsage: memoryUsage,
            droppedFrames: droppedFrames,
            frameRate: frameRate,
            frameRateStability: frameRateStability,
            gpuUsage: nil,
            decoderEfficiency: calculateDecoderEfficiency()
        )
    }

    private func calculateDecoderEfficiency() -> Double? {
        guard let lastEvent = lastAccessLogEntry,
              lastEvent.indicatedBitrate > 0 else { return nil }

        return (lastEvent.observedBitrate / lastEvent.indicatedBitrate) * 100.0
    }

    // Debug mode for detailed monitoring
    func enableDebugMode() {
        logger.info("Enabling performance debug mode")

        // Increase sampling frequency
        timer?.invalidate()
        timer = Timer.scheduledTimer(withTimeInterval: 0.5, repeats: true) { [weak self] _ in
            self?.updateSystemMetrics()
            self?.updateFrameRateStability()
            self?.updateCurrentMetrics()
        }
    }
}

// Extension for time_value_t
extension time_value_t {
    var totalSeconds: Double {
        return Double(self.seconds) + Double(self.microseconds) / 1_000_000.0
    }
}

