        var score = 100.0

        // CPU impact (weight: 40%)
        if cpuUsage > 10 {
            score -= min(40, (cpuUsage - 10) * 0.8)
        }

        // Memory impact (weight: 20%)
        if memoryUsage > 50 {
            score -= min(20, (memoryUsage - 50) * 0.4)
        }

        // Frame drops impact (weight: 30%)
        if droppedFrames > 0 {
            let dropPenalty = min(30, Double(droppedFrames) * 2)
            score -= dropPenalty
        }

        // Frame rate stability impact (weight: 10%)
        score -= (100 - frameRateStability) * 0.1

        return max(0, score)
    }

    private func updateSystemMetrics() {
