            frameRateStability = 100.0
            return
        }

        // Calculate stability based on variance
        let average = samples.reduce(0, +) / Double(samples.count)
        let variance = samples.reduce(0) { result, rate in
            result + pow(rate - average, 2)
        } / Double(samples.count)

