import Foundation
import AVFoundation

/// Actor-based marker store with pagination support for handling large datasets efficiently
actor MarkerStore {
    static let shared = MarkerStore()

    // Configuration
    private let pageSize = 100
    private let cacheSize = 10 // Number of pages to keep in memory

    // Storage
    private var pageCache: [String: [Int: MarkerPage]] = [:] // videoHash -> pageIndex -> page
    private var markerIndices: [String: MarkerIndex] = [:] // videoHash -> index
    private var accessOrder: [(String, Int)] = [] // LRU tracking for cache eviction

    // Temporary in-memory storage for demo/testing
    private var inMemoryMarkers: [String: [Marker]] = [:] // videoHash -> markers

    // Structures
    struct MarkerPage {
        let markers: [Marker]
        let pageIndex: Int
        let totalCount: Int
    }

    struct MarkerIndex {
        let totalCount: Int
        let sortedTimestamps: [CMTime]
        let markerIDs: [UUID]
        let lastModified: Date
    }

    // MARK: - Public Interface

    /// Load a specific page of markers
    func loadPage(for videoHash: String, pageIndex: Int) async throws -> MarkerPage {
        // Check cache first
        if let cachedPage = pageCache[videoHash]?[pageIndex] {
            updateAccessOrder(videoHash: videoHash, pageIndex: pageIndex)
            return cachedPage
        }

        // Load index if needed
        let index = try await loadOrBuildIndex(for: videoHash)

        // Calculate page bounds
        let startIdx = pageIndex * pageSize
        let endIdx = min(startIdx + pageSize, index.totalCount)

        guard startIdx < index.totalCount else {
            return MarkerPage(markers: [], pageIndex: pageIndex, totalCount: index.totalCount)
        }

        // Load markers for this page
        let pageMarkers = try await loadMarkersInRange(
            for: videoHash,
            startIndex: startIdx,
            endIndex: endIdx,
            index: index
        )

        let page = MarkerPage(
            markers: pageMarkers,
            pageIndex: pageIndex,
            totalCount: index.totalCount
        )

        // Cache the page
        cachePage(page, for: videoHash, pageIndex: pageIndex)

        return page
    }

    /// Get total marker count without loading all markers
    func getMarkerCount(for videoHash: String) async throws -> Int {
        let index = try await loadOrBuildIndex(for: videoHash)
        return index.totalCount
    }

    /// Find markers in a time range (optimized with binary search)
    func findMarkers(for videoHash: String, in timeRange: CMTimeRange) async throws -> [Marker] {
        let index = try await loadOrBuildIndex(for: videoHash)

        // Binary search for start and end positions
        let startPos = index.sortedTimestamps.firstIndex { $0 >= timeRange.start } ?? 0
        let endPos = index.sortedTimestamps.lastIndex { $0 <= timeRange.end } ?? (index.totalCount - 1)

        // Load only the pages containing these markers
        let startPage = startPos / pageSize
        let endPage = endPos / pageSize

        var markers: [Marker] = []
        for pageIdx in startPage...endPage {
            let page = try await loadPage(for: videoHash, pageIndex: pageIdx)
            let filtered = page.markers.filter { timeRange.containsTime($0.timestamp) }
            markers.append(contentsOf: filtered)
        }

        return markers
    }

    /// Add a new marker (updates index and invalidates relevant cache)
    func addMarker(_ marker: Marker, for videoHash: String) async throws {
        // Load current markers to find insertion point
        let index = try await loadOrBuildIndex(for: videoHash)

        // Find insertion position
        let insertPos = index.sortedTimestamps.firstIndex { $0 > marker.timestamp } ?? index.totalCount
        let pageIndex = insertPos / pageSize

        // Invalidate cached pages from this point forward
        invalidatePages(for: videoHash, fromPage: pageIndex)

        // Update in-memory storage
        var markers = inMemoryMarkers[videoHash] ?? []
        let insertIdx = markers.firstIndex { $0.timestamp > marker.timestamp } ?? markers.count
        markers.insert(marker, at: insertIdx)
        inMemoryMarkers[videoHash] = markers

        // Rebuild index
        _ = try await buildIndex(for: videoHash)
    }

    /// Update an existing marker
    func updateMarker(_ marker: Marker, for videoHash: String) async throws {
        // Find which page contains this marker
        let index = try await loadOrBuildIndex(for: videoHash)

        if let markerIdx = index.markerIDs.firstIndex(of: marker.id) {
            let pageIndex = markerIdx / pageSize
            invalidatePages(for: videoHash, fromPage: pageIndex, toPage: pageIndex)
        }

        // Update in-memory storage
        if var markers = inMemoryMarkers[videoHash],
           let idx = markers.firstIndex(where: { $0.id == marker.id }) {
            markers[idx] = marker
            markers.sort { $0.timestamp < $1.timestamp }
            inMemoryMarkers[videoHash] = markers
        }
    }

    /// Delete a marker
    func deleteMarker(id: UUID, for videoHash: String) async throws {
        let index = try await loadOrBuildIndex(for: videoHash)

        if let markerIdx = index.markerIDs.firstIndex(of: id) {
            let pageIndex = markerIdx / pageSize

            // Invalidate all pages from this point (due to index shift)
            invalidatePages(for: videoHash, fromPage: pageIndex)

            // Delete from in-memory storage
            if var markers = inMemoryMarkers[videoHash] {
                markers.removeAll { $0.id == id }
                inMemoryMarkers[videoHash] = markers
            }

            // Rebuild index
            _ = try await buildIndex(for: videoHash)
        }
    }

    /// Save all markers (batch operation)
    func saveAllMarkers(_ markers: [Marker], for videoHash: String) async throws {
        // Clear all caches for this video
        pageCache[videoHash] = nil
        markerIndices[videoHash] = nil

        // Store in memory
        inMemoryMarkers[videoHash] = markers

        // Build new index
        _ = try await buildIndex(for: videoHash)
    }

    // MARK: - Private Methods

    private func loadOrBuildIndex(for videoHash: String) async throws -> MarkerIndex {
        // Check if we have a current index
        if let index = markerIndices[videoHash] {
            // For now, always use cached index
            return index
        }

        // Build new index
        return try await buildIndex(for: videoHash)
    }

    private func buildIndex(for videoHash: String) async throws -> MarkerIndex {
        // Load from in-memory storage
        let allMarkers = inMemoryMarkers[videoHash] ?? []

        // Sort by timestamp
        let sorted = allMarkers.sorted { $0.timestamp < $1.timestamp }

        let index = MarkerIndex(
            totalCount: sorted.count,
            sortedTimestamps: sorted.map { $0.timestamp },
            markerIDs: sorted.map { $0.id },
            lastModified: Date()
        )

        markerIndices[videoHash] = index
        return index
    }

    private func loadMarkersInRange(
        for videoHash: String,
        startIndex: Int,
        endIndex: Int,
        index: MarkerIndex
    ) async throws -> [Marker] {
        // Get marker IDs for this range
        let markerIDs = Array(index.markerIDs[startIndex..<endIndex])

        // Load from in-memory storage
        let allMarkers = inMemoryMarkers[videoHash] ?? []
        return markerIDs.compactMap { id in
            allMarkers.first { $0.id == id }
        }
    }

    private func cachePage(_ page: MarkerPage, for videoHash: String, pageIndex: Int) {
        if pageCache[videoHash] == nil {
            pageCache[videoHash] = [:]
        }

        pageCache[videoHash]?[pageIndex] = page
        updateAccessOrder(videoHash: videoHash, pageIndex: pageIndex)

        // Evict old pages if cache is full
        evictIfNeeded()
    }

    private func updateAccessOrder(videoHash: String, pageIndex: Int) {
        accessOrder.removeAll { $0.0 == videoHash && $0.1 == pageIndex }
        accessOrder.append((videoHash, pageIndex))
    }

    private func evictIfNeeded() {
        let totalPages = pageCache.values.reduce(0) { $0 + $1.count }

        while totalPages > cacheSize && !accessOrder.isEmpty {
            let (videoHash, pageIndex) = accessOrder.removeFirst()
            pageCache[videoHash]?.removeValue(forKey: pageIndex)
        }
    }

    private func invalidatePages(for videoHash: String, fromPage: Int, toPage: Int? = nil) {
        guard var cache = pageCache[videoHash] else { return }

        if let toPage = toPage {
            for page in fromPage...toPage {
                cache.removeValue(forKey: page)
            }
        } else {
            // Invalidate all pages from this point forward
            cache = cache.filter { $0.key < fromPage }
        }

        pageCache[videoHash] = cache.isEmpty ? nil : cache
    }
}

