import Foundation
import AVFoundation
import Metal
import os.log

class VideoMemoryManager {
    static let shared = VideoMemoryManager()

    private let logger = Logger(subsystem: "com.swiftmedia", category: "VideoMemory")
    private var metalDevice: MTLDevice?

    private init() {
        metalDevice = MTLCreateSystemDefaultDevice()
        if metalDevice != nil {
            logger.info("Metal device initialized successfully")
        } else {
            logger.warning("Metal device not available - hardware acceleration disabled")
        }
    }

    /// Configure player for optimal hardware acceleration
    func configureForHardwareAcceleration(player: AVPlayer, playerLayer: AVPlayerLayer?) {
        guard let metalDevice = metalDevice else {
            logger.warning("Metal not available - skipping hardware acceleration")
            return
        }

        // Configure player layer for Metal
        if let playerLayer = playerLayer {
            playerLayer.pixelBufferAttributes = [
                kCVPixelBufferPixelFormatTypeKey as String: kCVPixelFormatType_420YpCbCr8BiPlanarVideoRange,
                kCVPixelBufferMetalCompatibilityKey as String: true,
                kCVPixelBufferIOSurfacePropertiesKey as String: [:]
            ]

            // Enable video composition with Metal
            playerLayer.videoGravity = .resizeAspect

            logger.info("Player layer configured for Metal acceleration")
        }

        // Configure decoder for GPU usage
        if let currentItem = player.currentItem {
            configureItemForHardwareDecoding(currentItem, device: metalDevice)
        }

        // Note: Buffer duration is now configured adaptively in VideoPlayerViewModel based on video resolution
        // Don't set a static buffer duration here to avoid overriding adaptive configuration

        // Configure automatic wait to minimize stalls
        player.automaticallyWaitsToMinimizeStalling = true

        logger.info("Hardware acceleration configured successfully")
    }

    /// Configure AVPlayerItem for hardware decoding
    private func configureItemForHardwareDecoding(_ item: AVPlayerItem, device: MTLDevice) {
        // Enable hardware decoding hints
        if #available(macOS 13.0, *) {
            item.preferredMaximumResolution = .zero // Let system decide
        }

        // Configure video composition if needed
        if item.videoComposition != nil {
            // Ensure video composition uses Metal if available
            logger.info("Video composition detected - optimizing for Metal")
        }

        // Set video decoder GPU registry ID for hardware decoding
        _ = device.registryID
        Task {
            do {
                let tracks = try await item.asset.loadTracks(withMediaType: .video)
                guard let videoTrack = tracks.first else { return }

                // Log codec information
                if let formatDescriptions = try? await videoTrack.load(.formatDescriptions) {
                    for desc in formatDescriptions {
                        let mediaSubType = CMFormatDescriptionGetMediaSubType(desc)
                        let codecType = fourCCToString(mediaSubType)
                        self.logger.info("Video codec: \(codecType)")

                        // Check if codec supports hardware acceleration
                        if self.isHardwareAcceleratedCodec(mediaSubType) {
                            self.logger.info("Hardware acceleration supported for codec: \(codecType)")
                        } else {
                            self.logger.warning("Hardware acceleration may not be available for codec: \(codecType)")
                        }
                    }
                }
            } catch {
                self.logger.error("Failed to load video tracks: \(error)")
            }
        }
    }

    /// Check if codec supports hardware acceleration
    private func isHardwareAcceleratedCodec(_ codecType: FourCharCode) -> Bool {
        let hardwareAcceleratedCodecs: Set<FourCharCode> = [
            kCMVideoCodecType_H264,        // H.264
            kCMVideoCodecType_HEVC,        // H.265/HEVC
            kCMVideoCodecType_AppleProRes422,
            kCMVideoCodecType_AppleProRes422HQ,
            kCMVideoCodecType_AppleProRes422LT,
            kCMVideoCodecType_AppleProRes422Proxy,
            kCMVideoCodecType_AppleProRes4444,
            kCMVideoCodecType_AppleProRes4444XQ,
            kCMVideoCodecType_AppleProResRAW,
            kCMVideoCodecType_AppleProResRAWHQ
        ]

        return hardwareAcceleratedCodecs.contains(codecType)
    }

    /// Optimize memory usage for large video files
    static func optimizeForLargeFiles() {
        // Configure AVAssetResourceLoader settings
        AVURLAsset.audiovisualMIMETypes()

        // Reduce buffer count for lower memory usage
        _ = 2.0 // preferredBufferDuration - keeping for potential future use

        // Clear any cached data structures
        // Note: macOS doesn't have NSImage caches like iOS UIImage

        // Clear URL caches
        URLCache.shared.removeAllCachedResponses()

        Logger(subsystem: "com.swiftmedia", category: "VideoMemory")
            .info("Memory optimized for large video files")
    }

    /// Handle memory pressure warnings
    static func handleMemoryWarning() {
        // Purge non-essential caches
        URLCache.shared.removeAllCachedResponses()

        // Clear any cached data structures
        // Note: macOS doesn't have NSImage caches like iOS UIImage

        // Force garbage collection if needed
        autoreleasepool {
            // Any temporary objects will be released here
        }

        // Notify performance monitor
        NotificationCenter.default.post(
            name: .performanceOptimizationNeeded,
            object: nil,
            userInfo: ["type": "memory", "level": "critical"]
        )

        Logger(subsystem: "com.swiftmedia", category: "VideoMemory")
            .warning("Memory warning handled - caches cleared")
    }

    /// Get current GPU usage (if available)
    func getCurrentGPUUsage() -> Double? {
        guard metalDevice != nil else { return nil }

        // Note: Direct GPU usage monitoring requires private APIs
        // This is a placeholder that could be expanded with proper GPU monitoring
        return nil
    }

    /// Create optimized pixel buffer attributes for video output
    static func optimizedPixelBufferAttributes() -> [String: Any] {
        return [
            kCVPixelBufferPixelFormatTypeKey as String: [
                kCVPixelFormatType_420YpCbCr8BiPlanarVideoRange,
                kCVPixelFormatType_420YpCbCr8BiPlanarFullRange,
                kCVPixelFormatType_422YpCbCr8,
                kCVPixelFormatType_32BGRA
            ],
            kCVPixelBufferMetalCompatibilityKey as String: true,
            kCVPixelBufferIOSurfacePropertiesKey as String: [:],
            kCVPixelBufferWidthKey as String: 3840,  // 4K max width
            kCVPixelBufferHeightKey as String: 2160  // 4K max height
        ]
    }
}

// Helper function to convert FourCharCode to String
private func fourCCToString(_ fourCC: FourCharCode) -> String {
    let bytes = [
        UInt8((fourCC >> 24) & 0xFF),
        UInt8((fourCC >> 16) & 0xFF),
        UInt8((fourCC >> 8) & 0xFF),
        UInt8(fourCC & 0xFF)
    ]

    if let string = String(bytes: bytes, encoding: .ascii) {
        return string
    } else {
        return String(format: "0x%08X", fourCC)
    }
}

