// MARK: - LRU Cache Implementation

/// Thread-safe LRU cache with memory limit support
class LRUCache<Key: Hashable, Value> {
    private class Node {
        var key: Key
        var value: Value
        var cost: Int
        var prev: Node?
        var next: Node?

        init(key: Key, value: Value, cost: Int) {
            self.key = key
            self.value = value
            self.cost = cost
        }
    }

    private let lock = NSLock()
    private var cache: [Key: Node] = [:]
    private var head: Node?
    private var tail: Node?
    private var totalCost = 0
    private let maxCost = 50 * 1024 * 1024 // 50MB default
    private let maxCount = 100 // Maximum number of entries

    func get(_ key: Key) -> Value? {
        lock.lock()
        defer { lock.unlock() }

        guard let node = cache[key] else { return nil }

        // Move to front (most recently used)
        moveToFront(node)
        return node.value
    }

    func set(_ key: Key, value: Value, cost: Int = 0) {
        lock.lock()
        defer { lock.unlock() }

        // Update existing node
        if let node = cache[key] {
            node.value = value
            let oldCost = node.cost
            node.cost = cost
            totalCost += (cost - oldCost)
            moveToFront(node)
        } else {
            // Create new node
            let node = Node(key: key, value: value, cost: cost)
            cache[key] = node
            totalCost += cost

            // Add to front
            if head == nil {
                head = node
                tail = node
            } else {
                node.next = head
                head?.prev = node
                head = node
            }
        }

        // Evict if necessary
        evictIfNeeded()
    }

    func remove(_ key: Key) {
        lock.lock()
        defer { lock.unlock() }

        guard let node = cache.removeValue(forKey: key) else { return }
        totalCost -= node.cost
        removeNode(node)
    }

    func stats() -> (pageCount: Int, memoryUsage: Int) {
        lock.lock()
        defer { lock.unlock() }

        return (cache.count, totalCost)
    }

    private func moveToFront(_ node: Node) {
        guard node !== head else { return }

        // Remove from current position
        removeNode(node)

        // Add to front
        node.next = head
        node.prev = nil
        head?.prev = node
        head = node
    }

    private func removeNode(_ node: Node) {
        if node === head {
            head = node.next
        }
        if node === tail {
            tail = node.prev
        }

        node.prev?.next = node.next
        node.next?.prev = node.prev
        node.prev = nil
        node.next = nil
    }

    private func evictIfNeeded() {
        // Evict based on memory limit
        while totalCost > maxCost && tail != nil {
            if let node = tail {
                cache.removeValue(forKey: node.key)
                totalCost -= node.cost
                removeNode(node)
            }
        }

        // Evict based on count limit
        while cache.count > maxCount && tail != nil {
            if let node = tail {
                cache.removeValue(forKey: node.key)
                totalCost -= node.cost
                removeNode(node)
            }
        }
    }
}

