func record(_ metrics: PerformanceMetrics) {
    metricsHistory.append(metrics)

    // Maintain history size
    if metricsHistory.count > maxHistorySize {
        metricsHistory.removeFirst()
    }

    // Downsample for long-term storage
    downsampleIfNeeded(metrics)

    // Update analytics
    updateAverages()
    analyzeTrends()

    // Apply adaptive optimizations if needed
    if adaptiveOptimizationEnabled {
        applyAdaptiveOptimizations(metrics)
    }
}

private func downsampleIfNeeded(_ metrics: PerformanceMetrics) {
    let now = Date()
    if now.timeIntervalSince(lastDownsampleTime) >= downsampleInterval {
        // Add the current metrics to downsampled history
        downsampledHistory.append(metrics)
        lastDownsampleTime = now

        // Maintain downsampled history size
        if downsampledHistory.count > maxDownsampledSize {
            // Remove oldest 10% when limit reached
            let removeCount = maxDownsampledSize / 10
            downsampledHistory.removeFirst(removeCount)
        }
    }
}

