    let timestamp: Date
    let sessionDuration: TimeInterval
    let averageMetrics: PerformanceMetrics?
    let peakCPU: Double
    let peakMemory: Double
    let totalDroppedFrames: Int
    let performanceScore: Double
    let recommendations: [String]
}

// Notification names
