import Foundation

enum ValidationError: LocalizedError {
    case multipleErrors([Error])
    case fieldRequired(fieldName: String)
    case valueOutOfRange(fieldName: String, min: Any?, max: Any?)
    case invalidFormat(fieldName: String, expectedFormat: String)
    case conflictingValues(field1: String, field2: String, reason: String)

    var errorDescription: String? {
        switch self {
        case .multipleErrors(let errors):
            let descriptions = errors.compactMap { $0.localizedDescription }
            return descriptions.joined(separator: "\n")
        case .fieldRequired(let fieldName):
            return "\(fieldName) is required"
        case .valueOutOfRange(let fieldName, let min, let max):
            if let min = min, let max = max {
                return "\(fieldName) must be between \(min) and \(max)"
            } else if let min = min {
                return "\(fieldName) must be at least \(min)"
            } else if let max = max {
                return "\(fieldName) must be at most \(max)"
            } else {
                return "\(fieldName) is out of range"
            }
        case .invalidFormat(let fieldName, let expectedFormat):
            return "\(fieldName) must be in format: \(expectedFormat)"
        case .conflictingValues(let field1, let field2, let reason):
            return "\(field1) conflicts with \(field2): \(reason)"
        }
    }

    var recoverySuggestion: String? {
        switch self {
        case .multipleErrors(let errors):
            let suggestions = errors.compactMap { ($0 as? LocalizedError)?.recoverySuggestion }
            return suggestions.isEmpty ? "Fix each error listed above" : suggestions.joined(separator: "\n")
        case .fieldRequired:
            return "Provide a value for this required field"
        case .valueOutOfRange(let fieldName, let min, let max):
            if let min = min, let max = max {
                return "Enter a value between \(min) and \(max)"
            } else if let min = min {
                return "Enter a value of at least \(min)"
            } else if let max = max {
                return "Enter a value of at most \(max)"
            } else {
                return "Enter a value within the allowed range"
            }
        case .invalidFormat(_, let expectedFormat):
            return "Use the format: \(expectedFormat)"
        case .conflictingValues:
            return "Change one of the conflicting values to resolve the issue"
        }
    }
}

