import Foundation
import os.log

struct ErrorEvent: Codable {
    let timestamp: Date
    let errorType: String
    let errorCode: String?
    let errorDescription: String
    let recoverySuggestion: String?
    let context: [String: String]
    let stackTrace: [String]?

    init(error: Error, context: [String: String] = [:]) {
        self.timestamp = Date()
        self.errorType = String(describing: type(of: error))
        self.errorDescription = error.localizedDescription

        // Extract recovery suggestion if available
        if let localizedError = error as? LocalizedError {
            self.recoverySuggestion = localizedError.recoverySuggestion
        } else {
            self.recoverySuggestion = nil
        }

        // Extract error code if available
        if let nsError = error as NSError? {
            self.errorCode = "\(nsError.domain):\(nsError.code)"
        } else {
            self.errorCode = nil
        }

        self.context = context
        self.stackTrace = Thread.callStackSymbols
    }
}

@MainActor
class ErrorAnalytics: ObservableObject {
    static let shared = ErrorAnalytics()

    @Published var recentErrors: [ErrorEvent] = []
    @Published var errorCounts: [String: Int] = [:]

    private let logger = Logger(subsystem: "com.swiftmedia", category: "ErrorAnalytics")
    private let maxRecentErrors = 100
    private let errorLogURL: URL

    init() {
        // Initialize error log file
        let documentsPath = FileManager.default.urls(for: .documentDirectory, in: .userDomainMask).first!
        self.errorLogURL = documentsPath.appendingPathComponent("SwiftMedia").appendingPathComponent("error_log.json")

        // Create directory if needed
        try? FileManager.default.createDirectory(at: errorLogURL.deletingLastPathComponent(), withIntermediateDirectories: true)

        // Load existing errors
        loadErrorLog()
    }

    func trackError(_ error: Error, context: [String: String] = [:]) {
        let event = ErrorEvent(error: error, context: context)

        // Log to system
        logger.error("Error tracked: \(event.errorType) - \(event.errorDescription)")

        // Update in-memory tracking
        recentErrors.append(event)
        if recentErrors.count > maxRecentErrors {
            recentErrors.removeFirst()
        }

        // Update error counts
        errorCounts[event.errorType, default: 0] += 1

        // Persist to disk
        saveErrorLog()

        // For critical errors, also log to console
        if error is PlayerError || error is MarkerPersistenceError {
            print("SwiftMedia Error: \(event.errorDescription)")
            if let suggestion = event.recoverySuggestion {
                print("Recovery: \(suggestion)")
            }
        }
    }

    func clearErrors() {
        recentErrors.removeAll()
        errorCounts.removeAll()
        try? FileManager.default.removeItem(at: errorLogURL)
    }

    func errorSummary() -> String {
        var summary = "Error Summary:\n"
        for (errorType, count) in errorCounts.sorted(by: { $0.value > $1.value }) {
            summary += "- \(errorType): \(count) occurrences\n"
        }
        return summary
    }

    private func loadErrorLog() {
        guard let data = try? Data(contentsOf: errorLogURL),
              let events = try? JSONDecoder().decode([ErrorEvent].self, from: data) else {
            return
        }

        recentErrors = Array(events.suffix(maxRecentErrors))

        // Rebuild error counts
        errorCounts.removeAll()
        for event in recentErrors {
            errorCounts[event.errorType, default: 0] += 1
        }
    }

    private func saveErrorLog() {
        Task {
            do {
                let encoder = JSONEncoder()
                encoder.outputFormatting = .prettyPrinted
                encoder.dateEncodingStrategy = .iso8601
                let data = try encoder.encode(recentErrors)
                try data.write(to: errorLogURL)
            } catch {
                logger.error("Failed to save error log: \(error.localizedDescription)")
            }
        }
    }
}

// Extension to make error tracking easier
extension Error {
    func track(context: [String: String] = [:]) {
        Task { @MainActor in
            ErrorAnalytics.shared.trackError(self, context: context)
        }
    }
}

