import Foundation

enum ScrubbingConfigError: LocalizedError {
    case emptyName
    case emptyBackwardKey
    case emptyForwardKey
    case sameKeysForDirection
    case invalidPlayingSeekTime(Double)
    case invalidPausedSeekFrames(Int)
    case keyConflict(config1: String, config2: String, key: String)
    case multipleErrors([ScrubbingConfigError])

    var errorDescription: String? {
        switch self {
        case .emptyName:
            return "Configuration name cannot be empty"
        case .emptyBackwardKey:
            return "Backward key cannot be empty"
        case .emptyForwardKey:
            return "Forward key cannot be empty"
        case .sameKeysForDirection:
            return "Forward and backward keys cannot be the same"
        case .invalidPlayingSeekTime(let time):
            return "Playing seek time must be positive (got \(time)s)"
        case .invalidPausedSeekFrames(let frames):
            return "Paused seek frames must be positive (got \(frames) frames)"
        case .keyConflict(let config1, let config2, let key):
            return "Key '\(key)' is used by both '\(config1)' and '\(config2)'"
        case .multipleErrors(let errors):
            let descriptions = errors.compactMap { $0.errorDescription }
            return descriptions.joined(separator: "\n")
        }
    }

    var recoverySuggestion: String? {
        switch self {
        case .emptyName:
            return "Enter a descriptive name for this scrubbing configuration"
        case .emptyBackwardKey, .emptyForwardKey:
            return "Press a key to assign it to this direction"
        case .sameKeysForDirection:
            return "Choose different keys for forward and backward scrubbing"
        case .invalidPlayingSeekTime:
            return "Enter a positive number of seconds (e.g., 0.5 for half a second)"
        case .invalidPausedSeekFrames:
            return "Enter a positive number of frames (e.g., 1 for single frame)"
        case .keyConflict:
            return "Choose a different key or modify the conflicting configuration"
        case .multipleErrors(let errors):
            let suggestions = errors.compactMap { $0.recoverySuggestion }
            return suggestions.isEmpty ? nil : suggestions.joined(separator: "\n")
        }
    }
}

struct ScrubbingConfigValidator {
    static func validate(_ config: ScrubbingConfig) throws {
        var errors: [ScrubbingConfigError] = []

        if config.name.trimmingCharacters(in: .whitespacesAndNewlines).isEmpty {
            errors.append(.emptyName)
        }

        if config.backwardKey == nil || config.backwardKey == "" {
            errors.append(.emptyBackwardKey)
        }

        if config.forwardKey == nil || config.forwardKey == "" {
            errors.append(.emptyForwardKey)
        }

        if let backward = config.backwardKey,
           let forward = config.forwardKey,
           backward == forward && !backward.isEmpty {
            errors.append(.sameKeysForDirection)
        }

        if config.playingSeekTime <= 0 {
            errors.append(.invalidPlayingSeekTime(config.playingSeekTime))
        }

        if config.pausedSeekFrames <= 0 {
            errors.append(.invalidPausedSeekFrames(config.pausedSeekFrames))
        }

        if !errors.isEmpty {
            if errors.count == 1 {
                throw errors[0]
            } else {
                throw ScrubbingConfigError.multipleErrors(errors)
            }
        }
    }

    static func validateAll(_ configs: [ScrubbingConfig]) throws {
        var allErrors: [ScrubbingConfigError] = []

        // Validate each config individually
        for config in configs {
            do {
                try validate(config)
            } catch let error as ScrubbingConfigError {
                allErrors.append(error)
            }
        }

        // Check for key conflicts between configs
        var keyToConfigs: [String: [(config: ScrubbingConfig, direction: String)]] = [:]

        for config in configs {
            if let backward = config.backwardKey, !backward.isEmpty {
                keyToConfigs[backward, default: []].append((config, "backward"))
            }
            if let forward = config.forwardKey, !forward.isEmpty {
                keyToConfigs[forward, default: []].append((config, "forward"))
            }
        }

        for (key, assignments) in keyToConfigs where assignments.count > 1 {
            for i in 0..<assignments.count {
                for j in (i+1)..<assignments.count {
                    allErrors.append(.keyConflict(
                        config1: assignments[i].config.name,
                        config2: assignments[j].config.name,
                        key: key
                    ))
                }
            }
        }

        if !allErrors.isEmpty {
            if allErrors.count == 1 {
                throw allErrors[0]
            } else {
                throw ScrubbingConfigError.multipleErrors(allErrors)
            }
        }
    }
}

