//
//  ScrubbingTabView.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import SwiftUI

struct ScrubbingTabView: View {
    @ObservedObject var viewModel: SettingsViewModel
    @State private var selectedConfigIndex: Int = 0
    @State private var showingConflictAlert = false
    @State private var conflictMessage = ""

    var body: some View {
        VStack(alignment: .leading, spacing: 20) {
            HStack {
                Text("Scrubbing Configurations")
                    .font(.headline)
                Spacer()

                // Action buttons
                HStack(spacing: 12) {
                    Button("Cancel") {
                        viewModel.cancelChanges()
                    }
                    .buttonStyle(.borderless)
                    .disabled(!viewModel.hasUnsavedChanges)

                    Button("Reset to Defaults") {
                        viewModel.resetScrubbingToDefaults()
                    }
                    .buttonStyle(.borderless)

                    Button(action: addNewConfiguration) {
                        Label("Add Configuration", systemImage: "plus")
                    }
                    .buttonStyle(.borderless)

                    Button("Save") {
                        if viewModel.hasShortcutConflicts().isEmpty {
                            viewModel.saveSettings()
                        } else {
                            showConflictAlert()
                        }
                    }
                    .buttonStyle(.borderedProminent)
                    .disabled(!viewModel.hasUnsavedChanges)
                }
            }
            .padding(.top)

            // Configuration Table
            VStack(spacing: 0) {
                // Header
                HStack(spacing: 0) {
                    Text("Name")
                        .frame(width: 120, alignment: .leading)
                        .padding(.horizontal, 12)

                    Text("Backward")
                        .frame(width: 100, alignment: .center)

                    Text("Forward")
                        .frame(width: 100, alignment: .center)

                    Text("Playing (sec)")
                        .frame(width: 100, alignment: .center)

                    Text("Paused (frames)")
                        .frame(width: 110, alignment: .center)

                    Spacer()
                        .frame(width: 40)
                }
                .font(.caption)
                .fontWeight(.semibold)
                .foregroundColor(.secondary)
                .padding(.vertical, 8)
                .background(Color(NSColor.separatorColor).opacity(0.3))

                Divider()

                // Rows
                ScrollView {
                    VStack(spacing: 0) {
                        ForEach(viewModel.tempScrubbingConfigs.indices, id: \.self) { index in
                            ScrubbingConfigRow(
                                config: $viewModel.tempScrubbingConfigs[index],
                                onDelete: {
                                    deleteConfiguration(at: index)
                                },
                                onChanged: { viewModel.markAsChanged() }
                            )

                            if index < viewModel.tempScrubbingConfigs.count - 1 {
                                Divider()
                            }
                        }
                    }
                }
                .frame(maxHeight: 200)
            }
            .background(Color(NSColor.controlBackgroundColor))
            .cornerRadius(8)

            if viewModel.hasUnsavedChanges {
                Text("You have unsaved changes. Click Save to apply them.")
                    .font(.caption)
                    .foregroundColor(.orange)
            } else {
                Text("Configure different scrubbing behaviors for various editing workflows. " +
                     "The active configuration will be used for arrow key navigation.")
                    .font(.caption)
                    .foregroundColor(.secondary)
            }

            Spacer()
        }
        .padding()
        .onChange(of: viewModel.tempScrubbingConfigs) { _, _ in
            checkForConflicts()
        }
        .alert("Keyboard Shortcut Conflict", isPresented: $showingConflictAlert) {
            Button("OK") { }
        } message: {
            Text(conflictMessage)
        }
    }

    private func checkForConflicts() {
        // Check internal scrubbing conflicts
        let internalConflicts = ScrubbingConfigValidator.hasConflicts(viewModel.tempScrubbingConfigs)
        let internalMessages = internalConflicts.map { "\($0.0) and \($0.1) have conflicting shortcuts" }

        // Check global shortcut conflicts
        let globalConflicts = viewModel.hasShortcutConflicts()

        // Combine all conflicts
        let allConflicts = internalMessages + globalConflicts

        if !allConflicts.isEmpty {
            conflictMessage = allConflicts.joined(separator: "\n")
            showingConflictAlert = true
        }
    }

    private func showConflictAlert() {
        checkForConflicts()
    }

    private func addNewConfiguration() {
        let newConfig = ScrubbingConfig(
            name: "Custom \(viewModel.tempScrubbingConfigs.count + 1)",
            backwardKey: "â†",
            forwardKey: "â†’",
            playingSeekSeconds: 5.0,
            pausedSeekFrames: 1
        )
        viewModel.tempScrubbingConfigs.append(newConfig)
        viewModel.markAsChanged()
    }

    private func deleteConfiguration(at index: Int) {
        guard viewModel.tempScrubbingConfigs.count > 1 else { return }

        viewModel.tempScrubbingConfigs.remove(at: index)
        viewModel.markAsChanged()
    }
}

struct ScrubbingConfigRow: View {
    @Binding var config: ScrubbingConfig
    let onDelete: () -> Void
    let onChanged: () -> Void
    @State private var showingValidationError = false
    @State private var validationErrors: [String] = []

    var body: some View {
        HStack(spacing: 0) {
            TextField("Name", text: $config.name)
                .textFieldStyle(.plain)
                .frame(width: 120)
                .padding(.horizontal, 12)
                .onChange(of: config.name) { _, _ in onChanged() }

            KeyboardShortcutRecorder(shortcut: $config.backwardKey)
                .frame(width: 100)
                .onChange(of: config.backwardKey) { _, _ in onChanged() }

            KeyboardShortcutRecorder(shortcut: $config.forwardKey)
                .frame(width: 100)
                .onChange(of: config.forwardKey) { _, _ in onChanged() }

            HStack {
                TextField("", value: $config.playingSeekSeconds, format: .number.precision(.fractionLength(1)))
                    .textFieldStyle(.roundedBorder)
                    .frame(width: 60)
                    .multilineTextAlignment(.center)
                    .onSubmit {
                        validateConfig()
                    }
                    .onChange(of: config.playingSeekSeconds) { _, _ in onChanged() }
            }
            .frame(width: 100)

            HStack {
                TextField("", value: $config.pausedSeekFrames, format: .number)
                    .textFieldStyle(.roundedBorder)
                    .frame(width: 60)
                    .multilineTextAlignment(.center)
                    .onSubmit {
                        validateConfig()
                    }
                    .onChange(of: config.pausedSeekFrames) { _, _ in onChanged() }
            }
            .frame(width: 110)

            Button(action: onDelete) {
                Image(systemName: "trash")
                    .foregroundColor(.secondary)
            }
            .buttonStyle(.plain)
            .frame(width: 40)
        }
        .padding(.vertical, 8)
        .onChange(of: config) { _, _ in
            validateConfig()
        }
        .alert("Configuration Error", isPresented: $showingValidationError) {
            Button("OK") { }
        } message: {
            Text(validationErrors.joined(separator: "\n"))
        }
    }

    private func validateConfig() {
        validationErrors = ScrubbingConfigValidator.validate(config)
        showingValidationError = !validationErrors.isEmpty
    }
}

