//
//  ScrubbingConfig.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import Foundation
import SwiftUI

struct ScrubbingConfig: Codable, Identifiable, Equatable {
    let id: UUID
    var name: String
    private var _backwardKey: String
    private var _forwardKey: String
    var playingSeekSeconds: Double
    var pausedSeekFrames: Int

    // Expose keys as optionals for validation
    var backwardKey: String? { 
        _backwardKey.isEmpty ? nil : _backwardKey 
    }

    var forwardKey: String? { 
        _forwardKey.isEmpty ? nil : _forwardKey 
    }

    // For compatibility and setter access
    var backwardKeyString: String {
        get { _backwardKey }
        set { _backwardKey = newValue }
    }

    var forwardKeyString: String {
        get { _forwardKey }
        set { _forwardKey = newValue }
    }

    // Alias for validator
    var playingSeekTime: Double { playingSeekSeconds }

    init(id: UUID = UUID(),
         name: String,
         backwardKey: String,
         forwardKey: String,
         playingSeekSeconds: Double,
         pausedSeekFrames: Int) {
        self.id = id
        self.name = name
        self._backwardKey = backwardKey
        self._forwardKey = forwardKey
        self.playingSeekSeconds = playingSeekSeconds
        self.pausedSeekFrames = pausedSeekFrames
    }

    // Codable conformance
    enum CodingKeys: String, CodingKey {
        case id, name
        case _backwardKey = "backwardKey"
        case _forwardKey = "forwardKey"
        case playingSeekSeconds, pausedSeekFrames
    }

}

struct ScrubbingSettings: Codable {
    var configs: [ScrubbingConfig]

    init(configs: [ScrubbingConfig] = DefaultShortcuts.scrubbingConfigs) {
        self.configs = configs
    }

    // Find config that matches the pressed key
    func configForKey(_ key: String) -> ScrubbingConfig? {
        configs.first { $0._forwardKey == key || $0._backwardKey == key }
    }

    mutating func addConfig(_ config: ScrubbingConfig) {
        guard configs.count < 10 else { return }
        configs.append(config)
    }

    mutating func removeConfig(id: UUID) {
        guard configs.count > 1 else { return }
        configs.removeAll { $0.id == id }
    }

    mutating func updateConfig(_ config: ScrubbingConfig) {
        if let index = configs.firstIndex(where: { $0.id == config.id }) {
            configs[index] = config
        }
    }
}

enum SeekDirection {
    case forward
    case backward
}

