import SwiftUI

struct ErrorBanner: View {
    let error: PlayerError
    let onDismiss: () -> Void

    @State private var isShowing = true

    var body: some View {
        if isShowing {
            HStack(spacing: DesignSystem.Spacing.sm) {
                Image(systemName: iconName)
                    .font(.system(size: 16))
                    .foregroundColor(iconColor)

                VStack(alignment: .leading, spacing: DesignSystem.Spacing.xxs) {
                    Text(error.errorDescription ?? "An error occurred")
                        .font(DesignSystem.Typography.headline)
                        .foregroundColor(DesignSystem.Colors.textPrimary)

                    if let recovery = error.recoverySuggestion {
                        Text(recovery)
                            .font(DesignSystem.Typography.caption)
                            .foregroundColor(DesignSystem.Colors.textSecondary)
                    }
                }

                Spacer()

                Button(action: dismiss) {
                    Image(systemName: "xmark.circle.fill")
                        .font(.system(size: 20))
                        .foregroundColor(DesignSystem.Colors.textTertiary)
                }
                .buttonStyle(PlainButtonStyle())
                .onHover { hovering in
                    NSCursor.pointingHand.set()
                }
            }
            .padding(DesignSystem.Spacing.md)
            .background(backgroundColor)
            .cornerRadius(DesignSystem.CornerRadius.medium)
            .shadow(radius: 4)
            .transition(.asymmetric(
                insertion: .move(edge: .top).combined(with: .opacity),
                removal: .move(edge: .top).combined(with: .opacity)
            ))
            .animation(DesignSystem.Animation.spring, value: isShowing)
            .onAppear {
                // Auto-dismiss after 5 seconds for non-critical errors
                if !error.isCritical {
                    DispatchQueue.main.asyncAfter(deadline: .now() + 5) {
                        dismiss()
                    }
                }
            }
        }
    }

    private var iconName: String {
        switch error {
        case .fileNotFound:
            return "doc.badge.xmark"
        case .unsupportedFormat:
            return "exclamationmark.triangle"
        case .decodingFailed:
            return "exclamationmark.octagon"
        case .markerSaveFailed:
            return "bookmark.slash"
        }
    }

    private var iconColor: Color {
        error.isCritical ? DesignSystem.Colors.error : DesignSystem.Colors.warning
    }

    private var backgroundColor: Color {
        if error.isCritical {
            return DesignSystem.Colors.error.opacity(0.15)
        } else {
            return DesignSystem.Colors.warning.opacity(0.15)
        }
    }

    private func dismiss() {
        withAnimation(DesignSystem.Animation.fast) {
            isShowing = false
        }

        DispatchQueue.main.asyncAfter(deadline: .now() + 0.3) {
            onDismiss()
        }
    }
}

// MARK: - Error Extensions
extension PlayerError {
    var isCritical: Bool {
        switch self {
        case .fileNotFound, .decodingFailed:
            return true
        case .unsupportedFormat, .markerSaveFailed:
            return false
        }
    }

    var recoverySuggestion: String? {
        switch self {
        case .fileNotFound:
            return "Check if the file has been moved or deleted"
        case .unsupportedFormat:
            return "Try converting the video to H.264 or H.265 format"
        case .decodingFailed:
            return "The video file may be corrupted"
        case .markerSaveFailed:
            return "Check permissions and try again"
        }
    }
}

// MARK: - Generic Error Toast
struct ErrorToast: View {
    let message: String
    let type: ErrorType
    let onDismiss: () -> Void

    enum ErrorType {
        case error
        case warning
        case info
        case success

        var color: Color {
            switch self {
            case .error: return DesignSystem.Colors.error
            case .warning: return DesignSystem.Colors.warning
            case .info: return DesignSystem.Colors.info
            case .success: return DesignSystem.Colors.success
            }
        }

        var icon: String {
            switch self {
            case .error: return "xmark.circle.fill"
            case .warning: return "exclamationmark.triangle.fill"
            case .info: return "info.circle.fill"
            case .success: return "checkmark.circle.fill"
            }
        }
    }

    @State private var isShowing = true

    var body: some View {
        if isShowing {
            HStack(spacing: DesignSystem.Spacing.sm) {
                Image(systemName: type.icon)
                    .foregroundColor(type.color)

                Text(message)
                    .font(DesignSystem.Typography.body)
                    .foregroundColor(DesignSystem.Colors.textPrimary)

                Spacer()

                Button(action: {
                    withAnimation(DesignSystem.Animation.fast) {
                        isShowing = false
                    }
                    onDismiss()
                }) {
                    Image(systemName: "xmark")
                        .font(.caption)
                        .foregroundColor(DesignSystem.Colors.textTertiary)
                }
                .buttonStyle(PlainButtonStyle())
            }
            .padding(.horizontal, DesignSystem.Spacing.md)
            .padding(.vertical, DesignSystem.Spacing.sm)
            .background(type.color.opacity(0.15))
            .overlay(
                RoundedRectangle(cornerRadius: DesignSystem.CornerRadius.small)
                    .stroke(type.color.opacity(0.3), lineWidth: 1)
            )
            .cornerRadius(DesignSystem.CornerRadius.small)
            .transition(.asymmetric(
                insertion: .move(edge: .top).combined(with: .opacity),
                removal: .move(edge: .top).combined(with: .opacity)
            ))
            .animation(DesignSystem.Animation.spring, value: isShowing)
        }
    }
}

