//  SwiftMedia
//
//  Created by Ojash Neopane on 6/25/25.
//

import SwiftUI

/// Defines the different types of keyboard shortcuts with their specific parameters
enum ShortcutType: Equatable {
    /// Simple shortcut with just a key and optional modifiers
    case simple(key: String, modifiers: EventModifiers = [])

    /// Scrubbing shortcut that requires forward/backward keys and seek amounts
    case scrubbing(config: ScrubbingConfig)

    /// Rating shortcut for 1-5 star ratings
    case rating(number: Int)

    /// Volume control shortcuts
    case volume(direction: VolumeDirection)

    /// Navigation shortcuts for directory traversal
    case navigation(direction: NavigationDirection)

    /// Custom shortcut with additional parameters
    case custom(key: String, modifiers: EventModifiers, parameters: [String: String])

    /// Get the primary key for this shortcut type
    var primaryKey: String? {
        switch self {
        case .simple(let key, _):
            return key
        case .scrubbing(let config):
            return nil // Scrubbing has two keys
        case .rating(let number):
            return String(number)
        case .volume(let direction):
            return direction == .up ? DefaultShortcuts.volumeUpKey : DefaultShortcuts.volumeDownKey
        case .navigation(let direction):
            return nil // Will be customized
        case .custom(let key, _, _):
            return key
        }
    }

    /// Get the modifiers for this shortcut type
    var modifiers: EventModifiers {
        switch self {
        case .simple(_, let modifiers):
            return modifiers
        case .scrubbing:
            return []
        case .rating:
            return []
        case .volume:
            return []
        case .navigation:
            return []
        case .custom(_, let modifiers, _):
            return modifiers
        }
    }
}

/// Direction for volume control
enum VolumeDirection: String, CaseIterable {
    case up
    case down
}

/// Direction for navigation
enum NavigationDirection: String, CaseIterable {
    case next
    case previous
}

/// Category for organizing shortcuts in settings
enum ShortcutCategory: String, CaseIterable {
    case playback = "Playback"
    case markers = "Markers"
    case navigation = "Navigation"
    case scrubbing = "Scrubbing"
    case application = "Application"
    case rating = "Rating"
    case volume = "Volume"

    var systemImageName: String {
        switch self {
        case .playback:
            return "play.circle"
        case .markers:
            return "bookmark"
        case .navigation:
            return "arrow.left.arrow.right"
        case .scrubbing:
            return "forward.backward"
        case .application:
            return "app"
        case .rating:
            return "star"
        case .volume:
            return "speaker.wave.2"
        }
    }
}

//
//  KeyboardHandlingView.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import SwiftUI

struct KeyboardHandlingView: View {
    @ObservedObject var viewModel: VideoPlayerViewModel
    @ObservedObject var settingsViewModel: SettingsViewModel
    @Binding var showingSettings: Bool
    @Binding var selectedMarkerForEdit: Marker?
    @Binding var showingFilePicker: Bool
    let onNavigateNext: () -> Void
    let onNavigatePrevious: () -> Void
    @FocusState private var isFocused: Bool

    // Shortcut registry for managing all shortcuts
    @StateObject private var shortcutRegistry: ShortcutRegistry

    init(
        viewModel: VideoPlayerViewModel,
        settingsViewModel: SettingsViewModel,
        showingSettings: Binding<Bool>,
        selectedMarkerForEdit: Binding<Marker?>,
        showingFilePicker: Binding<Bool>,
        onNavigateNext: @escaping () -> Void,
        onNavigatePrevious: @escaping () -> Void
    ) {
        self.viewModel = viewModel
        self.settingsViewModel = settingsViewModel
        self._showingSettings = showingSettings
        self._selectedMarkerForEdit = selectedMarkerForEdit
        self._showingFilePicker = showingFilePicker
        self.onNavigateNext = onNavigateNext
        self.onNavigatePrevious = onNavigatePrevious
        self._shortcutRegistry = StateObject(wrappedValue: ShortcutRegistry(settingsViewModel: settingsViewModel))
    }

    // Build keyboard shortcuts from registry
    private var keyboardShortcuts: [String: KeyboardShortcut] {
        shortcutRegistry.buildKeyboardShortcuts(
            viewModel: viewModel,
            showingSettings: $showingSettings,
            showingFilePicker: $showingFilePicker,
            selectedMarkerForEdit: $selectedMarkerForEdit,
            onNavigateNext: onNavigateNext,
            onNavigatePrevious: onNavigatePrevious
        )
    }

    var body: some View {
        Color.clear
            .focusable()
            .focused($isFocused)
            .onAppear {
                isFocused = true
            }
            .onChange(of: settingsViewModel.scrubbingConfigs) { newConfigs in
                // Update registry when scrubbing configs change
                shortcutRegistry.updateScrubbingConfigs(newConfigs)
            }
            // Unified keyboard shortcut handling
            .onKeyPress { keyPress in
                let timestamp = Date().timeIntervalSince1970
                let identifier = KeyboardShortcut.identifier(from: keyPress)

                // Log all key presses
                print("[\(String(format: "%.3f", timestamp))] Key pressed: '\(keyPress.characters)' modifiers: \(keyPress.modifiers) identifier: '\(identifier)'")

                // Look up shortcut in registry
                if let shortcut = keyboardShortcuts[identifier] {
                    print("[\(String(format: "%.3f", timestamp))] Shortcut triggered: \(shortcut.description)")
                    shortcut.action()
                    return .handled
                }

                print("[\(String(format: "%.3f", timestamp))] Key '\(keyPress.characters)' ignored - no matching shortcut")
                return .ignored
            }
    }
}
