//
//  FFmpegManager.swift
//  SwiftMedia
//
//  Created on 1/26/25.
//

import Foundation
import Combine
import AVFoundation

/// Manages FFmpeg operations for video segment extraction
class FFmpegManager: ObservableObject {

    // MARK: - Types

    struct FFmpegError: LocalizedError {
        let message: String
        let underlyingError: Error?

        var errorDescription: String? { message }

        static let notInstalled = FFmpegError(
            message: "FFmpeg is not installed. Please install it using: brew install ffmpeg",
            underlyingError: nil
        )

        static let extractionFailed = FFmpegError(
            message: "Failed to extract video segment",
            underlyingError: nil
        )
    }

    struct ExtractionProgress {
        let percentage: Double
        let currentTime: Double
        let totalTime: Double
        let estimatedTimeRemaining: TimeInterval?
    }

    // MARK: - Properties

    private let processQueue = DispatchQueue(label: "com.swiftmedia.ffmpeg", qos: .userInitiated)
    private var activeProcess: Process?
    private var progressSubject = PassthroughSubject<ExtractionProgress, Never>()

    var progressPublisher: AnyPublisher<ExtractionProgress, Never> {
        progressSubject.eraseToAnyPublisher()
    }

    // MARK: - Initialization

    init() {}

    // MARK: - Public Methods

    /// Check if FFmpeg is installed and available
    func checkFFmpegInstallation() -> Result<String, FFmpegError> {
        let process = Process()
        process.executableURL = URL(fileURLWithPath: "/usr/bin/env")
        process.arguments = ["which", "ffmpeg"]

        let pipe = Pipe()
        process.standardOutput = pipe

        do {
            try process.run()
            process.waitUntilExit()

            guard process.terminationStatus == 0 else {
                return .failure(.notInstalled)
            }

            let data = pipe.fileHandleForReading.readDataToEndOfFile()
            let path = String(data: data, encoding: .utf8)?.trimmingCharacters(in: .whitespacesAndNewlines) ?? ""

            return path.isEmpty ? .failure(.notInstalled) : .success(path)
        } catch {
            return .failure(FFmpegError(message: "Failed to check FFmpeg installation", underlyingError: error))
        }
    }

    /// Extract a video segment using stream copy (fast, no re-encoding)
    /// - Parameters:
    ///   - inputPath: Path to the source video file
    ///   - outputPath: Path for the extracted segment
    ///   - startTime: Start time in seconds
    ///   - duration: Duration in seconds
    /// - Returns: Success or error
    @MainActor
    func extractSegment(
        from inputPath: String,
        to outputPath: String,
        startTime: Double,
        duration: Double
    ) async -> Result<Void, FFmpegError> {

        // First check if FFmpeg is installed
        switch checkFFmpegInstallation() {
        case .failure(let error):
            return .failure(error)
        case .success:
            break
        }

        return await withCheckedContinuation { continuation in
            processQueue.async { [weak self] in
                self?.performExtraction(
                    inputPath: inputPath,
                    outputPath: outputPath,
                    startTime: startTime,
                    duration: duration,
                    completion: { result in
                        continuation.resume(returning: result)
                    }
                )
            }
        }
    }

    /// Cancel the current extraction process
    func cancelExtraction() {
        activeProcess?.terminate()
        activeProcess = nil
    }

    // MARK: - Private Methods

    private func performExtraction(
        inputPath: String,
        outputPath: String,
        startTime: Double,
        duration: Double,
        completion: @escaping (Result<Void, FFmpegError>) -> Void
    ) {
        let process = Process()
        process.executableURL = URL(fileURLWithPath: "/usr/bin/env")

        // FFmpeg arguments for stream copy extraction
        // -ss: seek to start time (before input for fast seeking)
        // -i: input file
        // -t: duration
        // -c copy: copy all streams without re-encoding
        // -avoid_negative_ts make_zero: fix timestamp issues
        process.arguments = [
            "ffmpeg",
            "-ss", formatTime(startTime),
            "-i", inputPath,
            "-t", formatTime(duration),
            "-c", "copy",
            "-avoid_negative_ts", "make_zero",
            "-y", // Overwrite output file if exists
            outputPath
        ]

        // Capture stderr for progress parsing
        let errorPipe = Pipe()
        process.standardError = errorPipe

        activeProcess = process

        // Read progress from stderr
        errorPipe.fileHandleForReading.readabilityHandler = { [weak self] handle in
            let data = handle.availableData
            guard !data.isEmpty else { return }

            if let output = String(data: data, encoding: .utf8) {
                self?.parseProgress(output, totalDuration: duration)
            }
        }

        do {
            try process.run()
            process.waitUntilExit()

            errorPipe.fileHandleForReading.readabilityHandler = nil

            if process.terminationStatus == 0 {
                completion(.success(()))
            } else {
                completion(.failure(.extractionFailed))
            }
        } catch {
            completion(.failure(FFmpegError(
                message: "Failed to start FFmpeg process",
                underlyingError: error
            )))
        }

        activeProcess = nil
    }

    /// Format time in seconds to HH:MM:SS.mmm format for FFmpeg
    private func formatTime(_ seconds: Double) -> String {
        let hours = Int(seconds) / 3600
        let minutes = (Int(seconds) % 3600) / 60
        let secs = seconds.truncatingRemainder(dividingBy: 60)

        return String(format: "%02d:%02d:%06.3f", hours, minutes, secs)
    }

    /// Parse FFmpeg progress output
    private func parseProgress(_ output: String, totalDuration: Double) {
        // FFmpeg outputs progress like: "time=00:00:10.50"
        if let timeMatch = output.range(of: #"time=(\d{2}):(\d{2}):(\d{2}\.\d+)"#, options: .regularExpression) {
            let timeString = String(output[timeMatch])
            let components = timeString.replacingOccurrences(of: "time=", with: "").split(separator: ":")

            if components.count == 3,
               let hours = Double(components[0]),
               let minutes = Double(components[1]),
               let seconds = Double(components[2]) {

                let currentTime = hours * 3600 + minutes * 60 + seconds
                let percentage = min(currentTime / totalDuration, 1.0) * 100

                let remainingTime = totalDuration - currentTime
                let estimatedTimeRemaining = remainingTime > 0 ? remainingTime : nil

                let progress = ExtractionProgress(
                    percentage: percentage,
                    currentTime: currentTime,
                    totalTime: totalDuration,
                    estimatedTimeRemaining: estimatedTimeRemaining
                )

                DispatchQueue.main.async { [weak self] in
                    self?.progressSubject.send(progress)
                }
            }
        }
    }

    /// Get video metadata using ffprobe
    func getVideoMetadata(for path: String) async -> Result<VideoMetadata, FFmpegError> {
        await withCheckedContinuation { continuation in
            processQueue.async { [weak self] in
                self?.performMetadataExtraction(path: path) { result in
                    continuation.resume(returning: result)
                }
            }
        }
    }

    private func performMetadataExtraction(
        path: String,
        completion: @escaping (Result<VideoMetadata, FFmpegError>) -> Void
    ) {
        let process = Process()
        process.executableURL = URL(fileURLWithPath: "/usr/bin/env")

        // Use ffprobe to get video metadata in JSON format
        process.arguments = [
            "ffprobe",
            "-v", "quiet",
            "-print_format", "json",
            "-show_format",
            "-show_streams",
            path
        ]

        let pipe = Pipe()
        process.standardOutput = pipe

        do {
            try process.run()
            process.waitUntilExit()

            guard process.terminationStatus == 0 else {
                completion(.failure(FFmpegError(
                    message: "Failed to read video metadata",
                    underlyingError: nil
                )))
                return
            }

            let data = pipe.fileHandleForReading.readDataToEndOfFile()

            // Parse JSON response
            if let json = try? JSONSerialization.jsonObject(with: data) as? [String: Any],
               let format = json["format"] as? [String: Any],
               let duration = Double(format["duration"] as? String ?? "0"),
               let streams = json["streams"] as? [[String: Any]] {

                // Find video stream
                let videoStream = streams.first { $0["codec_type"] as? String == "video" }

                let metadata = VideoMetadata(
                    duration: duration,
                    width: videoStream?["width"] as? Int ?? 0,
                    height: videoStream?["height"] as? Int ?? 0,
                    fps: parseFrameRate(videoStream?["r_frame_rate"] as? String),
                    codec: videoStream?["codec_name"] as? String ?? "unknown"
                )

                completion(.success(metadata))
            } else {
                completion(.failure(FFmpegError(
                    message: "Failed to parse video metadata",
                    underlyingError: nil
                )))
            }
        } catch {
            completion(.failure(FFmpegError(
                message: "Failed to extract video metadata",
                underlyingError: error
            )))
        }
    }

    private func parseFrameRate(_ rateString: String?) -> Double {
        guard let rateString = rateString else { return 30.0 }

        let components = rateString.split(separator: "/")
        if components.count == 2,
           let numerator = Double(components[0]),
           let denominator = Double(components[1]),
           denominator > 0 {
            return numerator / denominator
        }

        return Double(rateString) ?? 30.0
    }
}

// MARK: - Supporting Types

struct VideoMetadata {
    let duration: Double
    let width: Int
    let height: Int
    let fps: Double
    let codec: String
}

