//
//  CMTime+Segment.swift
//  SwiftMedia
//
//  Created on 1/26/25.
//

import Foundation
import AVFoundation

extension CMTime {

    // MARK: - FFmpeg Time Format

    /// Convert CMTime to FFmpeg time format (HH:MM:SS.mmm)
    /// - Returns: Time string formatted for FFmpeg
    func toFFmpegFormat() -> String {
        let seconds = CMTimeGetSeconds(self)
        guard seconds.isFinite && seconds >= 0 else {
            return "00:00:00.000"
        }

        let hours = Int(seconds) / 3600
        let minutes = (Int(seconds) % 3600) / 60
        let secs = seconds.truncatingRemainder(dividingBy: 60)

        return String(format: "%02d:%02d:%06.3f", hours, minutes, secs)
    }

    /// Create CMTime from FFmpeg time format string
    /// - Parameter timeString: Time in format HH:MM:SS.mmm
    /// - Returns: CMTime or nil if invalid format
    static func fromFFmpegFormat(_ timeString: String) -> CMTime? {
        let components = timeString.split(separator: ":")
        guard components.count == 3,
              let hours = Double(components[0]),
              let minutes = Double(components[1]),
              let seconds = Double(components[2]) else {
            return nil
        }

        let totalSeconds = hours * 3600 + minutes * 60 + seconds
        return CMTime(seconds: totalSeconds, preferredTimescale: 600)
    }

    // MARK: - Segment Calculations

    /// Calculate the frame-accurate end time for a segment
    /// The end time should be one frame before the next marker
    /// - Parameters:
    ///   - fps: Frame rate of the video
    /// - Returns: Adjusted end time
    func segmentEndTime(fps: Double) -> CMTime {
        guard fps > 0 else { return self }

        let frameDuration = CMTime(seconds: 1.0 / fps, preferredTimescale: self.timescale)
        return self - frameDuration
    }

    /// Calculate segment duration between two markers
    /// - Parameters:
    ///   - endMarkerTime: The time of the next marker
    ///   - fps: Frame rate for accurate calculation
    /// - Returns: Duration of the segment
    func segmentDuration(to endMarkerTime: CMTime, fps: Double) -> CMTime {
        let adjustedEndTime = endMarkerTime.segmentEndTime(fps: fps)
        return adjustedEndTime - self
    }

    /// Check if this time represents a valid segment start
    /// - Returns: true if the time is valid for segment extraction
    var isValidSegmentTime: Bool {
        return self != .invalid && 
               self != .indefinite && 
               self >= .zero &&
               CMTimeGetSeconds(self).isFinite
    }

    // MARK: - I-Frame Alignment

    /// Find the nearest I-frame time before this time
    /// This is used for lossless extraction optimization
    /// - Parameters:
    ///   - iFrameTimes: Array of I-frame times in the video
    /// - Returns: Nearest I-frame time before this time, or self if none found
    func nearestPrecedingIFrame(from iFrameTimes: [CMTime]) -> CMTime {
        let sortedTimes = iFrameTimes.sorted()

        // Binary search for the largest time <= self
        var left = 0
        var right = sortedTimes.count - 1
        var result = self

        while left <= right {
            let mid = (left + right) / 2

            if sortedTimes[mid] <= self {
                result = sortedTimes[mid]
                left = mid + 1
            } else {
                right = mid - 1
            }
        }

        return result
    }

    /// Find the nearest I-frame time after this time
    /// - Parameters:
    ///   - iFrameTimes: Array of I-frame times in the video
    /// - Returns: Nearest I-frame time after this time, or self if none found
    func nearestSucceedingIFrame(from iFrameTimes: [CMTime]) -> CMTime {
        let sortedTimes = iFrameTimes.sorted()

        // Binary search for the smallest time > self
        var left = 0
        var right = sortedTimes.count - 1
        var result = self

        while left <= right {
            let mid = (left + right) / 2

            if sortedTimes[mid] > self {
                result = sortedTimes[mid]
                right = mid - 1
            } else {
                left = mid + 1
            }
        }

        return result
    }

    // MARK: - Formatting

    /// Format as a segment identifier (e.g., "01m23s456ms")
    /// Used for generating unique segment filenames
    var segmentIdentifier: String {
        let seconds = CMTimeGetSeconds(self)
        guard seconds.isFinite && seconds >= 0 else {
            return "00m00s000ms"
        }

        let totalMilliseconds = Int(seconds * 1000)
        let minutes = totalMilliseconds / 60000
        let secs = (totalMilliseconds % 60000) / 1000
        let ms = totalMilliseconds % 1000

        return String(format: "%02dm%02ds%03dms", minutes, secs, ms)
    }

    /// Format duration for display (e.g., "1:23.4")
    var segmentDurationDisplay: String {
        let seconds = CMTimeGetSeconds(self)
        guard seconds.isFinite && seconds >= 0 else {
            return "0:00.0"
        }

        let mins = Int(seconds) / 60
        let secs = seconds.truncatingRemainder(dividingBy: 60)

        if mins > 0 {
            return String(format: "%d:%05.1f", mins, secs)
        } else {
            return String(format: "%.1f", secs)
        }
    }
}

// MARK: - Segment Time Range

extension CMTimeRange {

    /// Create a time range for a video segment
    /// - Parameters:
    ///   - startMarkerTime: Time of the starting marker
    ///   - endMarkerTime: Time of the ending marker
    ///   - fps: Frame rate for accurate end time calculation
    /// - Returns: Time range for the segment
    static func segmentRange(from startMarkerTime: CMTime, to endMarkerTime: CMTime, fps: Double) -> CMTimeRange {
        let duration = startMarkerTime.segmentDuration(to: endMarkerTime, fps: fps)
        return CMTimeRange(start: startMarkerTime, duration: duration)
    }

    /// Check if this range is valid for segment extraction
    var isValidSegmentRange: Bool {
        return self.start.isValidSegmentTime &&
               self.duration > .zero &&
               self.end.isValidSegmentTime
    }

    /// Get the duration in seconds for display
    var durationInSeconds: Double {
        return CMTimeGetSeconds(duration)
    }
}

