import Foundation

/// Result of a video segment extraction operation
struct ExtractionResult: Identifiable, Codable {
    let id = UUID()
    let timestamp: Date
    let status: ExtractionStatus
    let segment: SegmentInfo
    let outputPath: String?
    let fileSize: Int64?
    let duration: TimeInterval
    let error: ExtractionErrorInfo?

    /// Simplified segment information for storage
    struct SegmentInfo: Codable {
        let startTime: Double
        let endTime: Double
        let rating: Int?
        let labels: [String]
        let sourcePath: String
    }

    /// Extraction status
    enum ExtractionStatus: String, Codable {
        case success
        case failed
        case cancelled
    }

    /// Error information for failed extractions
    struct ExtractionErrorInfo: Codable {
        let code: String
        let message: String
        let details: String?
    }

    /// Success result initializer
    init(
        segment: SegmentInfo,
        outputPath: String,
        fileSize: Int64,
        duration: TimeInterval
    ) {
        self.timestamp = Date()
        self.status = .success
        self.segment = segment
        self.outputPath = outputPath
        self.fileSize = fileSize
        self.duration = duration
        self.error = nil
    }

    /// Failure result initializer
    init(
        segment: SegmentInfo,
        error: ExtractionErrorInfo,
        duration: TimeInterval
    ) {
        self.timestamp = Date()
        self.status = .failed
        self.segment = segment
        self.outputPath = nil
        self.fileSize = nil
        self.duration = duration
        self.error = error
    }

    /// Cancelled result initializer
    init(segment: SegmentInfo) {
        self.timestamp = Date()
        self.status = .cancelled
        self.segment = segment
        self.outputPath = nil
        self.fileSize = nil
        self.duration = 0
        self.error = nil
    }
}

// MARK: - Batch Result

/// Result of a batch extraction operation
struct BatchExtractionResult {
    let startTime: Date
    let endTime: Date
    let results: [ExtractionResult]

    var duration: TimeInterval {
        endTime.timeIntervalSince(startTime)
    }

    var successCount: Int {
        results.filter { $0.status == .success }.count
    }

    var failureCount: Int {
        results.filter { $0.status == .failed }.count
    }

    var cancelledCount: Int {
        results.filter { $0.status == .cancelled }.count
    }

    var totalFileSize: Int64 {
        results.compactMap { $0.fileSize }.reduce(0, +)
    }

    var averageSegmentDuration: TimeInterval {
        guard !results.isEmpty else { return 0 }
        let totalDuration = results.map { $0.segment.endTime - $0.segment.startTime }.reduce(0, +)
        return totalDuration / Double(results.count)
    }
}

// MARK: - Progress Tracking

/// Progress information for ongoing extraction
struct ExtractionProgress {
    let currentSegment: Int
    let totalSegments: Int
    let segmentProgress: Double
    let estimatedTimeRemaining: TimeInterval?
    let currentSegmentName: String

    var overallProgress: Double {
        guard totalSegments > 0 else { return 0 }
        let baseProgress = Double(currentSegment - 1) / Double(totalSegments)
        let segmentContribution = segmentProgress / Double(totalSegments)
        return (baseProgress + segmentContribution) * 100
    }

    var progressDescription: String {
        "Extracting segment \(currentSegment) of \(totalSegments)"
    }
}

// MARK: - Extraction Options

/// Options for video segment extraction
struct ExtractionOptions {
    var outputDirectory: URL
    var nameFormat: SegmentNameFormat
    var videoQuality: VideoQuality
    var audioQuality: AudioQuality
    var losslessMode: Bool

    enum SegmentNameFormat: String, CaseIterable {
        case timeRange = "TimeRange"           // video_00m30s-01m45s.mp4
        case sequential = "Sequential"          // video_segment_001.mp4
        case descriptive = "Descriptive"        // video_rating4_label.mp4

        var description: String {
            switch self {
            case .timeRange:
                return "Include start and end times"
            case .sequential:
                return "Number segments sequentially"
            case .descriptive:
                return "Include rating and labels"
            }
        }
    }

    enum VideoQuality: String, CaseIterable {
        case original = "Original"
        case high = "High"
        case medium = "Medium"
        case low = "Low"

        var ffmpegPreset: String? {
            switch self {
            case .original:
                return nil  // Stream copy
            case .high:
                return "slow"
            case .medium:
                return "medium"
            case .low:
                return "fast"
            }
        }
    }

    enum AudioQuality: String, CaseIterable {
        case original = "Original"
        case high = "High"
        case medium = "Medium"
        case low = "Low"

        var ffmpegBitrate: String? {
            switch self {
            case .original:
                return nil  // Stream copy
            case .high:
                return "256k"
            case .medium:
                return "128k"
            case .low:
                return "64k"
            }
        }
    }

    static var `default`: ExtractionOptions {
        ExtractionOptions(
            outputDirectory: FileManager.default.urls(for: .moviesDirectory, in: .userDomainMask).first!,
            nameFormat: .descriptive,
            videoQuality: .original,
            audioQuality: .original,
            losslessMode: false
        )
    }
}

