//
//  ExtractionFileManager.swift
//  SwiftMedia
//
//  Created on 1/26/25.
//

import Foundation
import AVFoundation

/// Manages file naming and directory operations for video segment extraction
class ExtractionFileManager: ObservableObject {

    // MARK: - Properties

    @Published var defaultOutputDirectory: URL?
    @Published var recentDirectories: [URL] = []

    private let maxRecentDirectories = 5
    private let userDefaults = UserDefaults.standard

    // Keys for UserDefaults
    private let outputDirectoryKey = "ExtractionOutputDirectory"
    private let recentDirectoriesKey = "ExtractionRecentDirectories"

    // MARK: - Initialization

    init() {
        loadSettings()
    }

    // MARK: - Public Methods

    /// Generate a unique filename for a segment
    /// - Parameters:
    ///   - segment: The segment to extract
    ///   - sourceVideoPath: Path to the source video
    ///   - options: Extraction options including naming format
    ///   - sequenceNumber: Number for sequential naming
    /// - Returns: Generated filename
    func generateFilename(
        for segment: Segment,
        sourceVideoPath: String,
        options: ExtractionOptions,
        sequenceNumber: Int? = nil
    ) -> String {

        let sourceURL = URL(fileURLWithPath: sourceVideoPath)
        let baseName = sourceURL.deletingPathExtension().lastPathComponent
        let fileExtension = sourceURL.pathExtension

        var filename: String

        switch options.nameFormat {
        case .timeRange:
            filename = generateTimeRangeFilename(
                baseName: baseName,
                segment: segment
            )

        case .sequential:
            let number = sequenceNumber ?? 1
            filename = "\(baseName)_segment_\(String(format: "%03d", number))"

        case .descriptive:
            filename = generateDescriptiveFilename(
                baseName: baseName,
                segment: segment
            )
        }

        return "\(filename).\(fileExtension)"
    }

    /// Generate full output path for a segment
    /// - Parameters:
    ///   - segment: The segment to extract
    ///   - sourceVideoPath: Path to the source video
    ///   - outputDirectory: Directory to save the file
    ///   - options: Extraction options
    ///   - sequenceNumber: Number for sequential naming
    /// - Returns: Full output path
    func generateOutputPath(
        for segment: Segment,
        sourceVideoPath: String,
        outputDirectory: URL,
        options: ExtractionOptions,
        sequenceNumber: Int? = nil
    ) -> URL {

        let filename = generateFilename(
            for: segment,
            sourceVideoPath: sourceVideoPath,
            options: options,
            sequenceNumber: sequenceNumber
        )

        var outputPath = outputDirectory.appendingPathComponent(filename)

        // Ensure unique filename if file exists
        outputPath = makeUniqueFilePath(outputPath)

        return outputPath
    }

    /// Select output directory using file picker
    /// - Returns: Selected directory URL or nil if cancelled
    @MainActor
    func selectOutputDirectory() async -> URL? {
        let panel = NSOpenPanel()
        panel.canChooseFiles = false
        panel.canChooseDirectories = true
        panel.allowsMultipleSelection = false
        panel.prompt = "Select Output Directory"
        panel.message = "Choose where to save extracted video segments"

        // Set initial directory
        if let defaultDir = defaultOutputDirectory {
            panel.directoryURL = defaultDir
        }

        let response = await panel.begin()

        if response == .OK, let url = panel.url {
            setDefaultOutputDirectory(url)
            return url
        }

        return nil
    }

    /// Set the default output directory
    func setDefaultOutputDirectory(_ url: URL) {
        defaultOutputDirectory = url
        addToRecentDirectories(url)
        saveSettings()
    }

    /// Create a subdirectory for batch extractions
    /// - Parameters:
    ///   - parentDirectory: Parent directory
    ///   - name: Subdirectory name
    /// - Returns: URL of created directory or nil if failed
    func createBatchDirectory(
        in parentDirectory: URL,
        name: String? = nil
    ) -> URL? {

        let directoryName = name ?? generateBatchDirectoryName()
        let batchURL = parentDirectory.appendingPathComponent(directoryName)

        do {
            try FileManager.default.createDirectory(
                at: batchURL,
                withIntermediateDirectories: true,
                attributes: nil
            )
            return batchURL
        } catch {
            print("Failed to create batch directory: \(error)")
            return nil
        }
    }

    /// Check if output directory has sufficient space
    /// - Parameters:
    ///   - directory: Directory to check
    ///   - estimatedSize: Estimated size needed in bytes
    /// - Returns: True if sufficient space available
    func hassufficientSpace(
        in directory: URL,
        for estimatedSize: Int64
    ) -> Bool {

        do {
            let values = try directory.resourceValues(forKeys: [.volumeAvailableCapacityForImportantUsageKey])

            if let availableSpace = values.volumeAvailableCapacityForImportantUsage {
                // Add 10% buffer
                let requiredSpace = Int64(Double(estimatedSize) * 1.1)
                return availableSpace >= requiredSpace
            }
        } catch {
            print("Failed to check available space: \(error)")
        }

        return true // Assume sufficient space if check fails
    }

    // MARK: - Private Methods

    private func generateTimeRangeFilename(
        baseName: String,
        segment: Segment
    ) -> String {

        var filename = baseName

        if let startTime = segment.startMarker?.timestamp,
           let endTime = segment.endMarker?.timestamp {
            let startId = startTime.segmentIdentifier
            let endId = endTime.segmentIdentifier
            filename += "_\(startId)-\(endId)"
        }

        return filename
    }

    private func generateDescriptiveFilename(
        baseName: String,
        segment: Segment
    ) -> String {

        var components = [baseName]

        // Add time range
        if let startTime = segment.startMarker?.timestamp,
           let endTime = segment.endMarker?.timestamp {
            let duration = CMTimeGetSeconds(endTime - startTime)
            components.append(String(format: "%.1fs", duration))
        }

        // Add rating
        if let rating = segment.startMarker?.rating {
            components.append("\(rating)star")
        }

        // Add first label
        if let label = segment.startMarker?.labels.first {
            let sanitized = sanitizeForFilename(label)
            if !sanitized.isEmpty {
                components.append(sanitized)
            }
        }

        return components.joined(separator: "_")
    }

    private func generateBatchDirectoryName() -> String {
        let formatter = DateFormatter()
        formatter.dateFormat = "yyyy-MM-dd_HHmmss"
        return "Segments_\(formatter.string(from: Date()))"
    }

    private func sanitizeForFilename(_ string: String) -> String {
        // Remove or replace characters that are problematic in filenames
        let invalidCharacters = CharacterSet(charactersIn: "/\\?%*|\"<>:")
        let sanitized = string.components(separatedBy: invalidCharacters).joined(separator: "-")

        // Replace spaces with underscores
        let result = sanitized.replacingOccurrences(of: " ", with: "_")

        // Limit length
        if result.count > 50 {
            return String(result.prefix(50))
        }

        return result
    }

    private func makeUniqueFilePath(_ originalPath: URL) -> URL {
        var path = originalPath
        var counter = 1

        while FileManager.default.fileExists(atPath: path.path) {
            let directory = originalPath.deletingLastPathComponent()
            let nameWithoutExtension = originalPath.deletingPathExtension().lastPathComponent
            let fileExtension = originalPath.pathExtension

            let newName = "\(nameWithoutExtension)_\(counter).\(fileExtension)"
            path = directory.appendingPathComponent(newName)
            counter += 1
        }

        return path
    }

    private func addToRecentDirectories(_ url: URL) {
        // Remove if already exists
        recentDirectories.removeAll { $0 == url }

        // Add to beginning
        recentDirectories.insert(url, at: 0)

        // Limit to max count
        if recentDirectories.count > maxRecentDirectories {
            recentDirectories = Array(recentDirectories.prefix(maxRecentDirectories))
        }
    }

    // MARK: - Persistence

    private func loadSettings() {
        // Load default directory
        if let bookmarkData = userDefaults.data(forKey: outputDirectoryKey),
           let url = try? URL(resolvingBookmarkData: bookmarkData, bookmarkDataIsStale: nil) {
            defaultOutputDirectory = url
        }

        // Load recent directories
        if let bookmarksData = userDefaults.array(forKey: recentDirectoriesKey) as? [Data] {
            recentDirectories = bookmarksData.compactMap { data in
                try? URL(resolvingBookmarkData: data, bookmarkDataIsStale: nil)
            }
        }
    }

    private func saveSettings() {
        // Save default directory
        if let url = defaultOutputDirectory,
           let bookmarkData = try? url.bookmarkData() {
            userDefaults.set(bookmarkData, forKey: outputDirectoryKey)
        }

        // Save recent directories
        let bookmarksData = recentDirectories.compactMap { url in
            try? url.bookmarkData()
        }
        userDefaults.set(bookmarksData, forKey: recentDirectoriesKey)
    }
}

// MARK: - File Size Estimation

extension ExtractionFileManager {

    /// Estimate the output file size for a segment
    /// - Parameters:
    ///   - segment: The segment to extract
    ///   - sourceFileSize: Size of the source video file
    ///   - videoDuration: Total duration of the video
    /// - Returns: Estimated size in bytes
    func estimateOutputSize(
        for segment: Segment,
        sourceFileSize: Int64,
        videoDuration: CMTime
    ) -> Int64 {

        guard videoDuration.seconds > 0 else { return 0 }

        let segmentDuration = segment.duration.seconds
        let durationRatio = segmentDuration / videoDuration.seconds

        // Estimate based on duration ratio
        // Add 5% overhead for container metadata
        return Int64(Double(sourceFileSize) * durationRatio * 1.05)
    }

    /// Estimate total size for batch extraction
    func estimateBatchSize(
        segments: [Segment],
        sourceFileSize: Int64,
        videoDuration: CMTime
    ) -> Int64 {

        return segments.reduce(0) { total, segment in
            total + estimateOutputSize(
                for: segment,
                sourceFileSize: sourceFileSize,
                videoDuration: videoDuration
            )
        }
    }
}

