            let nameWithoutExtension = originalPath.deletingPathExtension().lastPathComponent
            let fileExtension = originalPath.pathExtension

            let newName = "\(nameWithoutExtension)_\(counter).\(fileExtension)"
            path = directory.appendingPathComponent(newName)
            counter += 1
        }

        return path
    }

    private func addToRecentDirectories(_ url: URL) {
        // Remove if already exists
        recentDirectories.removeAll { $0 == url }

        // Add to beginning
        recentDirectories.insert(url, at: 0)

        // Limit to max count
        if recentDirectories.count > maxRecentDirectories {
            recentDirectories = Array(recentDirectories.prefix(maxRecentDirectories))
        }
    }

    // MARK: - Persistence

    private func loadSettings() {
        // Load default directory
        if let bookmarkData = userDefaults.data(forKey: outputDirectoryKey),
           let url = try? URL(resolvingBookmarkData: bookmarkData, bookmarkDataIsStale: nil) {
            defaultOutputDirectory = url
        }

        // Load recent directories
        if let bookmarksData = userDefaults.array(forKey: recentDirectoriesKey) as? [Data] {
            recentDirectories = bookmarksData.compactMap { data in
                try? URL(resolvingBookmarkData: data, bookmarkDataIsStale: nil)
            }
        }
    }

    private func saveSettings() {
        // Save default directory
        if let url = defaultOutputDirectory,
           let bookmarkData = try? url.bookmarkData() {
            userDefaults.set(bookmarkData, forKey: outputDirectoryKey)
        }

        // Save recent directories
        let bookmarksData = recentDirectories.compactMap { url in
            try? url.bookmarkData()
        }
        userDefaults.set(bookmarksData, forKey: recentDirectoriesKey)
    }
}

// MARK: - File Size Estimation

extension ExtractionFileManager {

    /// Estimate the output file size for a segment
    /// - Parameters:
    ///   - segment: The segment to extract
    ///   - sourceFileSize: Size of the source video file
    ///   - videoDuration: Total duration of the video
    /// - Returns: Estimated size in bytes
    func estimateOutputSize(
        for segment: Segment,
        sourceFileSize: Int64,
        videoDuration: CMTime
    ) -> Int64 {

        guard videoDuration.seconds > 0 else { return 0 }

        let segmentDuration = segment.duration.seconds
        let durationRatio = segmentDuration / videoDuration.seconds

        // Estimate based on duration ratio
        // Add 5% overhead for container metadata
        return Int64(Double(sourceFileSize) * durationRatio * 1.05)
    }

    /// Estimate total size for batch extraction
    func estimateBatchSize(
        segments: [Segment],
        sourceFileSize: Int64,
        videoDuration: CMTime
    ) -> Int64 {

        return segments.reduce(0) { total, segment in
            total + estimateOutputSize(
                for: segment,
                sourceFileSize: sourceFileSize,
                videoDuration: videoDuration
            )
        }
    }
}

