import Foundation

/// Comprehensive error types for video segment extraction
enum ExtractionError: LocalizedError {

    // MARK: - Setup Errors
    case ffmpegNotInstalled
    case ffmpegVersionIncompatible(String)
    case outputDirectoryNotSet
    case outputDirectoryNotWritable(URL)
    case insufficientDiskSpace(required: Int64, available: Int64)

    // MARK: - Input Validation Errors
    case invalidVideoPath(String)
    case videoFileNotFound(String)
    case unsupportedVideoFormat(String)
    case corruptedVideoFile(String)

    // MARK: - Segment Errors
    case invalidSegment(reason: String)
    case segmentOutOfBounds(start: Double, end: Double, duration: Double)
    case segmentTooShort(duration: Double, minimum: Double)
    case noMarkersFound

    // MARK: - Processing Errors
    case extractionFailed(reason: String)
    case ffmpegProcessError(code: Int32, output: String)
    case timeoutExceeded(seconds: Int)
    case memoryPressure
    case cancelled

    // MARK: - Output Errors
    case fileWriteFailed(path: String, underlyingError: Error?)
    case permissionDenied(path: String)
    case outputFileExists(path: String)

    // MARK: - Network Errors (for future cloud support)
    case networkUnavailable
    case uploadFailed(reason: String)

    // MARK: - LocalizedError Implementation

    var errorDescription: String? {
        switch self {
        case .ffmpegNotInstalled:
            return "FFmpeg is not installed"

        case .ffmpegVersionIncompatible(let version):
            return "FFmpeg version \(version) is not compatible. Please update FFmpeg."

        case .outputDirectoryNotSet:
            return "Please select an output directory for extracted segments"

        case .outputDirectoryNotWritable(let url):
            return "Cannot write to directory: \(url.lastPathComponent)"

        case .insufficientDiskSpace(let required, let available):
            let formatter = ByteCountFormatter()
            let reqStr = formatter.string(fromByteCount: required)
            let availStr = formatter.string(fromByteCount: available)
            return "Insufficient disk space. Required: \(reqStr), Available: \(availStr)"

        case .invalidVideoPath(let path):
            return "Invalid video path: \(path)"

        case .videoFileNotFound(let path):
            return "Video file not found: \(path)"

        case .unsupportedVideoFormat(let format):
            return "Unsupported video format: \(format)"

        case .corruptedVideoFile(let path):
            return "Video file appears to be corrupted: \(path)"

        case .invalidSegment(let reason):
            return "Invalid segment: \(reason)"

        case .segmentOutOfBounds(let start, let end, let duration):
            return String(format: "Segment (%.1f-%.1f) exceeds video duration (%.1f)", start, end, duration)

        case .segmentTooShort(let duration, let minimum):
            return String(format: "Segment too short (%.1fs). Minimum: %.1fs", duration, minimum)

        case .noMarkersFound:
            return "No markers found in the video"

        case .extractionFailed(let reason):
            return "Extraction failed: \(reason)"

        case .ffmpegProcessError(let code, let output):
            return "FFmpeg error (code \(code)): \(output.prefix(200))"

        case .timeoutExceeded(let seconds):
            return "Operation timed out after \(seconds) seconds"

        case .memoryPressure:
            return "Insufficient memory available"

        case .cancelled:
            return "Operation was cancelled"

        case .fileWriteFailed(let path, let error):
            var message = "Failed to write file: \(path)"
            if let error = error {
                message += " - \(error.localizedDescription)"
            }
            return message

        case .permissionDenied(let path):
            return "Permission denied: \(path)"

        case .outputFileExists(let path):
            return "Output file already exists: \(path)"

        case .networkUnavailable:
            return "Network connection unavailable"

        case .uploadFailed(let reason):
            return "Upload failed: \(reason)"
        }
    }

    var recoverySuggestion: String? {
        switch self {
        case .ffmpegNotInstalled:
            return "Install FFmpeg using: brew install ffmpeg"

        case .ffmpegVersionIncompatible:
            return "Update FFmpeg using: brew upgrade ffmpeg"

        case .outputDirectoryNotSet:
            return "Click 'Select Directory' to choose where to save segments"

        case .outputDirectoryNotWritable:
            return "Choose a different directory or check file permissions"

        case .insufficientDiskSpace:
            return "Free up disk space or choose a different output location"

        case .unsupportedVideoFormat:
            return "Convert the video to a supported format (MP4, MOV, M4V)"

        case .segmentTooShort:
            return "Select a longer segment or adjust the minimum duration setting"

        case .noMarkersFound:
            return "Add markers to the video before extracting segments"

        case .memoryPressure:
            return "Close other applications and try again"

        case .outputFileExists:
            return "Choose a different filename or enable overwrite option"

        default:
            return nil
        }
    }

    var isRecoverable: Bool {
        switch self {
        case .cancelled, .ffmpegNotInstalled, .outputDirectoryNotSet,
             .noMarkersFound, .outputFileExists:
            return true
        default:
            return false
        }
    }

    var requiresUserAction: Bool {
        switch self {
        case .ffmpegNotInstalled, .outputDirectoryNotSet,
             .outputDirectoryNotWritable, .insufficientDiskSpace,
             .permissionDenied, .outputFileExists:
            return true
        default:
            return false
        }
    }
}

// MARK: - Error Context

/// Additional context for extraction errors
struct ExtractionErrorContext {
    let error: ExtractionError
    let segment: Segment?
    let videoPath: String?
    let timestamp: Date
    let additionalInfo: [String: Any]

    init(
        error: ExtractionError,
        segment: Segment? = nil,
        videoPath: String? = nil,
        additionalInfo: [String: Any] = [:]
    ) {
        self.error = error
        self.segment = segment
        self.videoPath = videoPath
        self.timestamp = Date()
        self.additionalInfo = additionalInfo
    }

    var logDescription: String {
        var components = ["ExtractionError: \(error.localizedDescription)"]

        if let videoPath = videoPath {
            components.append("Video: \(videoPath)")
        }

        if let segment = segment,
           let start = segment.startMarker?.timestamp,
           let end = segment.endMarker?.timestamp {
            components.append("Segment: \(start.seconds)-\(end.seconds)")
        }

        if !additionalInfo.isEmpty {
            components.append("Info: \(additionalInfo)")
        }

        return components.joined(separator: " | ")
    }
}

// MARK: - FFmpeg Error Parsing

extension ExtractionError {

    /// Parse FFmpeg stderr output to create appropriate error
    static func fromFFmpegOutput(_ output: String, exitCode: Int32) -> ExtractionError {
        let lowercased = output.lowercased()

        // Check for common FFmpeg errors
        if lowercased.contains("no such file or directory") {
            if let path = extractPath(from: output) {
                return .videoFileNotFound(path)
            }
        }

        if lowercased.contains("permission denied") {
            if let path = extractPath(from: output) {
                return .permissionDenied(path)
            }
        }

        if lowercased.contains("invalid data") || lowercased.contains("could not find codec") {
            return .corruptedVideoFile("Unknown")
        }

        if lowercased.contains("no space left on device") {
            return .insufficientDiskSpace(required: 0, available: 0)
        }

        if lowercased.contains("unknown format") || lowercased.contains("unrecognized option") {
            return .unsupportedVideoFormat("Unknown")
        }

        // Default to generic process error
        return .ffmpegProcessError(code: exitCode, output: output)
    }

    private static func extractPath(from output: String) -> String? {
        // Try to extract file path from FFmpeg output
        // This is a simple implementation - could be improved with regex
        let lines = output.components(separatedBy: .newlines)
        for line in lines {
            if line.contains(":") && line.contains("/") {
                let components = line.components(separatedBy: ":")
                if components.count > 1 {
                    return components[1].trimmingCharacters(in: .whitespaces)
                }
            }
        }
        return nil
    }
}

