import SwiftUI
import AVKit
import UniformTypeIdentifiers

struct ContentView: View {
    @EnvironmentObject var viewModel: VideoPlayerViewModel
    @StateObject private var settingsViewModel = SettingsViewModel()
    @StateObject private var directoryManager = VideoDirectoryManager()
    @StateObject private var navigationManager = NavigationManager()
    @StateObject private var filePickerManager = FilePickerManager()
    @StateObject private var directoryScanManager = DirectoryScanManager()

    @State private var showingSettings = false
    @State private var showingMarkerDetails = false
    @State private var selectedMarkerForEdit: Marker?
    @State private var showingMarkerList = false

    var body: some View {
        ZStack {
            DesignSystem.Colors.background
                .ignoresSafeArea()

            // Keyboard handling layer
            KeyboardHandlingView(
                viewModel: viewModel,
                settingsViewModel: settingsViewModel,
                showingSettings: $showingSettings,
                selectedMarkerForEdit: $selectedMarkerForEdit,
                showingMarkerList: $showingMarkerList,
                onNavigateNext: navigationManager.navigateToNextVideo,
                onNavigatePrevious: navigationManager.navigateToPreviousVideo,
                onOpenFile: filePickerManager.openFilePicker
            )

            MainContentView(
                viewModel: viewModel,
                directoryManager: directoryManager,
                filePickerManager: filePickerManager,
                selectedMarkerForEdit: $selectedMarkerForEdit,
                showingMarkerList: $showingMarkerList
            )
        }
        .sheet(isPresented: $showingSettings) {
            SettingsView()
                .environmentObject(settingsViewModel)
        }
        .sheet(item: $selectedMarkerForEdit) { _ in
            MarkerManagementView(viewModel: viewModel)
                .frame(width: 400, height: 650)
        }
        .task {
            setupManagers()
        }
        .onChange(of: settingsViewModel.scrubbingConfigs) {
            // Keep scrubbing settings in sync
            viewModel.scrubbingSettings = ScrubbingSettings(configs: settingsViewModel.scrubbingConfigs)
        }
        .alert("Error", isPresented: $navigationManager.showingError) {
            Button("OK") { }
        } message: {
            Text(navigationManager.errorMessage)
        }
        .onReceive(NotificationCenter.default.publisher(for: .openVideoFile)) { _ in
            filePickerManager.openFilePicker()
        }
        .onReceive(NotificationCenter.default.publisher(for: .grantDirectoryAccess)) { _ in
            Task {
                await filePickerManager.handleGrantDirectoryAccess()
            }
        }
        .onChange(of: viewModel.videoURL) { _, newURL in
            Task {
                await directoryScanManager.handleVideoURLChange(newURL)
            }
        }
    }
    // MARK: - Manager Setup
    private func setupManagers() {
        // Sync scrubbing settings from SettingsViewModel to VideoPlayerViewModel
        viewModel.scrubbingSettings = ScrubbingSettings(configs: settingsViewModel.scrubbingConfigs)

        // Setup manager dependencies
        navigationManager.viewModel = viewModel
        navigationManager.directoryManager = directoryManager

        filePickerManager.viewModel = viewModel
        filePickerManager.directoryManager = directoryManager
        filePickerManager.navigationManager = navigationManager

        directoryScanManager.directoryManager = directoryManager
        directoryScanManager.navigationManager = navigationManager
    }
}

