import SwiftUI

struct ExtractionModalView: View {
    @Environment(\.dismiss) var dismiss
    @EnvironmentObject var viewModel: VideoPlayerViewModel

    @State private var selectedTab = 0

    private let tabs = [
        Tab(
            title: "Single Segment",
            icon: "scissors",
            content: AnyView(SingleSegmentExtractionView())
        ),
        Tab(
            title: "By Rating",
            icon: "star.fill",
            content: AnyView(RatingBasedExtractionView())
        ),
        Tab(
            title: "Multi-Select",
            icon: "checklist",
            content: AnyView(MultiSelectExtractionView())
        )
    ]

    var body: some View {
        VStack(spacing: 0) {
            // Title Bar
            HStack {
                Text("Extract Video Segments")
                    .font(.title2)
                    .fontWeight(.semibold)

                Spacer()

                Button(action: { dismiss() }) {
                    Image(systemName: "xmark.circle.fill")
                        .font(.system(size: 22))
                        .foregroundColor(.secondary)
                        .symbolRenderingMode(.hierarchical)
                }
                .buttonStyle(.plain)
            }
            .padding()
            .background(Color(NSColor.windowBackgroundColor))

            Divider()

            // Tab View
            VStack(spacing: 0) {
                // Tab Bar
                HStack(spacing: 20) {
                    ForEach(0..<tabs.count, id: \.self) { index in
                        TabButton(
                            title: tabs[index].title,
                            icon: tabs[index].icon,
                            isSelected: selectedTab == index,
                            action: { selectedTab = index }
                        )
                    }
                    Spacer()
                }
                .padding(.horizontal)
                .padding(.vertical, 10)
                .background(Color(NSColor.windowBackgroundColor))

                Divider()

                // Tab Content
                tabs[selectedTab].content
                    .frame(maxWidth: .infinity, maxHeight: .infinity)
                    .animation(.easeInOut(duration: 0.2), value: selectedTab)
            }
        }
        .frame(width: 700, height: 500)
        .background(Color(NSColor.windowBackgroundColor))
        .onKeyPress(.escape) {
            dismiss()
            return .handled
        }
        .onKeyPress(.tab, modifiers: .control) {
            // Tab navigation with Ctrl+Tab
            selectedTab = (selectedTab + 1) % tabs.count
            return .handled
        }
        .onKeyPress(.tab, modifiers: [.control, .shift]) {
            // Reverse tab navigation with Ctrl+Shift+Tab
            selectedTab = selectedTab > 0 ? selectedTab - 1 : tabs.count - 1
            return .handled
        }
    }
}

