import SwiftUI
import AVFoundation

struct SingleSegmentExtractionView: View {
    @EnvironmentObject var viewModel: VideoPlayerViewModel

    var body: some View {
        VStack(spacing: 20) {
            // Segment Info
            VStack(alignment: .leading, spacing: 12) {
                Text("Current Segment")
                    .font(.headline)

                if let currentSegment = viewModel.currentSegment {
                    HStack {
                        VStack(alignment: .leading, spacing: 6) {
                            Label("Start", systemImage: "play.fill")
                                .foregroundColor(.secondary)
                            Text(formatTime(currentSegment.startTime))
                                .font(.system(.body, design: .monospaced))
                        }

                        Spacer()

                        VStack(alignment: .leading, spacing: 6) {
                            Label("End", systemImage: "stop.fill")
                                .foregroundColor(.secondary)
                            Text(formatTime(currentSegment.endTime))
                                .font(.system(.body, design: .monospaced))
                        }

                        Spacer()

                        VStack(alignment: .leading, spacing: 6) {
                            Label("Duration", systemImage: "timer")
                                .foregroundColor(.secondary)
                            Text(formatDuration(currentSegment.duration))
                                .font(.system(.body, design: .monospaced))
                        }
                    }
                    .padding()
                    .background(Color(NSColor.controlBackgroundColor))
                    .cornerRadius(8)

                    // Marker Labels
                    if !currentSegment.markers.isEmpty {
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Marker Labels")
                                .font(.headline)

                            ScrollView(.horizontal, showsIndicators: false) {
                                HStack(spacing: 8) {
                                    ForEach(currentSegment.markers.flatMap { $0.labels }, id: \.self) { label in
                                        Text(label)
                                            .font(.caption)
                                            .padding(.horizontal, 10)
                                            .padding(.vertical, 4)
                                            .background(Color.accentColor.opacity(0.2))
                                            .cornerRadius(12)
                                    }
                                }
                            }
                        }
                    }
                } else {
                    Text("No segment selected. Navigate to a marker to define a segment.")
                        .foregroundColor(.secondary)
                        .padding()
                        .frame(maxWidth: .infinity)
                        .background(Color(NSColor.controlBackgroundColor))
                        .cornerRadius(8)
                }
            }
            .padding()

            Spacer()

            // Extract Button
            HStack {
                Spacer()

                Button(action: extractCurrentSegment) {
                    Label("Extract Segment", systemImage: "scissors")
                        .frame(width: 160)
                }
                .controlSize(.large)
                .keyboardShortcut(.return, modifiers: [])
                .disabled(viewModel.currentSegment == nil)
            }
            .padding()
        }
    }

    private func extractCurrentSegment() {
        // TODO: Implement extraction using SegmentExtractionManager
        print("Extracting current segment...")
    }

    private func formatTime(_ time: CMTime) -> String {
        let seconds = time.seconds
        let hours = Int(seconds) / 3600
        let minutes = (Int(seconds) % 3600) / 60
        let secs = Int(seconds) % 60
        let frames = Int((seconds - Double(Int(seconds))) * 30) // Assuming 30fps

        if hours > 0 {
            return String(format: "%02d:%02d:%02d.%02d", hours, minutes, secs, frames)
        } else {
            return String(format: "%02d:%02d.%02d", minutes, secs, frames)
        }
    }

    private func formatDuration(_ duration: CMTime) -> String {
        let seconds = duration.seconds
        if seconds < 60 {
            return String(format: "%.1fs", seconds)
        } else {
            let minutes = Int(seconds) / 60
            let secs = Int(seconds) % 60
            return String(format: "%dm %ds", minutes, secs)
        }
    }
}

