//
//  VideoPlayerViewModel.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import Foundation
import AVFoundation
import Combine

enum PlayerError: LocalizedError {
    case fileNotFound
    case unsupportedFormat
    case decodingFailed
    case markerSaveFailed(String)

    var errorDescription: String? {
        switch self {
        case .fileNotFound: return "Video file not found"
        case .unsupportedFormat: return "This video format is not supported"
        case .decodingFailed: return "Failed to decode video"
        case .markerSaveFailed(let details): return "Failed to save markers: \(details)"
        }
    }

    var recoverySuggestion: String? {
        switch self {
        case .fileNotFound:
            return "Try opening a different video file or check if the file was moved."
        case .unsupportedFormat:
            return "Try converting the video to MP4 or MOV format."
        case .decodingFailed:
            return "Try restarting the app or check if the video file is corrupted."
        case .markerSaveFailed:
            return "Check disk space and permissions, then try again."
        }
    }
}

@MainActor
class VideoPlayerViewModel: ObservableObject {
    @Published var player: AVPlayer?
    @Published var currentTime: CMTime = .zero
    @Published var duration: CMTime = .zero
    @Published var isPlaying = false
    @Published var frameRate: Float = 30.0
    @Published var videoURL: URL?
    @Published var playerError: PlayerError?
    @Published var loadTime: TimeInterval = 0
    @Published var markers: [Marker] = [] {
        didSet {
            // Invalidate cache when markers change
            markersCacheInvalidated = true

            // Auto-save markers when changed
            if oldValue != markers {
                scheduleMarkerSave()
            }
        }
    }
    @Published var selectedMarker: Marker?
    @Published var scrubbingSettings = ScrubbingSettings()
    @Published var seekFeedback: SeekFeedback?
    @Published var showPerformanceOverlay = false
    @Published var videoHash: String?

    private var timeObserver: Any?
    private var cancellables = Set<AnyCancellable>()
    private var loadStartTime: Date?
    private var sortedMarkersCache: [Marker] = []
    private var markersCacheInvalidated = true
    private var saveWorkItem: DispatchWorkItem?
    private var seekManager: SeekManager?

    private func updateMarkersCache() {
        if markersCacheInvalidated {
            sortedMarkersCache = markers.sorted()
            markersCacheInvalidated = false
        }
    }

    var segments: [Segment] {
        // Use cached markers if available, otherwise compute without modifying state
        let sortedMarkers = markersCacheInvalidated ? markers.sorted() : sortedMarkersCache

        var result = [Segment]()
        guard let duration = player?.currentItem?.duration,
              duration.isValid && duration.value > 0 else { return result }

        if sortedMarkers.isEmpty {
            // No markers - entire video is one segment
            result.append(Segment(startMarker: nil, endMarker: nil))
        } else {
            // First segment: start to first marker
            if let firstMarker = sortedMarkers.first {
                result.append(Segment(startMarker: nil, endMarker: firstMarker))
            }

            // Middle segments
            if sortedMarkers.count > 1 {
                for markerIndex in 0..<sortedMarkers.count - 1 {
                    result.append(Segment(
                        startMarker: sortedMarkers[markerIndex],
                        endMarker: sortedMarkers[markerIndex + 1]
                    ))
                }
            }

            // Last segment: last marker to end
            if let lastMarker = sortedMarkers.last {
                result.append(Segment(startMarker: lastMarker, endMarker: nil))
            }
        }

        return result
    }

    var currentSegment: Segment? {
        let time = currentTime

        for segment in segments {
            let startTime = segment.startMarker?.timestamp ?? .zero
            let endTime = segment.endMarker?.timestamp ?? duration

            // Check if current time is within this segment
            if CMTimeCompare(time, startTime) >= 0 && CMTimeCompare(time, endTime) < 0 {
                return segment
            }
        }

        // If we're at the very end, return the last segment
        if let lastSegment = segments.last,
           CMTimeCompare(time, duration) >= 0 {
            return lastSegment
        }

        return nil
    }

    deinit {
        // Note: Cannot access @MainActor properties from deinit
        // Cleanup is handled in loadVideo when replacing player
    }

    func loadVideo(url: URL) {
        // Clean up existing player
        player?.pause()  // Explicitly pause current player to prevent multiple audio streams
        isPlaying = false

        // Stop performance monitoring for old player
        PerformanceMonitor.shared.stopMonitoring()

        // Clear existing observers to prevent dangling subscriptions
        cancellables.removeAll()

        // Cancel any pending seeks from old SeekManager
        seekManager?.cancelPendingSeeks()

        if let observer = timeObserver {
            player?.removeTimeObserver(observer)
            timeObserver = nil
        }

        // Reset error state
        playerError = nil

        // Reset markers for new video (will load after video is ready)
        markers = []
        selectedMarker = nil
        markersCacheInvalidated = true

        // Start performance monitoring
        loadStartTime = Date()

        // Check if file exists
        guard FileManager.default.fileExists(atPath: url.path) else {
            playerError = .fileNotFound
            return
        }

        videoURL = url
        let asset = AVURLAsset(url: url)
        let playerItem = AVPlayerItem(asset: asset)

        player = AVPlayer(playerItem: playerItem)

        // Initialize SeekManager
        seekManager = SeekManager(player: player!)

        // Configure player for large files
        configurePlayerForLargeFiles()

        // Get video duration
        Task {
            do {
                let duration = try await asset.load(.duration)
                await MainActor.run {
                    self.duration = duration
                }

                // Extract frame rate
                if let track = try await asset.loadTracks(withMediaType: .video).first {
                    let frameRate = try await track.load(.nominalFrameRate)
                    await MainActor.run {
                        self.frameRate = frameRate > 0 ? frameRate : 30.0
                    }
                }
            } catch {
                await MainActor.run {
                    self.playerError = .decodingFailed
                }
                print("Error loading video metadata: \(error)")
            }
        }

        // Set up time observer
        setupTimeObserver()

        // Observe player status
        playerItem.publisher(for: \.status)
            .sink { [weak self] status in
                if status == .failed {
                    self?.handlePlayerItemError(playerItem.error)
                } else if status == .readyToPlay {
                    // Record load time
                    if let startTime = self?.loadStartTime {
                        self?.loadTime = Date().timeIntervalSince(startTime)
                        print("Video loaded in \(String(format: "%.2f", self?.loadTime ?? 0)) seconds")
                    }

                    // Load markers for this video
                    self?.loadMarkers()

                    // Start performance monitoring
                    if let player = self?.player {
                        PerformanceMonitor.shared.startMonitoring(player: player)
                        PerformanceAnalytics.shared.record(PerformanceMonitor.shared.currentMetrics ?? PerformanceMetrics(
                            timestamp: Date(),
                            cpuUsage: 0,
                            memoryUsage: 0,
                            droppedFrames: 0,
                            frameRate: 30,
                            frameRateStability: 100,
                            gpuUsage: nil,
                            decoderEfficiency: nil
                        ))
                    }

                    // Auto-play the video when it loads
                    self?.play()
                }
            }
            .store(in: &cancellables)

        // Observe when video ends
        NotificationCenter.default.publisher(for: .AVPlayerItemDidPlayToEndTime, object: playerItem)
            .sink { [weak self] _ in
                self?.isPlaying = false
            }
            .store(in: &cancellables)
    }

    private func setupTimeObserver() {
        let interval = CMTime(seconds: 1.0/30.0, preferredTimescale: CMTimeScale(NSEC_PER_SEC))
        timeObserver = player?.addPeriodicTimeObserver(forInterval: interval, queue: .main) { [weak self] time in
            Task { @MainActor in
                self?.currentTime = time
            }
        }
    }

    func play() {
        player?.play()
        isPlaying = true
    }

    func pause() {
        player?.pause()
        isPlaying = false
    }

    func togglePlayPause() {
        if isPlaying {
            pause()
        } else {
            play()
        }
    }

    func togglePerformanceOverlay() {
        showPerformanceOverlay.toggle()
    }

    // MARK: - Volume Control

    func increaseVolume() {
        guard let player = player else { return }
        let newVolume = min(player.volume + 0.1, 1.0)
        player.volume = newVolume
        showVolumeFeedback(volume: newVolume)
    }

    func decreaseVolume() {
        guard let player = player else { return }
        let newVolume = max(player.volume - 0.1, 0.0)
        player.volume = newVolume
        showVolumeFeedback(volume: newVolume)
    }

    private func showVolumeFeedback(volume: Float) {
        let percentage = Int(volume * 100)
        seekFeedback = SeekFeedback(text: "Volume: \(percentage)%", direction: .forward)

        // Auto-hide after 1 second
        DispatchQueue.main.asyncAfter(deadline: .now() + 1.0) { [weak self] in
            self?.seekFeedback = nil
        }
    }

    func seek(to time: CMTime, toleranceBefore: CMTime = .zero, toleranceAfter: CMTime = .zero) {
        let clampedTime = clampTime(time)

        if let seekManager = seekManager {
            seekManager.seek(
                to: clampedTime,
                toleranceBefore: toleranceBefore,
                toleranceAfter: toleranceAfter
            ) { finished in
                if finished {
                    // UI will update automatically via @Published currentTime
                }
            }
        } else {
            player?.seek(to: clampedTime, toleranceBefore: toleranceBefore, toleranceAfter: toleranceAfter)
        }
    }

    func seek(to progress: Double) {
        let time = CMTime(seconds: progress * CMTimeGetSeconds(duration), preferredTimescale: duration.timescale)
        seek(to: time)
    }

    private func calculateAdaptiveBufferDuration(dimensions: CGSize, bitrate: Float) async -> TimeInterval {
        // Use centralized adaptive buffer calculation from DefaultConfiguration
        return DefaultConfiguration.adaptiveBufferDuration(
            width: dimensions.width,
            height: dimensions.height,
            bitrate: bitrate
        )
    }

    private func configurePlayerForLargeFiles() {
        // Configure player for 4K+ videos
        player?.automaticallyWaitsToMinimizeStalling = false

        // Enable hardware acceleration optimizations
        VideoMemoryManager.optimizeForLargeFiles()

        // Configure for hardware decoding if available
        if let currentItem = player?.currentItem {
            // Set video composition pixel buffer attributes for optimal performance
            _ = VideoMemoryManager.optimizedPixelBufferAttributes()

            // Configure adaptive buffer based on video resolution
            Task {
                if let videoTrack = try? await currentItem.asset.loadTracks(withMediaType: .video).first {
                    // Get video dimensions and bitrate
                    let dimensions = try? await videoTrack.load(.naturalSize)
                    let estimatedBitrate = try? await videoTrack.load(.estimatedDataRate)

                    // Calculate adaptive buffer duration based on resolution and bitrate
                    let bufferDuration: TimeInterval = await calculateAdaptiveBufferDuration(
                        dimensions: dimensions ?? .zero,
                        bitrate: estimatedBitrate ?? 0
                    )

                    // Apply adaptive buffer configuration
                    await MainActor.run {
                        currentItem.preferredForwardBufferDuration = bufferDuration
                        currentItem.preferredPeakBitRate = Double.infinity // Allow maximum quality
                        currentItem.preferredMaximumResolution = CGSize.zero // No resolution limit
                    }

                    // Log codec and resolution information for debugging
                    if let formatDescriptions = try? await videoTrack.load(.formatDescriptions) {
                        for desc in formatDescriptions {
                            let mediaSubType = CMFormatDescriptionGetMediaSubType(desc)
                            print("Video codec type: \(mediaSubType)")
                        }
                    }

                    if let dimensions = dimensions {
                        print("Video resolution: \(Int(dimensions.width))x\(Int(dimensions.height))")
                        print("Buffer duration: \(bufferDuration)s")
                        print("Estimated bitrate: \(estimatedBitrate ?? 0) bps")
                    }
                }
            }
        }
    }

    private func handlePlayerItemError(_ error: Error?) {
        guard let error = error as? NSError else {
            playerError = .decodingFailed
            return
        }

        // Check for specific error codes
        switch error.code {
        case NSFileReadNoSuchFileError:
            playerError = .fileNotFound
        case -12847: // Invalid media format
            playerError = .unsupportedFormat
        default:
            playerError = .decodingFailed
        }
    }

    // MARK: - Marker Management

    func addMarker(at time: CMTime? = nil) {
        let targetTime = time ?? currentTime


        let newMarker = Marker(timestamp: targetTime, rating: 3, labels: [])
        markers.append(newMarker)
        markersCacheInvalidated = true

        // Add automatic start marker if this is the first marker and it's not at 00:00
        if markers.count == 1 && targetTime != .zero {
            let startMarker = Marker(timestamp: .zero, rating: 3, labels: ["Start"])
            markers.insert(startMarker, at: 0)
        }
    }

    func deleteMarker(id: UUID) {
        // Don't allow deletion of start marker
        if let markerToDelete = markers.first(where: { $0.id == id }),
           markerToDelete.timestamp == .zero {
            print("Cannot delete start marker")
            return
        }

        markers.removeAll { $0.id == id }
        markersCacheInvalidated = true

        // Clear selection if deleted marker was selected
        if selectedMarker?.id == id {
            selectedMarker = nil
        }
    }

    func deleteNearestMarker() {
        let currentTime = currentTime

        // Ensure cache is up to date
        updateMarkersCache()

        // Find all markers before or at current time
        let markersBefore = sortedMarkersCache.filter { marker in
            CMTimeCompare(marker.timestamp, currentTime) <= 0
        }

        // Get the closest one (last in the sorted array)
        guard let nearestMarker = markersBefore.last else {
            print("No marker found before current time")
            return
        }

        // Don't delete start marker
        if nearestMarker.timestamp == .zero {
            print("Cannot delete start marker")
            return
        }

        deleteMarker(id: nearestMarker.id)
        print("Deleted marker at \(nearestMarker.timestamp.timeString)")
    }

    func updateMarker(id: UUID, rating: Int? = nil, labels: [String]? = nil) {
        guard let index = markers.firstIndex(where: { $0.id == id }) else { return }

        if let rating = rating {
            markers[index].rating = max(1, min(5, rating))
        }

        if let labels = labels {
            markers[index].labels = labels
        }

        markersCacheInvalidated = true
    }

    func updateMarker(_ marker: Marker) {
        guard let index = markers.firstIndex(where: { $0.id == marker.id }) else { return }
        markers[index] = marker
        markersCacheInvalidated = true
    }

    func deleteMarker(_ marker: Marker) {
        deleteMarker(id: marker.id)
    }

    func editMarker(_ marker: Marker) {
        selectedMarker = marker
        // The UI will handle showing the edit dialog based on selectedMarker
    }

    func jumpToNextMarker() {
        let currentTime = currentTime

        // Ensure cache is up to date
        updateMarkersCache()

        // Use cached sorted markers for O(log n) lookup
        let index = sortedMarkersCache.binarySearch { marker in
            CMTimeCompare(marker.timestamp, currentTime) <= 0
        }

        if index < sortedMarkersCache.count {
            let nextMarker = sortedMarkersCache[index]
            seek(to: nextMarker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
            selectedMarker = nextMarker
        } else if !sortedMarkersCache.isEmpty {
            // Wrap to first marker
            let firstMarker = sortedMarkersCache[0]
            seek(to: firstMarker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
            selectedMarker = firstMarker
        }
    }

    func jumpToPreviousMarker() {
        let currentTime = currentTime

        // Ensure cache is up to date
        updateMarkersCache()

        // Binary search for previous marker
        var index = sortedMarkersCache.binarySearch { marker in
            CMTimeCompare(marker.timestamp, currentTime) < 0
        }

        // If we're exactly on a marker, go to the one before it
        if index > 0 && index < sortedMarkersCache.count &&
           CMTimeCompare(sortedMarkersCache[index - 1].timestamp, currentTime) == 0 {
            index -= 1
        }

        if index > 0 {
            let prevMarker = sortedMarkersCache[index - 1]
            seek(to: prevMarker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
            selectedMarker = prevMarker
        } else if !sortedMarkersCache.isEmpty {
            // Wrap to last marker
            let lastMarker = sortedMarkersCache.last!
            seek(to: lastMarker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
            selectedMarker = lastMarker
        }
    }

    func selectMarkerAt(time: CMTime) {
        // Ensure cache is up to date
        updateMarkersCache()

        // Find the closest marker to the given time
        let closestMarker = sortedMarkersCache.min { marker1, marker2 in
            let diff1 = abs(CMTimeGetSeconds(CMTimeSubtract(marker1.timestamp, time)))
            let diff2 = abs(CMTimeGetSeconds(CMTimeSubtract(marker2.timestamp, time)))
            return diff1 < diff2
        }

        if let marker = closestMarker {
            let diff = abs(CMTimeGetSeconds(CMTimeSubtract(marker.timestamp, time)))
            // Only select if within 0.5 seconds
            if diff < 0.5 {
                selectedMarker = marker
            }
        }
    }

    func setNearestMarkerRating(_ rating: Int) {
        // Ensure cache is up to date
        updateMarkersCache()

        // Find all markers before or at current time
        let markersBefore = sortedMarkersCache.filter { marker in
            CMTimeCompare(marker.timestamp, currentTime) <= 0
        }

        // Get the closest one (last in the sorted array)
        guard let nearestMarker = markersBefore.last else {
            print("No marker found before current time")
            return
        }

        // Update the rating
        updateMarker(id: nearestMarker.id, rating: rating)
        print("Set marker at \(nearestMarker.timestamp.timeString) to \(rating) stars")
    }

    // MARK: - Marker Persistence

    private func loadMarkers() {
        guard let videoURL = videoURL else { return }

        Task {
            do {
                // Calculate video hash for unique identification
                let hash = await MarkerPersistence.shared.generateVideoHash(for: videoURL)
                await MainActor.run {
                    self.videoHash = hash
                }

                // Load existing markers from metadata file
                let loadedMarkers = try await MarkerPersistence.shared.loadMarkers(for: videoURL)

                await MainActor.run {
                    if !loadedMarkers.isEmpty {
                        markers = loadedMarkers
                        markersCacheInvalidated = true
                    }
                }
            } catch {
                print("[VideoPlayerViewModel] Failed to load markers: \(error)")
            }
        }
    }

    // MARK: - Scrubbing

    func scrubForward(with config: ScrubbingConfig) {
        if isPlaying {
            seekWhilePlaying(direction: .forward, seconds: config.playingSeekSeconds)
        } else {
            seekWhilePaused(direction: .forward, frames: config.pausedSeekFrames)
        }
    }

    func scrubBackward(with config: ScrubbingConfig) {
        if isPlaying {
            seekWhilePlaying(direction: .backward, seconds: config.playingSeekSeconds)
        } else {
            seekWhilePaused(direction: .backward, frames: config.pausedSeekFrames)
        }
    }

    // Convenience methods for backward compatibility
    func scrubForward() {
        // Use first config as default
        if let firstConfig = scrubbingSettings.configs.first {
            scrubForward(with: firstConfig)
        }
    }

    func scrubBackward() {
        // Use first config as default
        if let firstConfig = scrubbingSettings.configs.first {
            scrubBackward(with: firstConfig)
        }
    }

    private func seekWhilePlaying(direction: SeekDirection, seconds: Double) {
        let currentTime = currentTime
        let seekTime = direction == .forward
            ? CMTimeAdd(currentTime, CMTimeMakeWithSeconds(seconds, preferredTimescale: 600))
            : CMTimeSubtract(currentTime, CMTimeMakeWithSeconds(seconds, preferredTimescale: 600))

        showSeekFeedback(direction: direction, amount: "\(Int(seconds))s")

        seekManager?.seek(to: seekTime,
                         toleranceBefore: CMTimeMakeWithSeconds(0.5, preferredTimescale: 600),
                         toleranceAfter: CMTimeMakeWithSeconds(0.5, preferredTimescale: 600)) { finished in
            if finished {
                // UI will update automatically via @Published currentTime
            }
        }
    }

    private func seekWhilePaused(direction: SeekDirection, frames: Int) {
        let currentTime = currentTime
        let frameDuration = CMTimeMake(value: 1, timescale: Int32(frameRate))
        let seekOffset = CMTimeMultiply(frameDuration, multiplier: Int32(frames))

        let seekTime = direction == .forward
            ? CMTimeAdd(currentTime, seekOffset)
            : CMTimeSubtract(currentTime, seekOffset)

        let frameText = frames == 1 ? "frame" : "frames"
        showSeekFeedback(direction: direction, amount: "\(frames) \(frameText)")

        seekManager?.seek(to: seekTime,
                         toleranceBefore: .zero,
                         toleranceAfter: .zero) { finished in
            if finished {
                // UI will update automatically via @Published currentTime
            }
        }
    }

    private func clampTime(_ time: CMTime) -> CMTime {
        guard let duration = player?.currentItem?.duration,
              duration.isValid && duration.value > 0 else { return time }

        if CMTimeCompare(time, .zero) < 0 {
            return .zero
        } else if CMTimeCompare(time, duration) > 0 {
            return duration
        } else {
            return time
        }
    }

    private func showSeekFeedback(direction: SeekDirection, amount: String) {
        let symbol = direction == .forward ? "+" : "-"
        seekFeedback = SeekFeedback(text: "\(symbol)\(amount)", direction: direction)

        // Auto-hide after 1 second
        DispatchQueue.main.asyncAfter(deadline: .now() + 1.0) { [weak self] in
            self?.seekFeedback = nil
        }
    }

    // Removed manual objectWillChange.send() calls - @Published properties handle updates automatically

    // MARK: - Marker Persistence

    private func scheduleMarkerSave() {
        // Cancel any pending save
        saveWorkItem?.cancel()

        // Schedule new save after 0.5 seconds
        saveWorkItem = DispatchWorkItem { [weak self] in
            self?.saveMarkers()
        }

        if let workItem = saveWorkItem {
            DispatchQueue.main.asyncAfter(deadline: .now() + 0.5, execute: workItem)
        }
    }

    private func saveMarkers() {
        guard let videoURL = videoURL else { return }

        Task {
            do {
                // Only save if we have user-added markers (not just the start marker)
                let userMarkers = markers.filter { marker in
                    // Exclude start markers (timestamp 0 with "Start" label)
                    !(marker.timestamp == .zero && marker.labels.contains("Start"))
                }

                // Only create/update metadata file if we have actual user markers
                if !userMarkers.isEmpty {
                    try await MarkerPersistence.shared.saveMarkers(markers, for: videoURL)
                } else {
                    // If we only have the start marker, delete the metadata file if it exists
                    if await MarkerPersistence.shared.shouldDeleteMetadataFile(markers: markers) {
                        try await MarkerPersistence.shared.deleteMarkers(for: videoURL)
                    }
                }
            } catch {
                await MainActor.run {
                    playerError = PlayerError.markerSaveFailed(error.localizedDescription)
                }
            }
        }
    }
}

// MARK: - Helper Types

struct SeekFeedback: Equatable {
    let text: String
    let direction: SeekDirection
    let id = UUID()

    static func == (lhs: SeekFeedback, rhs: SeekFeedback) -> Bool {
        lhs.id == rhs.id
    }
}

