import Foundation
import AVFoundation

struct Segment: Identifiable {
    let id: UUID
    let startMarker: Marker?
    let endMarker: Marker?

    var duration: CMTime {
        let startTime = startMarker?.timestamp ?? .zero
        let endTime = endMarker?.timestamp ?? .positiveInfinity

        if endTime == .positiveInfinity {
            return .positiveInfinity
        }

        return CMTimeSubtract(endTime, startTime)
    }

    var averageRating: Double {
        // Segment rating is determined by the marker at the start of the segment
        if let startRating = startMarker?.rating {
            return Double(startRating)
        }
        // Default rating for segments with no start marker (beginning of video)
        return 3.0
    }

    init(id: UUID = UUID(), startMarker: Marker? = nil, endMarker: Marker? = nil) {
        self.id = id
        self.startMarker = startMarker
        self.endMarker = endMarker
    }
}

