    /// Get all keys associated with this definition
    var associatedKeys: [String] {
        switch type {
        case .simple(let key, _):
            return [key]
        case .scrubbing(let config):
            return [config.forwardKey, config.backwardKey].compactMap { $0 }
        case .rating(let number):
            return [String(number)]
        case .volume(let direction):
            let key = direction == .up ? DefaultShortcuts.volumeUpKey : DefaultShortcuts.volumeDownKey
            return [key]
        case .navigation:
            return [] // Customized at runtime
        case .custom(let key, _, _):
            return [key]
        }
    }
}

