import SwiftUI

/// Defines a keyboard shortcut with its metadata and behavior
struct ShortcutDefinition: Identifiable, Equatable {
    let id: String
    let name: String
    let category: ShortcutCategory
    let type: ShortcutType
    let isCustomizable: Bool
    let action: ShortcutAction

    /// The action to perform when the shortcut is triggered
    enum ShortcutAction: Equatable {
        case playPause
        case addMarker
        case previousMarker
        case nextMarker
        case deleteMarker
        case editMarker
        case openSettings
        case openFile
        case togglePerformanceOverlay
        case toggleMarkerList
        case setRating(Int)
        case adjustVolume(VolumeDirection)
        case navigate(NavigationDirection)
        case scrub(ScrubbingConfig, SeekDirection)
    }

    // MARK: - Factory Methods

    /// Creates a simple shortcut definition
    static func simple(
        _ id: String,
        name: String,
        category: ShortcutCategory,
        defaultKey: String,
        modifiers: EventModifiers = [],
        action: ShortcutAction,
        isCustomizable: Bool = true
    ) -> ShortcutDefinition {
        ShortcutDefinition(
            id: id,
            name: name,
            category: category,
            type: .simple(key: defaultKey, modifiers: modifiers),
            isCustomizable: isCustomizable,
            action: action
        )
    }

    /// Creates a scrubbing shortcut definition
    static func scrubbing(_ config: ScrubbingConfig) -> ShortcutDefinition {
        ShortcutDefinition(
            id: "scrub_\(config.id.uuidString)",
            name: config.name,
            category: .scrubbing,
            type: .scrubbing(config: config),
            isCustomizable: true,
            action: .scrub(config, .forward) // Direction determined at runtime
        )
    }

    /// Creates a rating shortcut definition
    static func rating(_ number: Int) -> ShortcutDefinition {
        ShortcutDefinition(
            id: "rating_\(number)",
            name: "Set Rating \(number)",
            category: .rating,
            type: .rating(number: number),
            isCustomizable: false,
            action: .setRating(number)
        )
    }

    /// Creates a volume shortcut definition
    static func volume(_ direction: VolumeDirection) -> ShortcutDefinition {
        let name = direction == .up ? "Volume Up" : "Volume Down"

        return ShortcutDefinition(
            id: "volume_\(direction.rawValue)",
            name: name,
            category: .volume,
            type: .volume(direction: direction),
            isCustomizable: true,
            action: .adjustVolume(direction)
        )
    }

    /// Creates a navigation shortcut definition
    static func navigation(_ direction: NavigationDirection, defaultKey: String) -> ShortcutDefinition {
        let name = direction == .next ? "Next Video in Directory" : "Previous Video in Directory"

        return ShortcutDefinition(
            id: "navigate_\(direction.rawValue)",
            name: name,
            category: .navigation,
            type: .navigation(direction: direction),
            isCustomizable: true,
            action: .navigate(direction)
        )
    }

    /// Get all keys associated with this definition
    var associatedKeys: [String] {
        switch type {
        case .simple(let key, _):
            return [key]
        case .scrubbing(let config):
            return [config.forwardKey, config.backwardKey].compactMap { $0 }
        case .rating(let number):
            return [String(number)]
        case .volume(let direction):
            let key = direction == .up ? DefaultShortcuts.volumeUpKey : DefaultShortcuts.volumeDownKey
            return [key]
        case .navigation:
            return [] // Customized at runtime
        case .custom(let key, _, _):
            return [key]
        }
    }
}

