        self._showingExtractionModal = showingExtractionModal
        self.onNavigateNext = onNavigateNext
        self.onNavigatePrevious = onNavigatePrevious
        self.onOpenFile = onOpenFile
        self._shortcutRegistry = StateObject(wrappedValue: ShortcutRegistry(settingsViewModel: settingsViewModel))
    }

    // Get cached keyboard shortcuts from registry with O(1) lookup
    private var keyboardShortcuts: [String: KeyboardShortcut] {
        shortcutRegistry.getCachedShortcuts(
            viewModel: viewModel,
            showingSettings: $showingSettings,
            selectedMarkerForEdit: $selectedMarkerForEdit,
            showingMarkerList: $showingMarkerList,
            onNavigateNext: onNavigateNext,
            onNavigatePrevious: onNavigatePrevious,
            onOpenFile: onOpenFile
        )
    }

    var body: some View {
        let baseView = Color.clear
            .focusable()
            .focused($isFocused)
            .onAppear {
                isFocused = true
            }

        let withChangeHandlers = baseView
            .onChange(of: settingsViewModel.scrubbingConfigs) { _, newConfigs in
                // Update registry when scrubbing configs change
                shortcutRegistry.updateScrubbingConfigs(newConfigs)
            }
            .onChange(of: settingsViewModel.playPauseKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.addMarkerKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.previousMarkerKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.nextMarkerKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.deleteMarkerKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.editMarkerKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.openSettingsKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.nextVideoInDirectoryKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.previousVideoInDirectoryKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }

        return withChangeHandlers
            // Unified keyboard shortcut handling
            .onKeyPress { keyPress in
                let timestamp = Date().timeIntervalSince1970
                let identifier = KeyboardShortcut.identifier(from: keyPress)

                // Log all key presses with Unicode for debugging
                let unicodeValues = keyPress.characters.unicodeScalars.map { String($0.value, radix: 16) }.joined(separator: " ")
                print("[\(String(format: "%.3f", timestamp))] Key pressed: '\(keyPress.characters)' (Unicode: \(unicodeValues)) modifiers: \(keyPress.modifiers) identifier: '\(identifier)'")

                // Debug: Print available shortcuts if space is pressed
                if keyPress.characters == " " {
                    print("DEBUG: Space key detected. Available shortcuts:")
                    for (id, shortcut) in keyboardShortcuts {
                        print("  - ID: '\(id)', Key: '\(shortcut.key)', Description: \(shortcut.description)")
                    }
                    print("DEBUG: playPauseKey from settings: '\(settingsViewModel.playPauseKey)'")
                    print("DEBUG: Looking for identifier: '\(identifier)'")
                }

                // Look up shortcut in registry
                if let shortcut = keyboardShortcuts[identifier] {
                    print("[\(String(format: "%.3f", timestamp))] Shortcut triggered: \(shortcut.description)")
                    shortcut.action()
                    return .handled
                }

                print("[\(String(format: "%.3f", timestamp))] Key '\(keyPress.characters)' ignored - no matching shortcut")
                return .ignored
            }
    }
}

