            defaultKey: DefaultShortcuts.volumeDown.key,
            modifiers: DefaultShortcuts.volumeDown.eventModifiers,
            action: .adjustVolume(.down)
        ))

        // MARK: - Rating Shortcuts
        for rating in 1...5 {
            definitions.append(.rating(rating))
        }

        // MARK: - Scrubbing Shortcuts
        for config in settingsViewModel.scrubbingConfigs {
            definitions.append(.scrubbing(config))
        }
    }

    /// Load user customizations from settings
    func loadCustomizations() {
        customizations["playPause"] = settingsViewModel.playPauseKey
        customizations["addMarker"] = settingsViewModel.addMarkerKey
        customizations["previousMarker"] = settingsViewModel.previousMarkerKey
        customizations["nextMarker"] = settingsViewModel.nextMarkerKey
        customizations["deleteMarker"] = settingsViewModel.deleteMarkerKey
        customizations["editMarker"] = settingsViewModel.editMarkerKey
        customizations["openSettings"] = settingsViewModel.openSettingsKey
        customizations["openFile"] = DefaultShortcuts.openKey
        customizations["navigate_next"] = settingsViewModel.nextVideoInDirectoryKey
        customizations["navigate_previous"] = settingsViewModel.previousVideoInDirectoryKey
    }

    /// Build keyboard shortcuts for runtime use
    func buildKeyboardShortcuts(
        viewModel: VideoPlayerViewModel,
        showingSettings: Binding<Bool>,
        selectedMarkerForEdit: Binding<Marker?>,
        showingMarkerList: Binding<Bool>,
        showingExtractionModal: Binding<Bool>,
        onNavigateNext: @escaping () -> Void,
        onNavigatePrevious: @escaping () -> Void,
        onOpenFile: @escaping () -> Void
    ) -> [String: KeyboardShortcut] {
        var registry: [String: KeyboardShortcut] = [:]

        for definition in definitions {
            let shortcuts = createShortcuts(
                for: definition,
                viewModel: viewModel,
                showingSettings: showingSettings,
                selectedMarkerForEdit: selectedMarkerForEdit,
                showingMarkerList: showingMarkerList,
                showingExtractionModal: showingExtractionModal,
                onNavigateNext: onNavigateNext,
                onNavigatePrevious: onNavigatePrevious,
                onOpenFile: onOpenFile
            )

            for shortcut in shortcuts {
                registry[shortcut.identifier] = shortcut
                // Debug logging for play/pause shortcut
                if definition.id == "playPause" {
                    print("DEBUG: PlayPause shortcut - key: '\(shortcut.key)' (Unicode: \(shortcut.key.unicodeScalars.map { String($0.value, radix: 16) }.joined(separator: " "))), identifier: '\(shortcut.identifier)'")
                }
            }
        }

        return registry
    }

    /// Create keyboard shortcuts from a definition
    private func createShortcuts(
        for definition: ShortcutDefinition,
        viewModel: VideoPlayerViewModel,
        showingSettings: Binding<Bool>,
        selectedMarkerForEdit: Binding<Marker?>,
        showingMarkerList: Binding<Bool>,
        showingExtractionModal: Binding<Bool>,
        onNavigateNext: @escaping () -> Void,
        onNavigatePrevious: @escaping () -> Void,
        onOpenFile: @escaping () -> Void
    ) -> [KeyboardShortcut] {

        switch definition.type {
        case .simple(let key, let modifiers):
            let actualKey = customizations[definition.id] ?? key
            let action = createAction(
                for: definition.action,
                viewModel: viewModel,
                showingSettings: showingSettings,
                selectedMarkerForEdit: selectedMarkerForEdit,
                showingMarkerList: showingMarkerList,
                showingExtractionModal: showingExtractionModal,
                onNavigateNext: onNavigateNext,
                onNavigatePrevious: onNavigatePrevious,
                onOpenFile: onOpenFile
            )

            return [KeyboardShortcut(
                key: actualKey,
                modifiers: modifiers,
                description: definition.name,
                action: action
            )]

        case .scrubbing(let config):
            var shortcuts: [KeyboardShortcut] = []

            if let forwardKey = config.forwardKey {
                shortcuts.append(KeyboardShortcut(
                    key: forwardKey,
                    modifiers: [],
                    description: "Scrub Forward - \(config.name)",
                    action: { [weak viewModel] in viewModel?.scrubForward(with: config) }
                ))
            }

            if let backwardKey = config.backwardKey {
                shortcuts.append(KeyboardShortcut(
                    key: backwardKey,
                    modifiers: [],
                    description: "Scrub Backward - \(config.name)",
                    action: { [weak viewModel] in viewModel?.scrubBackward(with: config) }
                ))
            }

            return shortcuts

        case .rating(let number):
            return [KeyboardShortcut(
                key: String(number),
                modifiers: [],
                description: definition.name,
                action: { [weak viewModel] in viewModel?.setNearestMarkerRating(number) }
            )]

        case .volume(let direction):
            let key = direction == .up ? DefaultShortcuts.volumeUpKey : DefaultShortcuts.volumeDownKey
            return [KeyboardShortcut(
                key: key,
                modifiers: [],
                description: definition.name,
                action: { [weak viewModel] in
                    direction == .up ? viewModel?.increaseVolume() : viewModel?.decreaseVolume()
                }
            )]

        case .navigation(let direction):
            let key = customizations["navigate_\(direction.rawValue)"] ?? ""
            guard !key.isEmpty else { return [] }

            // Handle both single key and modifier versions
            var shortcuts: [KeyboardShortcut] = []

            if key.contains("âŒ˜") {
                let actualKey = key.replacingOccurrences(of: "âŒ˜", with: "")
                shortcuts.append(KeyboardShortcut(
                    key: actualKey,
                    modifiers: .command,
                    description: definition.name,
                    action: direction == .next ? onNavigateNext : onNavigatePrevious
                ))
            } else {
                shortcuts.append(KeyboardShortcut(
                    key: key,
                    modifiers: [],
                    description: definition.name,
                    action: direction == .next ? onNavigateNext : onNavigatePrevious
                ))
            }

            return shortcuts

        case .custom:
            return [] // Handle custom shortcuts if needed
        }
    }

    /// Create action closure for a shortcut action
    private func createAction(
        for action: ShortcutDefinition.ShortcutAction,
        viewModel: VideoPlayerViewModel,
        showingSettings: Binding<Bool>,
        selectedMarkerForEdit: Binding<Marker?>,
        showingMarkerList: Binding<Bool>,
        showingExtractionModal: Binding<Bool>,
        onNavigateNext: @escaping () -> Void,
        onNavigatePrevious: @escaping () -> Void,
        onOpenFile: @escaping () -> Void
    ) -> () -> Void {

        switch action {
        case .playPause:
            return { [weak viewModel] in viewModel?.togglePlayPause() }
        case .addMarker:
            return { [weak viewModel] in viewModel?.addMarker() }
        case .previousMarker:
            return { [weak viewModel] in viewModel?.jumpToPreviousMarker() }
        case .nextMarker:
            return { [weak viewModel] in viewModel?.jumpToNextMarker() }
        case .deleteMarker:
            return { [weak viewModel] in viewModel?.deleteNearestMarker() }
        case .editMarker:
            return { [weak viewModel] in
                if let marker = viewModel?.selectedMarker {
                    selectedMarkerForEdit.wrappedValue = marker
                }
            }
        case .openSettings:
            return { showingSettings.wrappedValue = true }
        case .openFile:
            return onOpenFile
        case .togglePerformanceOverlay:
            return { [weak viewModel] in viewModel?.togglePerformanceOverlay() }
        case .toggleMarkerList:
            return { showingMarkerList.wrappedValue.toggle() }
        case .extractCurrentSegment:
            return { showingExtractionModal.wrappedValue = true }
        case .setRating(let rating):
            return { [weak viewModel] in viewModel?.setNearestMarkerRating(rating) }
        case .adjustVolume(let direction):
            return { [weak viewModel] in
                direction == .up ? viewModel?.increaseVolume() : viewModel?.decreaseVolume()
            }
        case .navigate(let direction):
            return direction == .next ? onNavigateNext : onNavigatePrevious
        case .scrub:
            return {} // Handled separately in scrubbing case
        }
    }

    /// Update scrubbing configurations
    func updateScrubbingConfigs(_ configs: [ScrubbingConfig]) {
        // Remove old scrubbing definitions
        definitions.removeAll { definition in
            if case .scrubbing = definition.type {
                return true
            }
            return false
        }

        // Add new scrubbing definitions
        for config in configs {
            definitions.append(.scrubbing(config))
        }

        // Invalidate cache
        cacheNeedsUpdate = true
    }

    /// Refresh all shortcuts from settings
    func refreshShortcuts() {
        loadCustomizations()
        cacheNeedsUpdate = true
        objectWillChange.send()
    }

    /// Get cached keyboard shortcuts with O(1) lookup
    func getCachedShortcuts(
        viewModel: VideoPlayerViewModel,
        showingSettings: Binding<Bool>,
        selectedMarkerForEdit: Binding<Marker?>,
        showingMarkerList: Binding<Bool>,
        showingExtractionModal: Binding<Bool>,
        onNavigateNext: @escaping () -> Void,
        onNavigatePrevious: @escaping () -> Void,
        onOpenFile: @escaping () -> Void
    ) -> [String: KeyboardShortcut] {
        // Check if we need to rebuild cache
        if cacheNeedsUpdate || lastViewModel !== viewModel {
            // Store dependencies
            lastViewModel = viewModel
            lastShowingSettings = showingSettings
            lastSelectedMarkerForEdit = selectedMarkerForEdit
            lastShowingMarkerList = showingMarkerList
            lastOnNavigateNext = onNavigateNext
            lastOnNavigatePrevious = onNavigatePrevious
            lastOnOpenFile = onOpenFile

            // Rebuild cache
            shortcutCache = buildKeyboardShortcuts(
                viewModel: viewModel,
                showingSettings: showingSettings,
                selectedMarkerForEdit: selectedMarkerForEdit,
                showingMarkerList: showingMarkerList,
                onNavigateNext: onNavigateNext,
                onNavigatePrevious: onNavigatePrevious,
                onOpenFile: onOpenFile
            )
            cacheNeedsUpdate = false
        }

        return shortcutCache
    }

    /// Get customized shortcut spec for a given ID
    private func getCustomizedSpec(for id: String) -> ShortcutSpec? {
        // Check if there's a full spec customization stored
        // For now, return nil - this can be enhanced to support full spec customization
        return nil
    }
}

