    struct ExtractionTask: Identifiable {
        let id = UUID()
        let segment: Segment
        let outputPath: String
        var status: ExtractionStatus = .pending
        var progress: Double = 0.0
        var error: Error?
    }

    enum ExtractionStatus {
        case pending
        case inProgress
        case completed
        case failed
        case cancelled
    }

    // MARK: - Properties

    @Published var isExtracting = false
    @Published var currentProgress: Double = 0.0
    @Published var currentTask: ExtractionTask?
    @Published var extractionQueue: [ExtractionTask] = []
    @Published var completedExtractions: [ExtractionTask] = []
    @Published var outputDirectory: URL?

    private let ffmpegManager = FFmpegManager()
    private var cancellables = Set<AnyCancellable>()
    private var extractionWorkItem: DispatchWorkItem?

    // Progress tracking
    private let progressSubject = PassthroughSubject<Double, Never>()
    var progressPublisher: AnyPublisher<Double, Never> {
        progressSubject.eraseToAnyPublisher()
    }

    // MARK: - Initialization

    init() {
        setupProgressTracking()
        loadOutputDirectory()
    }

    // MARK: - Public Methods

    /// Extract a single segment from the video
    /// - Parameters:
    ///   - segment: The segment to extract
    ///   - videoPath: Path to the source video
    ///   - fps: Frame rate of the video
    /// - Returns: Result with the output file path or error
    @MainActor
    func extractSegment(
        _ segment: Segment,
        from videoPath: String,
        fps: Double
    ) async -> Result<String, ExtractionError> {

        // Validate segment
        guard let startTime = segment.startMarker?.timestamp,
              let endTime = segment.endMarker?.timestamp,
              startTime.isValidSegmentTime,
              endTime.isValidSegmentTime else {
            return .failure(.invalidSegment)
        }

        // Check output directory
        guard let outputDir = outputDirectory else {
            return .failure(.outputDirectoryNotSet)
        }

        // Generate output filename
        let outputPath = generateOutputPath(
            for: segment,
            videoPath: videoPath,
            outputDirectory: outputDir
        )

        // Create extraction task
        var task = ExtractionTask(segment: segment, outputPath: outputPath)
        task.status = .inProgress

        await MainActor.run {
            self.currentTask = task
            self.isExtracting = true
            self.currentProgress = 0.0
        }

        // Calculate segment duration
        let duration = startTime.segmentDuration(to: endTime, fps: fps)
        let startSeconds = CMTimeGetSeconds(startTime)
        let durationSeconds = CMTimeGetSeconds(duration)

        // Perform extraction
        let result = await ffmpegManager.extractSegment(
            from: videoPath,
            to: outputPath,
            startTime: startSeconds,
            duration: durationSeconds
        )

        // Update task status
        switch result {
        case .success:
            task.status = .completed
            await MainActor.run {
                self.completedExtractions.append(task)
                self.currentTask = nil
                self.isExtracting = false
                self.currentProgress = 100.0
            }
            return .success(outputPath)

        case .failure(let error):
            task.status = .failed
            task.error = error
            await MainActor.run {
                self.currentTask = nil
                self.isExtracting = false
                self.currentProgress = 0.0
            }
            return .failure(.extractionFailed(error.localizedDescription))
        }
    }

    /// Extract multiple segments in batch
    /// - Parameters:
    ///   - segments: Array of segments to extract
    ///   - videoPath: Path to the source video
    ///   - fps: Frame rate of the video
    /// - Returns: Array of results for each extraction
    @MainActor
    func extractSegments(
        _ segments: [Segment],
        from videoPath: String,
        fps: Double
    ) async -> [(segment: Segment, result: Result<String, ExtractionError>)] {

        var results: [(Segment, Result<String, ExtractionError>)] = []

        // Create tasks for all segments
        let tasks = segments.compactMap { segment -> ExtractionTask? in
            guard let outputDir = outputDirectory else { return nil }

            let outputPath = generateOutputPath(
                for: segment,
                videoPath: videoPath,
                outputDirectory: outputDir
            )

            return ExtractionTask(segment: segment, outputPath: outputPath)
        }

        extractionQueue = tasks

        // Process each segment
        for (index, task) in tasks.enumerated() {
            // Check for cancellation
            if extractionWorkItem?.isCancelled == true {
                let cancelResult = (task.segment, Result<String, ExtractionError>.failure(.cancelled))
                results.append(cancelResult)
                continue
            }

            // Update progress
            let overallProgress = Double(index) / Double(tasks.count) * 100
            await MainActor.run {
                self.currentProgress = overallProgress
            }

            // Extract segment
            let result = await extractSegment(task.segment, from: videoPath, fps: fps)
            results.append((task.segment, result))
        }

        // Clear queue
        await MainActor.run {
            self.extractionQueue.removeAll()
            self.currentProgress = 100.0
        }

        return results
    }

    /// Cancel ongoing extraction
    func cancelExtraction() {
        ffmpegManager.cancelExtraction()
        extractionWorkItem?.cancel()

        if var task = currentTask {
            task.status = .cancelled
            currentTask = task
        }

        isExtracting = false
        currentProgress = 0.0
    }

    /// Set the output directory for extracted segments
    func setOutputDirectory(_ url: URL) {
        outputDirectory = url
        saveOutputDirectory()
    }

    /// Get segments filtered by minimum rating
    func getSegmentsByRating(from segments: [Segment], minimumRating: Int) -> [Segment] {
        segments.filter { segment in
            segment.averageRating >= Double(minimumRating)
        }
    }

    // MARK: - Private Methods

    private func setupProgressTracking() {
        // Subscribe to FFmpeg progress updates
        ffmpegManager.progressPublisher
            .receive(on: DispatchQueue.main)
            .sink { [weak self] progress in
                self?.currentProgress = progress.percentage
                self?.progressSubject.send(progress.percentage)
            }
            .store(in: &cancellables)
    }

    private func generateOutputPath(
        for segment: Segment,
        videoPath: String,
        outputDirectory: URL
    ) -> String {
        let videoURL = URL(fileURLWithPath: videoPath)
        let baseName = videoURL.deletingPathExtension().lastPathComponent
        let fileExtension = videoURL.pathExtension

        // Create descriptive filename
        var filename = "\(baseName)_segment"

        // Add time range
        if let startTime = segment.startMarker?.timestamp,
           let endTime = segment.endMarker?.timestamp {
            let startId = startTime.segmentIdentifier
            let endId = endTime.segmentIdentifier
            filename += "_\(startId)-\(endId)"
        }

        // Add rating if available
        if let rating = segment.startMarker?.rating {
            filename += "_\(rating)star"
        }

        // Add first label if available
        if let label = segment.startMarker?.labels.first {
            let sanitizedLabel = label
                .replacingOccurrences(of: " ", with: "_")
                .replacingOccurrences(of: "/", with: "-")
            filename += "_\(sanitizedLabel)"
        }

        filename += ".\(fileExtension)"

        return outputDirectory.appendingPathComponent(filename).path
    }

    // MARK: - Persistence

    private func loadOutputDirectory() {
        if let bookmarkData = UserDefaults.standard.data(forKey: "SegmentExtractionOutputDirectory"),
           let url = try? URL(resolvingBookmarkData: bookmarkData, bookmarkDataIsStale: nil) {
            outputDirectory = url
        }
    }

    private func saveOutputDirectory() {
        guard let outputDirectory = outputDirectory,
              let bookmarkData = try? outputDirectory.bookmarkData() else { return }

        UserDefaults.standard.set(bookmarkData, forKey: "SegmentExtractionOutputDirectory")
    }
}

// MARK: - Convenience Extensions

extension SegmentExtractionManager {

    /// Extract the current segment based on playhead position
    /// - Parameters:
    ///   - currentTime: Current playhead time
    ///   - markers: All markers in the video
    ///   - videoPath: Path to the video
    ///   - fps: Frame rate
    /// - Returns: Result with output path or error
    @MainActor
    func extractCurrentSegment(
        at currentTime: CMTime,
        markers: [Marker],
        videoPath: String,
        fps: Double
    ) async -> Result<String, ExtractionError> {

        // Find the segment containing the current time
        guard let segment = findSegment(at: currentTime, from: markers) else {
            return .failure(.invalidSegment)
        }

        return await extractSegment(segment, from: videoPath, fps: fps)
    }

    private func findSegment(at time: CMTime, from markers: [Marker]) -> Segment? {
        let sortedMarkers = markers.sorted { $0.timestamp < $1.timestamp }

        // Find markers before and after the current time
        var startMarker: Marker?
        var endMarker: Marker?

        for i in 0..<sortedMarkers.count {
            if sortedMarkers[i].timestamp <= time {
                startMarker = sortedMarkers[i]
            } else {
                endMarker = sortedMarkers[i]
                break
            }
        }

        // Handle edge cases
        if startMarker == nil && !sortedMarkers.isEmpty {
            // Current time is before first marker
            endMarker = sortedMarkers.first
        }

        return Segment(startMarker: startMarker, endMarker: endMarker)
    }
}

