import Testing
import AVFoundation
@testable import SwiftMedia

struct VideoPlayerViewModelTests {

    @Test func markerOperations() async throws {
        let viewModel = VideoPlayerViewModel()

        // Add a marker
        viewModel.currentTime = CMTime(seconds: 10, preferredTimescale: 600)
        viewModel.addMarker()

        #expect(viewModel.markers.count == 1)
        #expect(viewModel.markers.first?.timestamp.seconds == 10)

        // Add another marker
        viewModel.currentTime = CMTime(seconds: 20, preferredTimescale: 600)
        viewModel.addMarker()

        #expect(viewModel.markers.count == 2)

        // Delete marker
        if let firstMarker = viewModel.markers.first {
            viewModel.deleteMarker(id: firstMarker.id)
            #expect(viewModel.markers.count == 1)
        }

        // Update marker
        if var remainingMarker = viewModel.markers.first {
            remainingMarker.rating = 5
            remainingMarker.labels = ["test", "updated"]
            viewModel.updateMarker(remainingMarker)

            #expect(viewModel.markers.first?.rating == 5)
            #expect(viewModel.markers.first?.labels == ["test", "updated"])
        }
    }

    @Test func markerNavigation() async throws {
        let viewModel = VideoPlayerViewModel()

        // Create markers at specific times
        viewModel.markers = [
            Marker(timestamp: CMTime(seconds: 10, preferredTimescale: 600), rating: 3, labels: []),
            Marker(timestamp: CMTime(seconds: 20, preferredTimescale: 600), rating: 4, labels: []),
            Marker(timestamp: CMTime(seconds: 30, preferredTimescale: 600), rating: 5, labels: [])
        ]

        // Test jump to next marker
        viewModel.currentTime = CMTime(seconds: 5, preferredTimescale: 600)
        viewModel.jumpToNextMarker()
        // Note: In real test, we'd need to mock player seek behavior

        // Test jump to previous marker
        viewModel.currentTime = CMTime(seconds: 25, preferredTimescale: 600)
        viewModel.jumpToPreviousMarker()

        // Test delete nearest marker
        viewModel.currentTime = CMTime(seconds: 19, preferredTimescale: 600)
        let initialCount = viewModel.markers.count
        viewModel.deleteNearestMarker()
        #expect(viewModel.markers.count == initialCount - 1)
    }

    @Test func segmentCalculation() async throws {
        let viewModel = VideoPlayerViewModel()

        // Create markers for segments
        viewModel.markers = [
            Marker(timestamp: CMTime(seconds: 10, preferredTimescale: 600), rating: 2, labels: []),
            Marker(timestamp: CMTime(seconds: 20, preferredTimescale: 600), rating: 4, labels: []),
            Marker(timestamp: CMTime(seconds: 30, preferredTimescale: 600), rating: 5, labels: [])
        ]
        viewModel.duration = CMTime(seconds: 40, preferredTimescale: 600)

        let segments = viewModel.segments

        // Should have 4 segments (before first, between markers, after last)
        #expect(segments.count == 4)

        // Check first segment (0-10s)
        #expect(segments[0].startMarker == nil)
        #expect(segments[0].endMarker?.timestamp.seconds == 10)

        // Check middle segment (10-20s)
        #expect(segments[1].startMarker?.timestamp.seconds == 10)
        #expect(segments[1].endMarker?.timestamp.seconds == 20)
        #expect(segments[1].averageRating == 3.0) // (2+4)/2

        // Check last segment (30-40s)
        #expect(segments[3].startMarker?.timestamp.seconds == 30)
        #expect(segments[3].endMarker == nil)
    }

    @Test func scrubbingConfiguration() async throws {
        let viewModel = VideoPlayerViewModel()

        // Test all configs are available
        #expect(viewModel.scrubbingSettings.configs.count >= 1)

        // Test default config
        let defaultConfig = viewModel.scrubbingSettings.configs.first
        #expect(defaultConfig?.name == "Default")
        #expect(defaultConfig?.forwardKey == "")
        #expect(defaultConfig?.backwardKey == "")
        #expect(defaultConfig?.playingSeekSeconds == 5.0)
        #expect(defaultConfig?.pausedSeekFrames == 1)

        // Test config matching by key
        #expect(viewModel.scrubbingSettings.configForKey("") != nil)
        #expect(viewModel.scrubbingSettings.configForKey("") != nil)

        // Test scrubbing with specific config
        if let config = viewModel.scrubbingSettings.configs.first {
            viewModel.scrubForward(with: config)
            viewModel.scrubBackward(with: config)
        }
    }

    @Test func performanceMetrics() async throws {
        let viewModel = VideoPlayerViewModel()

        // Test marker addition performance
        let startTime = Date()
        for i in 0..<1000 {
            viewModel.currentTime = CMTime(seconds: Double(i), preferredTimescale: 600)
            viewModel.addMarker()
        }
        let endTime = Date()
        let elapsedTime = endTime.timeIntervalSince(startTime)

        // Should be able to add 1000 markers in under 1 second
        #expect(elapsedTime < 1.0)
        #expect(viewModel.markers.count == 1000)

        // Test segment calculation performance with many markers
        let segmentStartTime = Date()
        _ = viewModel.segments
        let segmentEndTime = Date()
        let segmentTime = segmentEndTime.timeIntervalSince(segmentStartTime)

        // Segment calculation should be fast even with many markers
        #expect(segmentTime < 0.1)
    }
}

