//
//  ProgressBarView.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import SwiftUI
import AVFoundation

struct ProgressBarView: View {
    @ObservedObject var viewModel: VideoPlayerViewModel
    @Binding var selectedMarkerForEdit: Marker?

    @State private var isDragging = false
    @State private var dragProgress: Double = 0
    @State private var isHovering = false
    @State private var hoveredMarker: Marker?

    var progress: Double {
        guard viewModel.duration.seconds > 0 else { return 0 }

        if isDragging {
            return dragProgress
        }

        return viewModel.currentTime.seconds / viewModel.duration.seconds
    }

    var body: some View {
        GeometryReader { geometry in
            ZStack(alignment: .leading) {
                // Background
                Rectangle()
                    .fill(Color.black.opacity(0.7))
                    .frame(height: 38)

                // Segment shading
                ForEach(viewModel.segments) { segment in
                    if let startTime = segment.startMarker?.timestamp ?? (segment.id == viewModel.segments.first?.id ? CMTime.zero : nil),
                       let endTime = segment.endMarker?.timestamp ?? (segment.id == viewModel.segments.last?.id ? viewModel.duration : nil) {
                        Rectangle()
                            .fill(ratingGradient(for: segment.averageRating))
                            .opacity(0.15)
                            .frame(
                                width: geometry.size.width * (endTime.position(in: viewModel.duration) - startTime.position(in: viewModel.duration)),
                                height: 38
                            )
                            .offset(x: geometry.size.width * startTime.position(in: viewModel.duration))
                    }
                }

                // Progress fill
                Rectangle()
                    .fill(Color.white.opacity(0.8))
                    .frame(width: geometry.size.width * progress, height: 38)
                    .animation(.linear(duration: isDragging ? 0 : 0.033), value: progress)

                // Markers
                ForEach(viewModel.markers) { marker in
                    MarkerView(
                        marker: marker,
                        isSelected: viewModel.selectedMarker?.id == marker.id,
                        isHovered: hoveredMarker?.id == marker.id,
                        position: marker.timestamp.position(in: viewModel.duration),
                        geometry: geometry
                    )
                    .onHover { hovering in
                        hoveredMarker = hovering ? marker : nil
                    }
                    .onTapGesture {
                        viewModel.selectedMarker = marker
                        viewModel.seek(to: marker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
                    }
                    .onTapGesture(count: 2) {
                        selectedMarkerForEdit = marker
                    }
                }

                // Time display overlay
                HStack {
                    Spacer()

                    Text(timeDisplay)
                        .font(.system(size: 14, weight: .medium, design: .monospaced))
                        .foregroundColor(.white)
                        .shadow(color: .black.opacity(0.5), radius: 2, x: 0, y: 1)

                    Spacer()
                }
                .frame(height: 38)

                // Marker tooltip
                if let marker = hoveredMarker {
                    MarkerTooltip(marker: marker, position: marker.timestamp.position(in: viewModel.duration), geometry: geometry)
                }
            }
            .brightness(isHovering ? 0.1 : 0)
            .onHover { hovering in
                isHovering = hovering
            }
            .onTapGesture(coordinateSpace: .local) { location in
                let progress = location.x / geometry.size.width
                seekToProgress(progress)
            }
            .gesture(
                DragGesture(coordinateSpace: .local)
                    .onChanged { value in
                        isDragging = true
                        let progress = max(0, min(1, value.location.x / geometry.size.width))
                        dragProgress = progress
                    }
                    .onEnded { value in
                        let progress = max(0, min(1, value.location.x / geometry.size.width))
                        seekToProgress(progress)
                        isDragging = false
                    }
            )
        }
        .frame(height: 38)
    }

    private var timeDisplay: String {
        let currentSeconds = isDragging ?
            viewModel.duration.seconds * dragProgress :
            viewModel.currentTime.seconds

        let currentTime = CMTime(seconds: currentSeconds, preferredTimescale: 600)

        return "\(currentTime.timeString) / \(viewModel.duration.timeString)"
    }

    private func seekToProgress(_ progress: Double) {
        let targetTime = CMTime(
            seconds: viewModel.duration.seconds * progress,
            preferredTimescale: viewModel.duration.timescale
        )

        viewModel.seek(to: targetTime, toleranceBefore: .zero, toleranceAfter: .zero)
    }

    private func ratingGradient(for rating: Double) -> LinearGradient {
        let normalizedRating = (rating - 1) / 4 // Convert 1-5 to 0-1
        let color = Color(
            red: 1.0 - normalizedRating,
            green: normalizedRating,
            blue: 0
        )
        return LinearGradient(
            colors: [color, color.opacity(0.5)],
            startPoint: .top,
            endPoint: .bottom
        )
    }
}

// MARK: - Marker View
struct MarkerView: View {
    let marker: Marker
    let isSelected: Bool
    let isHovered: Bool
    let position: CGFloat
    let geometry: GeometryProxy

    var markerColor: Color {
        let normalized = CGFloat(marker.rating - 1) / 4.0
        return Color(
            red: 1.0 - normalized,
            green: normalized,
            blue: 0
        )
    }

    var body: some View {
        Rectangle()
            .fill(markerColor)
            .frame(width: 2, height: isHovered || isSelected ? 46 : 42)
            .shadow(color: .black.opacity(0.5), radius: 2)
            .offset(x: geometry.size.width * position - 1, y: isHovered || isSelected ? -4 : -2)
            .animation(.easeInOut(duration: 0.1), value: isHovered)
            .animation(.easeInOut(duration: 0.1), value: isSelected)
    }
}

// MARK: - Marker Tooltip
struct MarkerTooltip: View {
    let marker: Marker
    let position: CGFloat
    let geometry: GeometryProxy

    var body: some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(marker.timestamp.timeString)
                .font(.system(size: 12, weight: .medium, design: .monospaced))

            HStack(spacing: 2) {
                ForEach(0..<5) { index in
                    Image(systemName: index < marker.rating ? "star.fill" : "star")
                        .font(.system(size: 10))
                        .foregroundColor(.yellow)
                }
            }

            if !marker.labels.isEmpty {
                Text(marker.labels.joined(separator: ", "))
                    .font(.system(size: 11))
                    .lineLimit(1)
            }
        }
        .padding(6)
        .background(Color.black.opacity(0.9))
        .foregroundColor(.white)
        .cornerRadius(4)
        .offset(
            x: min(max(10, geometry.size.width * position - 40), geometry.size.width - 90),
            y: -60
        )
    }
}

