//
//  ImportExportManager.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/25/25.
//

import Foundation
import AVFoundation
import AppKit

struct CancellationError: Error {}

@MainActor
class ImportExportManager: ObservableObject {
    static let shared = ImportExportManager()

    @Published var isImporting = false
    @Published var isExporting = false
    @Published var progress: Double = 0
    @Published var lastError: Error?

    private init() {}

    // MARK: - Export Operations
    func exportMarkersToCSV(markers: [Marker], videoName: String) async throws -> URL {
        let savePanel = NSSavePanel()
        savePanel.title = "Export Markers"
        savePanel.allowedContentTypes = [.commaSeparatedText]
        savePanel.nameFieldStringValue = "\(videoName)_markers"

        guard savePanel.runModal() == .OK,
              let url = savePanel.url else {
            throw ImportExportError.exportFailed(URL(fileURLWithPath: ""), underlyingError: CancellationError())
        }

        isExporting = true
        defer { isExporting = false }

        var csvContent = "timestamp,rating,labels\n"

        for (index, marker) in markers.enumerated() {
            progress = Double(index) / Double(markers.count)

            let timestamp = formatTimestamp(marker.timestamp)
            let labels = marker.labels.joined(separator: ";")
            csvContent += "\"\(timestamp)\",\(marker.rating),\"\(labels)\"\n"
        }

        do {
            try csvContent.write(to: url, atomically: true, encoding: .utf8)
            progress = 1.0
            return url
        } catch {
            throw ImportExportError.exportFailed(url, underlyingError: error)
        }
    }

    func exportSegmentsList(segments: [Segment], videoName: String) async throws -> URL {
        let savePanel = NSSavePanel()
        savePanel.title = "Export Segments"
        savePanel.allowedContentTypes = [.commaSeparatedText]
        savePanel.nameFieldStringValue = "\(videoName)_segments"

        guard savePanel.runModal() == .OK,
              let url = savePanel.url else {
            throw ImportExportError.exportFailed(URL(fileURLWithPath: ""), underlyingError: CancellationError())
        }

        isExporting = true
        defer { isExporting = false }

        var csvContent = "segment_number,start_time,end_time,duration,rating\n"

        for (index, segment) in segments.enumerated() {
            progress = Double(index) / Double(segments.count)

            let segmentNumber = index + 1
            let startTime = segment.startMarker?.timestamp ?? .zero
            let endTime = segment.endMarker?.timestamp ?? CMTime(seconds: Double.infinity, preferredTimescale: 600)
            let duration = segment.duration
            let rating = Int(segment.averageRating)

            let startStr = formatTimestamp(startTime)
            let endStr = endTime.isPositiveInfinity ? "END" : formatTimestamp(endTime)
            let durationStr = duration.isPositiveInfinity ? "N/A" : formatTimestamp(duration)

            csvContent += "\(segmentNumber),\"\(startStr)\",\"\(endStr)\",\"\(durationStr)\",\(rating)\n"
        }

        do {
            try csvContent.write(to: url, atomically: true, encoding: .utf8)
            progress = 1.0
            return url
        } catch {
            throw ImportExportError.exportFailed(url, underlyingError: error)
        }
    }

    // MARK: - Import Operations
    func importMarkersFromCSV() async throws -> [Marker] {
        let openPanel = NSOpenPanel()
        openPanel.title = "Import Markers"
        openPanel.allowedContentTypes = [.commaSeparatedText]
        openPanel.allowsMultipleSelection = false

        guard openPanel.runModal() == .OK,
              let url = openPanel.url else {
            throw ImportExportError.importFailed(URL(fileURLWithPath: ""), underlyingError: CancellationError())
        }

        return try await importMarkersFromCSV(at: url)
    }

    func importMarkersFromCSV(at url: URL) async throws -> [Marker] {
        isImporting = true
        defer { isImporting = false }

        do {
            let content = try String(contentsOf: url, encoding: .utf8)
            let lines = content.components(separatedBy: .newlines)
                .filter { !$0.isEmpty }

            guard lines.count > 1 else {
                throw ImportExportError.invalidCSVFormat(line: 0, reason: "No data found")
            }

            // Skip header
            var markers: [Marker] = []

            for (index, line) in lines.dropFirst().enumerated() {
                progress = Double(index) / Double(lines.count - 1)

                let lineNumber = index + 2 // Account for 0-index and header
                let marker = try parseCSVLine(line, lineNumber: lineNumber)
                markers.append(marker)
            }

            progress = 1.0
            return markers
        } catch {
            if error is ImportExportError {
                throw error
            } else {
                throw ImportExportError.importFailed(url, underlyingError: error)
            }
        }
    }

    func batchImportFromDirectory() async throws -> BatchImportResult {
        let openPanel = NSOpenPanel()
        openPanel.title = "Select Directory with CSV Files"
        openPanel.canChooseDirectories = true
        openPanel.canChooseFiles = false
        openPanel.allowsMultipleSelection = false

        guard openPanel.runModal() == .OK,
              let directoryURL = openPanel.url else {
            throw ImportExportError.importFailed(URL(fileURLWithPath: ""), underlyingError: CancellationError())
        }

        let enumerator = FileManager.default.enumerator(
            at: directoryURL,
            includingPropertiesForKeys: [.isRegularFileKey],
            options: [.skipsHiddenFiles]
        )

        var results: [(url: URL, markers: [Marker]?, error: Error?)] = []
        var csvFiles: [URL] = []

        // Collect CSV files
        while let url = enumerator?.nextObject() as? URL {
            if url.pathExtension.lowercased() == "csv" {
                csvFiles.append(url)
            }
        }

        // Process each file
        for (index, csvURL) in csvFiles.enumerated() {
            progress = Double(index) / Double(csvFiles.count)

            do {
                let markers = try await importMarkersFromCSV(at: csvURL)
                results.append((csvURL, markers, nil))
            } catch {
                results.append((csvURL, nil, error))
            }
        }

        progress = 1.0

        let succeeded = results.filter { $0.error == nil }.count
        let failed = results.filter { $0.error != nil }.count

        return BatchImportResult(
            totalFiles: csvFiles.count,
            succeeded: succeeded,
            failed: failed,
            results: results
        )
    }

    // MARK: - Private Helpers
    private func formatTimestamp(_ time: CMTime) -> String {
        let totalSeconds = Int(CMTimeGetSeconds(time))
        let hours = totalSeconds / 3600
        let minutes = (totalSeconds % 3600) / 60
        let seconds = totalSeconds % 60
        return String(format: "%02d:%02d:%02d", hours, minutes, seconds)
    }

    private func parseTimestamp(_ string: String) throws -> CMTime {
        let components = string.split(separator: ":").map { String($0) }

        if components.count == 3,
           let hours = Int(components[0]),
           let minutes = Int(components[1]),
           let seconds = Int(components[2]) {
            let totalSeconds = hours * 3600 + minutes * 60 + seconds
            return CMTime(seconds: Double(totalSeconds), preferredTimescale: 600)
        } else {
            throw ImportExportError.invalidTimestamp(value: string, line: 0)
        }
    }

    private func parseCSVLine(_ line: String, lineNumber: Int) throws -> Marker {
        // Simple CSV parser that handles quoted values
        var fields: [String] = []
        var currentField = ""
        var inQuotes = false

        for char in line {
            if char == "\"" {
                inQuotes.toggle()
            } else if char == "," && !inQuotes {
                fields.append(currentField.trimmingCharacters(in: .whitespaces))
                currentField = ""
            } else {
                currentField.append(char)
            }
        }
        fields.append(currentField.trimmingCharacters(in: .whitespaces))

        guard fields.count >= 3 else {
            throw ImportExportError.invalidCSVFormat(
                line: lineNumber,
                reason: "Expected 3 fields, found \(fields.count)"
            )
        }

        // Parse timestamp
        let timestamp = try parseTimestamp(fields[0])

        // Parse rating
        guard let rating = Int(fields[1]), (1...5).contains(rating) else {
            throw ImportExportError.invalidRating(value: fields[1], line: lineNumber)
        }

        // Parse labels
        let labels = fields[2].isEmpty ? [] : fields[2].split(separator: ";").map { String($0) }

        return Marker(
            timestamp: timestamp,
            rating: rating,
            labels: labels
        )
    }
}

// MARK: - Batch Import Result
struct BatchImportResult {
    let totalFiles: Int
    let succeeded: Int
    let failed: Int
    let results: [(url: URL, markers: [Marker]?, error: Error?)]

    var summary: String {
        if failed == 0 {
            return "Successfully imported \(succeeded) files"
        } else if succeeded == 0 {
            return "Failed to import all \(failed) files"
        } else {
            return "Imported \(succeeded) files, \(failed) failed"
        }
    }
}

