import SwiftUI
import AVKit
import UniformTypeIdentifiers

struct ContentView: View {
    @StateObject private var viewModel = VideoPlayerViewModel()
    @StateObject private var settingsViewModel = SettingsViewModel()
    @StateObject private var projectManager = ProjectManager.shared
    @StateObject private var recentProjectsManager = RecentProjectsManager.shared
    @StateObject private var autoSaveManager = AutoSaveManager.shared

    @State private var showingFilePicker = false
    @State private var showingSettings = false
    @State private var showingMarkerDetails = false
    @State private var selectedMarkerForEdit: Marker?
    @State private var showingError = false
    @State private var errorMessage = ""

    var body: some View {
        ZStack {
            Color.black
                .ignoresSafeArea()

            // Keyboard handling layer
            KeyboardHandlingView(
                viewModel: viewModel,
                settingsViewModel: settingsViewModel,
                showingSettings: $showingSettings,
                selectedMarkerForEdit: $selectedMarkerForEdit
            )

            if viewModel.videoURL != nil {
                ZStack(alignment: .bottom) {
                    VideoPlayerView(player: viewModel.player)
                        .ignoresSafeArea()

                    // Progress bar overlay
                    ProgressBarView(viewModel: viewModel, selectedMarkerForEdit: $selectedMarkerForEdit)

                    // Scrubbing feedback overlay
                    ScrubbingFeedbackOverlay(viewModel: viewModel)
                }
            } else {
                VStack(spacing: 20) {
                    Image(systemName: "video.fill")
                        .font(.system(size: 64))
                        .foregroundColor(.gray)

                    Text("Open a video file to begin")
                        .font(.title2)
                        .foregroundColor(.gray)

                    Button("Open Video...") {
                        openFilePicker()
                    }
                    .buttonStyle(.borderedProminent)
                }
            }
        }
        .sheet(isPresented: $showingSettings) {
            SettingsView()
                .environmentObject(settingsViewModel)
        }
        .sheet(item: $selectedMarkerForEdit) { marker in
            MarkerDetailsView(marker: marker, viewModel: viewModel)
                .frame(width: 400, height: 350)
        }
        .onAppear {
            setupManagers()
        }
        .alert("Error", isPresented: $showingError) {
            Button("OK") { }
        } message: {
            Text(errorMessage)
        }
    }

    private func openFilePicker() {
        let panel = NSOpenPanel()
        panel.title = "Select a video file"
        panel.allowsMultipleSelection = false
        panel.canChooseDirectories = false
        panel.canChooseFiles = true
        panel.allowedContentTypes = [UTType(filenameExtension: "mp4")!, UTType(filenameExtension: "mov")!]

        if panel.runModal() == .OK {
            if let url = panel.url {
                viewModel.loadVideo(url: url)
            }
        }
    }

    // MARK: - Manager Setup
    private func setupManagers() {
        autoSaveManager.startMonitoring(
            projectManager: projectManager,
            videoPlayerViewModel: viewModel,
            settingsViewModel: settingsViewModel
        )
    }

    // MARK: - Project Operations
    private func saveProject() {
        Task {
            do {
                guard let videoURL = viewModel.videoURL,
                      let player = viewModel.player else { return }

                try await projectManager.saveProject(
                    to: projectManager.currentProjectURL,
                    markers: viewModel.markers,
                    videoURL: videoURL,
                    player: player,
                    settings: settingsViewModel
                )

                // Update recent projects
                if let projectURL = projectManager.currentProjectURL {
                    let videoName = videoURL.deletingPathExtension().lastPathComponent
                    recentProjectsManager.addRecentProject(
                        url: projectURL,
                        videoName: videoName,
                        markerCount: viewModel.markers.count
                    )
                }
            } catch {
                showError(error)
            }
        }
    }

    private func saveProjectAs() {
        let panel = NSSavePanel()
        panel.title = "Save Project"
        panel.allowedContentTypes = [UTType(filenameExtension: ProjectManager.projectFileExtension)!]
        panel.nameFieldStringValue = viewModel.videoURL?.deletingPathExtension().lastPathComponent ?? "Untitled"

        // Set default directory
        if !settingsViewModel.metadataDirectory.isEmpty,
           let url = URL(string: settingsViewModel.metadataDirectory) {
            panel.directoryURL = url
        } else if let videoURL = viewModel.videoURL {
            panel.directoryURL = videoURL.deletingLastPathComponent()
        }

        if panel.runModal() == .OK {
            if let url = panel.url {
                Task {
                    do {
                        guard let videoURL = viewModel.videoURL,
                              let player = viewModel.player else { return }

                        try await projectManager.saveProject(
                            to: url,
                            markers: viewModel.markers,
                            videoURL: videoURL,
                            player: player,
                            settings: settingsViewModel
                        )

                        // Update recent projects
                        let videoName = videoURL.deletingPathExtension().lastPathComponent
                        recentProjectsManager.addRecentProject(
                            url: url,
                            videoName: videoName,
                            markerCount: viewModel.markers.count
                        )
                    } catch {
                        showError(error)
                    }
                }
            }
        }
    }

    private func openProjectPicker() {
        let panel = NSOpenPanel()
        panel.title = "Open Project"
        panel.allowsMultipleSelection = false
        panel.canChooseDirectories = false
        panel.canChooseFiles = true
        panel.allowedContentTypes = [UTType(filenameExtension: ProjectManager.projectFileExtension)!]

        if panel.runModal() == .OK {
            if let url = panel.url {
                loadProject(from: url)
            }
        }
    }

    private func loadProject(from url: URL) {
        Task {
            do {
                let (markers, videoURL, settings) = try await projectManager.loadProject(from: url)

                // Load video
                viewModel.loadVideo(url: videoURL)

                // Wait for video to be ready
                try await Task.sleep(nanoseconds: 500_000_000) // 0.5 seconds

                // Apply loaded data
                viewModel.markers = markers
                applyProjectSettings(settings)

                // Update recent projects
                let videoName = videoURL.deletingPathExtension().lastPathComponent
                recentProjectsManager.addRecentProject(
                    url: url,
                    videoName: videoName,
                    markerCount: markers.count
                )
            } catch {
                showError(error)
            }
        }
    }

    private func applyProjectSettings(_ settings: Project.ProjectSettings) {
        // Apply keyboard shortcuts
        settingsViewModel.playPauseKey = settings.keyboardShortcuts.playPauseKey
        settingsViewModel.addMarkerKey = settings.keyboardShortcuts.addMarkerKey
        settingsViewModel.previousMarkerKey = settings.keyboardShortcuts.previousMarkerKey
        settingsViewModel.nextMarkerKey = settings.keyboardShortcuts.nextMarkerKey
        settingsViewModel.deleteMarkerKey = settings.keyboardShortcuts.deleteMarkerKey
        settingsViewModel.editMarkerKey = settings.keyboardShortcuts.editMarkerKey
        settingsViewModel.openSettingsKey = settings.keyboardShortcuts.openSettingsKey
        settingsViewModel.saveKey = settings.keyboardShortcuts.saveKey
        settingsViewModel.openKey = settings.keyboardShortcuts.openKey

        // Apply scrubbing configs
        settingsViewModel.scrubbingConfigs = settings.scrubbingConfigs
        viewModel.scrubbingSettings = ScrubbingSettings(configs: settings.scrubbingConfigs)

        // Reload settings
        settingsViewModel.loadSettings()
    }

    private func showError(_ error: Error) {
        errorMessage = error.localizedDescription
        if let persistenceError = error as? PersistenceError,
           let suggestion = persistenceError.recoverySuggestion {
            errorMessage += "\n\n" + suggestion
        }
        showingError = true
    }
}

