//
//  RecentProjectsManager.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/25/25.
//

import Foundation
import SwiftUI

struct RecentProject: Codable, Identifiable {
    let id = UUID()
    let url: URL
    let name: String
    let videoName: String
    let lastOpened: Date
    let markerCount: Int

    init(url: URL, name: String, videoName: String, lastOpened: Date = Date(), markerCount: Int = 0) {
        self.url = url
        self.name = name
        self.videoName = videoName
        self.lastOpened = lastOpened
        self.markerCount = markerCount
    }
}

@MainActor
class RecentProjectsManager: ObservableObject {
    static let shared = RecentProjectsManager()

    @Published var recentProjects: [RecentProject] = []

    private let maxRecentProjects = 10
    private let userDefaults = UserDefaults.standard
    private let storageKey = "recentProjects"

    private init() {
        loadRecentProjects()
    }

    // MARK: - Public Methods
    func addRecentProject(url: URL, videoName: String, markerCount: Int) {
        let projectName = url.deletingPathExtension().lastPathComponent
        let newProject = RecentProject(
            url: url,
            name: projectName,
            videoName: videoName,
            markerCount: markerCount
        )

        // Remove existing entry for same URL
        recentProjects.removeAll { $0.url == url }

        // Add to beginning
        recentProjects.insert(newProject, at: 0)

        // Limit to max count
        if recentProjects.count > maxRecentProjects {
            recentProjects.removeLast()
        }

        saveRecentProjects()
    }

    func removeRecentProject(at index: Int) {
        guard index < recentProjects.count else { return }
        recentProjects.remove(at: index)
        saveRecentProjects()
    }

    func removeRecentProject(url: URL) {
        recentProjects.removeAll { $0.url == url }
        saveRecentProjects()
    }

    func clearRecentProjects() {
        recentProjects.removeAll()
        saveRecentProjects()
    }

    func cleanupMissingFiles() {
        let fileManager = FileManager.default
        recentProjects.removeAll { project in
            !fileManager.fileExists(atPath: project.url.path)
        }
        saveRecentProjects()
    }

    // MARK: - Menu Building
    func buildRecentProjectsMenu(action: @escaping (URL) -> Void) -> some View {
        Group {
            if recentProjects.isEmpty {
                Text("No Recent Projects")
                    .foregroundColor(.secondary)
            } else {
                ForEach(Array(recentProjects.prefix(9).enumerated()), id: \.element.id) { index, project in
                    Button(action: {
                        action(project.url)
                    }) {
                        VStack(alignment: .leading, spacing: 2) {
                            HStack {
                                Text(project.name)
                                Spacer()
                                if index < 9 {
                                    Text("\(index + 1)")
                                        .font(.caption)
                                        .foregroundColor(.secondary)
                                }
                            }
                            HStack {
                                Text("\(project.videoName)")
                                    .font(.caption)
                                    .foregroundColor(.secondary)
                                Text("")
                                    .font(.caption)
                                    .foregroundColor(.secondary)
                                Text("\(project.markerCount) markers")
                                    .font(.caption)
                                    .foregroundColor(.secondary)
                            }
                        }
                    }
                    .keyboardShortcut(
                        KeyEquivalent(Character(String(index + 1))),
                        modifiers: .command
                    )
                }

                Divider()

                Button("Clear Recent Projects") {
                    self.clearRecentProjects()
                }
            }
        }
    }

    // MARK: - Private Methods
    private func loadRecentProjects() {
        // First, try to migrate old format if needed
        migrateOldFormat()

        guard let data = userDefaults.data(forKey: storageKey),
              let decoded = try? JSONDecoder().decode([RecentProject].self, from: data) else {
            return
        }

        recentProjects = decoded

        // Clean up missing files on load
        cleanupMissingFiles()
    }

    private func saveRecentProjects() {
        guard let encoded = try? JSONEncoder().encode(recentProjects) else { return }
        userDefaults.set(encoded, forKey: storageKey)
    }

    private func migrateOldFormat() {
        // Check if there's an old format to migrate
        if let oldData = userDefaults.object(forKey: "recentProjectsData") as? Data {
            // Attempt to decode old format
            struct OldRecentProject: Codable {
                let url: URL
                let name: String
                let lastOpened: Date
            }

            if let oldProjects = try? JSONDecoder().decode([OldRecentProject].self, from: oldData) {
                // Convert to new format
                recentProjects = oldProjects.map { old in
                    RecentProject(
                        url: old.url,
                        name: old.name,
                        videoName: "Unknown", // We don't have this in old format
                        lastOpened: old.lastOpened,
                        markerCount: 0 // We don't have this in old format
                    )
                }

                // Save in new format
                saveRecentProjects()

                // Remove old data
                userDefaults.removeObject(forKey: "recentProjectsData")
            }
        }
    }
}

// MARK: - AppStorage Extension for Recent Projects
extension View {
    func onOpenRecentProject(_ action: @escaping (URL) -> Void) -> some View {
        self.onReceive(NotificationCenter.default.publisher(for: .openRecentProject)) { notification in
            if let url = notification.object as? URL {
                action(url)
            }
        }
    }
}

extension Notification.Name {
    static let openRecentProject = Notification.Name("openRecentProject")
}

