import SwiftUI
import AVFoundation

struct MarkerDetailsView: View {
    @Environment(\.dismiss) var dismiss
    @State var marker: Marker
    @ObservedObject var viewModel: VideoPlayerViewModel
    @State private var labelsText: String = ""
    @State private var showingDeleteConfirmation = false

    var frameNumber: Int {
        guard let asset = viewModel.player?.currentItem?.asset else { return 0 }
        let fps = asset.tracks(withMediaType: .video).first?.nominalFrameRate ?? 30.0
        return Int(marker.timestamp.seconds * Double(fps))
    }

    var body: some View {
        VStack(spacing: 0) {
            // Title Bar
            HStack {
                Text("Marker Details")
                    .font(.headline)
                Spacer()
                Button(action: { dismiss() }) {
                    Image(systemName: "xmark.circle.fill")
                        .foregroundColor(.secondary)
                }
                .buttonStyle(.plain)
            }
            .padding()
            .background(Color(NSColor.separatorColor).opacity(0.5))

            // Content
            VStack(alignment: .leading, spacing: 20) {
                // Timestamp (read-only)
                VStack(alignment: .leading, spacing: 8) {
                    Text("Timestamp")
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    HStack {
                        Text(marker.timestamp.timeString)
                            .font(.system(.body, design: .monospaced))

                        Text("Frame \(frameNumber)")
                            .foregroundColor(.secondary)
                            .font(.caption)
                    }
                    .padding(8)
                    .frame(maxWidth: .infinity, alignment: .leading)
                    .background(Color(NSColor.textBackgroundColor))
                    .cornerRadius(6)
                }

                // Rating
                VStack(alignment: .leading, spacing: 8) {
                    Text("Rating")
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    StarRatingView(rating: $marker.rating)
                        .padding(.vertical, 4)
                }

                // Labels
                VStack(alignment: .leading, spacing: 8) {
                    Text("Labels")
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    TextField("Enter labels separated by commas...", text: $labelsText)
                        .textFieldStyle(.roundedBorder)
                        .onSubmit {
                            updateLabels()
                        }

                    Text("Separate multiple labels with commas")
                        .font(.caption)
                        .foregroundColor(.secondary)
                }

                Spacer()

                // Buttons
                HStack {
                    Button("Delete", role: .destructive) {
                        showingDeleteConfirmation = true
                    }

                    Spacer()

                    Button("Cancel") {
                        dismiss()
                    }
                    .keyboardShortcut(.cancelAction)

                    Button("Save") {
                        saveChanges()
                        dismiss()
                    }
                    .keyboardShortcut(.defaultAction)
                }
                .padding(.top)
            }
            .padding()
        }
        .frame(width: 400, height: 350)
        .background(Color(NSColor.windowBackgroundColor))
        .onAppear {
            labelsText = marker.labels.joined(separator: ", ")
        }
        .alert("Delete Marker", isPresented: $showingDeleteConfirmation) {
            Button("Cancel", role: .cancel) { }
            Button("Delete", role: .destructive) {
                deleteMarker()
                dismiss()
            }
        } message: {
            Text("Are you sure you want to delete this marker?")
        }
    }

    private func updateLabels() {
        let labels = labelsText
            .split(separator: ",")
            .map { $0.trimmingCharacters(in: .whitespaces) }
            .filter { !$0.isEmpty }
        marker.labels = labels
    }

    private func saveChanges() {
        updateLabels()
        viewModel.updateMarker(marker)
    }

    private func deleteMarker() {
        viewModel.deleteMarker(marker)
    }
}

