import SwiftUI

struct ShortcutsTabView: View {
    @ObservedObject var viewModel: SettingsViewModel
    @State private var showingConflictAlert = false
    @State private var conflictMessage = ""

    var body: some View {
        VStack(alignment: .leading, spacing: 20) {
            HStack {
                Text("Keyboard Shortcuts")
                    .font(.headline)
                Spacer()

                // Action buttons
                HStack(spacing: 12) {
                    Button("Cancel") {
                        viewModel.cancelChanges()
                    }
                    .buttonStyle(.borderless)
                    .disabled(!viewModel.hasUnsavedChanges)

                    Button("Reset to Defaults") {
                        viewModel.resetShortcutsToDefaults()
                    }
                    .buttonStyle(.borderless)

                    Button("Save") {
                        if viewModel.hasShortcutConflicts().isEmpty {
                            viewModel.saveSettings()
                        } else {
                            showConflictAlert()
                        }
                    }
                    .buttonStyle(.borderedProminent)
                    .disabled(!viewModel.hasUnsavedChanges)
                }
            }
            .padding(.top)

            ScrollView {
                VStack(spacing: 16) {
                    // Playback
                    ShortcutSection(title: "Playback") {
                        EditableShortcutRow(
                            name: "Play/Pause",
                            shortcut: $viewModel.tempPlayPauseKey,
                            onChanged: { viewModel.markAsChanged() }
                        )
                    }

                    // Markers
                    ShortcutSection(title: "Markers") {
                        EditableShortcutRow(
                            name: "Add Marker",
                            shortcut: $viewModel.tempAddMarkerKey,
                            onChanged: { viewModel.markAsChanged() }
                        )
                        EditableShortcutRow(
                            name: "Delete Nearest Marker",
                            shortcut: $viewModel.tempDeleteMarkerKey,
                            onChanged: { viewModel.markAsChanged() }
                        )
                        EditableShortcutRow(
                            name: "Previous Marker",
                            shortcut: $viewModel.tempPreviousMarkerKey,
                            onChanged: { viewModel.markAsChanged() }
                        )
                        EditableShortcutRow(
                            name: "Next Marker",
                            shortcut: $viewModel.tempNextMarkerKey,
                            onChanged: { viewModel.markAsChanged() }
                        )
                        EditableShortcutRow(
                            name: "Edit Marker Details",
                            shortcut: $viewModel.tempEditMarkerKey,
                            onChanged: { viewModel.markAsChanged() }
                        )
                    }


                    // Application
                    ShortcutSection(title: "Application") {
                        EditableShortcutRow(
                            name: "Open Settings",
                            shortcut: $viewModel.tempOpenSettingsKey,
                            onChanged: { viewModel.markAsChanged() }
                        )
                        EditableShortcutRow(
                            name: "Save Project",
                            shortcut: $viewModel.tempSaveKey,
                            onChanged: { viewModel.markAsChanged() }
                        )
                        EditableShortcutRow(
                            name: "Load Project",
                            shortcut: $viewModel.tempOpenKey,
                            onChanged: { viewModel.markAsChanged() }
                        )
                    }
                }
            }

            if viewModel.hasUnsavedChanges {
                Text("You have unsaved changes. Click Save to apply them.")
                    .font(.caption)
                    .foregroundColor(.orange)
            } else {
                Text("Click on a shortcut to record a new key combination.")
                    .font(.caption)
                    .foregroundColor(.secondary)
            }

            Spacer()
        }
        .padding()
        .alert("Keyboard Shortcut Conflicts", isPresented: $showingConflictAlert) {
            Button("OK") { }
        } message: {
            Text(conflictMessage)
        }
    }

    private func showConflictAlert() {
        let conflicts = viewModel.hasShortcutConflicts()
        conflictMessage = conflicts.joined(separator: "\n")
        showingConflictAlert = true
    }
}

struct ShortcutSection<Content: View>: View {
    let title: String
    @ViewBuilder let content: Content

    var body: some View {
        VStack(alignment: .leading, spacing: 8) {
            Text(title)
                .font(.subheadline)
                .fontWeight(.semibold)
                .foregroundColor(.secondary)

            VStack(spacing: 4) {
                content
            }
            .padding(12)
            .background(Color(NSColor.controlBackgroundColor))
            .cornerRadius(8)
        }
    }
}

struct EditableShortcutRow: View {
    let name: String
    @Binding var shortcut: String
    let onChanged: () -> Void

    var body: some View {
        HStack {
            Text(name)
                .foregroundColor(.primary)
            Spacer()
            KeyboardShortcutRecorder(shortcut: $shortcut)
                .onChange(of: shortcut) { _, _ in
                    onChanged()
                }
        }
    }
}

